    vec2 center = vec2(0.5 * iResolution.x + (turnRadius * cos(speed * iTime)), 
                       0.5 * iResolution.y +(turnRadius * sin(speed * iTime)));

    vec3 shapeColor = vec3(0.3, 0.73, 0.14);
