// Derived from Simple_animated_Truchet by @Shane
// [url]https://www.shadertoy.com/view/llfyWX[/url]

// Mouse control direction


#define PI     3.14159265359
#define TWO_PI (PI*2.)

// vec2 to float hash function taken from @Shane - Based on IQ's original.

float hash21(vec2 p){ return fract(sin(dot(p, vec2(141.213, 289.867)))*43758.5453); }

// Standard 2D rotation formula.

mat2 rot(in float a){ float c = cos(a), s = sin(a); return mat2(c, -s, s, c); }




//distance field dot



float df_circle(vec2 pos, float rad){

 float shp  = length(pos) -rad;

    return   shp;
}




void mainImage( out vec4 c, in vec2 uv )
{

    // I   //  Screen coordinates is formated to go from -1. to 1.

	vec2 p = -1.+ 2.* uv / iResolution.xy;
    p.x *= iResolution.x / iResolution.y;



    // II  //  Moove & Rescale screen space

    p *= 1. + dot(p, p)*.05;        // fish eye
    p += vec2(.0, iTime/12.);       // moove along Y axis
    //p *= .7*floor(iResolution.x/200.); // rescale space
    p*=3.;

 	// III //  Make truchet lines
           //  store tile id & Divide screen into grid 
    	   //  & store direction in a checkerboard way.

    vec2 id = ceil(p);

    float dir = mod(id.x + id.y, 2.) * 2. -1.;  


    p = fract(p) - .5;  // or // p -= id + .5; 

           //  Truchet tiles orientation randomisation
    float r =  hash21(id) > .5 ? 1. : -1.;

	p.y *= r;

           //  Applying symetry on diagonal axis to avoid drawing two arcs
           //  Tricks form @shane

    p *= sign(p.x + p.y);

           //  Drawing the two arc's strokes directly with a tickness of t
           //  The tricks to draw a contour was taken from a @FabriceNeyret2 
           //  comment in this shader https://www.shadertoy.com/view/MtXyWX

    p -= .5; 

    // IV  //  RENDER

           //  Animate flow

    vec2 pos  = p ;
    vec2 pos2 = p ;
    vec2 pos3 = p ;  

    if(iMouse.z > 0.){
    	float m = smoothstep(.0,-1. + 2. * iResolution.x, -1. + 2. * iMouse.x);
        m*= 20.;
    	pos  *= rot(m *  dir);
        pos2 *= rot(m * -dir);
        pos3 *= rot(-5.*dir * r);        // magic tricks here*

    }else{
        pos  *= rot(iTime *  dir);
        pos2 *= rot(iTime * -dir);
        pos3 *= rot(-5.*r * dir);      // magic tricks here
    						       // * use the random state r to flip again the sections
    						       // and get the inner / outer truchet together.   
    }
           //  Part into cells  & convert into polar coordinates

    const float num = 8.;                              // partitions number

    float ang  = atan(pos.y, pos.x);                   // Pixel angle  
    float ang2 = atan(pos2.y, pos2.x);                 // pixel angle with opposite rotation
    float ang3 = atan(pos3.y, pos3.x);                 // offset angle WITHOUT animation

    float ctr  = ceil(ang / TWO_PI * num) -.5;         // the cell centers.
    float ctr2 = ceil(ang2 / TWO_PI * num )-.5;

          pos  = rot(ctr  * TWO_PI / num) * pos;        // Converting to polar coordinates
          pos2 = rot(ctr2 * TWO_PI / num) * pos2;       //
          pos.x  -= .5;                                 // p.x = radius, p.y = angle.
          pos2.x -= .5;                                 // Translate coordinates


           //  render the objects

    float t = .084;                                      // thickness
    float rd = .1;                                     // offset value added on each side of 
                                                         // the original truchet line

    vec2 p1  = vec2(sin(ang3) *  rd,cos(ang3) * -rd);    // Two mirored points
    vec2 p2  = p1*vec2(-1);                              // inner or outer around ang2 (main angle)

    float line1 = abs(length(p - p1) - .5) - t ;         // two lines
          line1 = 1. - smoothstep(.01,.2, sqrt(line1));  // abs() draw the contour

    float line2 = abs(length(p - p2) - .5) - t ;    
          line2 = 1. - smoothstep(.01,.2, sqrt(line2));

    float line3 = abs(length(p ) - .5) - .005 ;    
          line3 = 1. - smoothstep(.01,.2, sqrt(line3));

    float ball1 = smoothstep(.01, .02, df_circle(pos  + vec2(r * dir * .1, .0) , .025)); // two balls
    float ball2 = smoothstep(.01, .02, df_circle(pos2 - vec2(r * dir * .1, .0) , .03));

    // V   //   Coloring


    c= vec4(.1);

    c  = vec4(line2 *cos(sin(r * ang))      + line1 * cos(sin(r * ang2)));        // first two opposite gradient
    c *= vec4(line2 *cos(sin(r * ang * 2.)) + line1 * cos(sin(r * ang * 2.)));    // second two gradient  
    c *= .3 + line1;
    c -= .1 * sin(2. * cos(8. * uv.x + uv.y) - hash21(uv));                       // texture
    c += 1.-ball1;                                                                // balls
    c *= 1.*ball2+.2;
    c += line3;
    vec2 uu  = 1. - 2. * uv / iResolution.xy;                                     // vigneting
    float v  = 1. - smoothstep(.7, 1.4, length(uu*uu));

    c *= vec4(v); 
    c+= vec4(.0,.0,.07,1.);

    //c = vec4(ball1);

    //c = vec4(line3);



}

float f( float x, int idx )
{
    float t = x + sin(iTime);

    if ( idx == 2 )
    	return sin(t*t*t)*sin(t);
    else if ( idx == 1 )
    	return step( fract(t), 0.5 ) - 0.5;
    else
    {
        //return fract(t);
        //return 0.1 * tan( t );
        return texture(iChannel0, vec2(0.2*t,0.5)).r - 0.5;
    }
}

float eval_step( vec2 uv, int idx )
{
    //note: evaluate function at pixel-edges
    //      +-------+
	//      |       |
	//      x0  x  x1
	//      |       |
	//      +-------+
    float dx = 0.5 * dFdx( uv.x );
    float x0 = uv.x - dx;
    float x1 = uv.x + dx;
    float y0 = f( x0, idx );
    float y1 = f( x1, idx );

    //note: determine if center is within the interval, y0 < uv.y < y1
    float mn = min(y0,y1);
    float mx = max(y0,y1);
    float w = dFdy(uv.y);
    float c = step( mn-w, uv.y ) * step( uv.y, mx+w );

    return c;
}

float sd_segment(vec2 p, vec2 a, vec2 b)
{
    vec2 ab=b-a,ap=p-a;
    return length(ap-ab*clamp(dot(ab,ap)/dot(ab,ab), 0.0, 1.0));
}
float eval_dist( vec2 uv, int idx )
{
    //note: evaluate function at pixel-edges
    //      +-------+
	//      |       |
	//      x0  x  x1
	//      |       |
	//      +-------+
    float dx = 0.5 * dFdx( uv.x );
    float x0 = uv.x - dx;
    float x1 = uv.x + dx;
    float y0 = f( x0, idx );
    float y1 = f( x1, idx );

    //note: calculate distance to line-segment (x0,y0)<->(x1,y1)
    //      +-------+
	//      |    /-x1
    //      |  -x   |
	//     x0-/     |
	//      +-------+
    float d = sd_segment( uv, vec2(x0,y0), vec2(x1,y1) );
    return smoothstep( dFdy(uv.y), 0.0, d );
}


float eval( vec2 uv, int idx )
{
    //return eval_step( uv, idx );
    return eval_dist( uv, idx );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.y;
    int idx = int(  floor( 3.0*uv.y ) );
    uv = vec2(uv.x, fract( uv.y*3.0) );
    uv = vec2(8.0,3.0) * (uv - vec2(0.0, 0.5));
    uv += vec2( 0.2 * cos(iTime), 0.2 * sin(1.1*iTime) );

    float c = eval( uv, idx );

    vec3 col = vec3( 0.125 + 0.65 * c );

	fragColor = vec4( col, 1.0 );
}

// First exercise here : https://www.shadertoy.com/view/Mt2cDh

void mainImage(
    out vec4 color,
    in vec2 coord 
) {
    float zoom = (cos(iTime*3.)+1.1)/(2.*iResolution.x); // line will be 2px thick minimum

    float distLinePixels;

    float baryCoeff; // all real values
    //unitless (0 means "stay at A", 1 means "go from A to B")

    // v(ZW) means vector from Z to W, equals W-Z
    // · is dot product

    // Exo 2 : a line is defined by going through two points A and B with A != B

    vec2 A = iResolution.xy/2.;
    vec2 B = iMouse.xy;

    // H in (AB) <=> there exist one t such as v(AH) = t * v(AB)
    // so t is AH/AB (algebraic ! i.e. keeping the sign)

    // t = 0 => M=A
    // t = 1 => M=B
    // t is called a barycentric coefficient

    // for every M in the plane, there exist only one point H such as
    // v(AM) = v(AH) + v(HM) && v(HM)·v(AB)=0 ( perpendicular )

    vec2 AB = B - A;
    vec2 AM = coord - A;

    // v(AM)·v(AB) = v(AH)·v(AB) + v(HM)·v(AB) = v(AH)·v(AB) by construction
    baryCoeff = dot(AM,AB)/dot(AB,AB);

    // segment when mouse pressed
    // line otherwise
    if(iMouse.z > .5)
        baryCoeff = clamp(baryCoeff,0.,1.);

    vec2 HM = AM - baryCoeff*AB;
    distLinePixels = length(HM);


	color.rgb = mix(
          // red / blue mix, be sure to not go out of [0,1]
          mix( vec3(1.,.0,.0) , vec3(.0,.0,1.) , clamp(baryCoeff,0.,1.) )
          // white
        , vec3(1.)
        , clamp(1. - abs( pow(distLinePixels/iResolution.x,1.5) /zoom),0.,1.)
    );
}

// Created by Stephane Cuillerdier - Aiekick/2017 (twitter:@aiekick)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Tuned via XShade (http://www.funparadigm.com/xshade/)
// link : https://www.shadertoy.com/view/lljyWm

float fullAtan(vec2 p)
{
    return step(0.0,-p.x)*3.1415926535 + sign(p.x) * atan(p.x, sign(p.x) * p.y);
}

float fractus(vec2 p, vec2 v)
{
	vec2 z = p;
    vec2 c = v;
	float k = 1., h = 1.0;    
    float t = (sin(iTime * .5)*.5+.5) * 5.;
    for (float i=0.;i<5.;i++)
    {
        if (i > t) break;
        h *= 4.*k;
		k = dot(z,z);
        if(k > 4.) break;
		z = vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y) + c;
    }
    float d = sqrt(k/h)*log(k);

    // next iteration
    if (k > 4.)
    	z = vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y) + c;
    h *= 4.*k;
    k = dot(z,z);
    float d1 = sqrt(k/h)*log(k);

    // df blending
	return mix(d, d1, fract(t));
}

vec2 dfFractus(vec3 p)
{
	float a = fullAtan(p.xz); // axis y

    vec2 c;
    c.x = mix(0.2, -0.5, sin(a * 2.));
    c.y = mix(0.5, 0.0, sin(a * 3.));

    float path = length(p.xz) - 3.;

    vec2 rev = vec2(path, p.y);
    float aa = a + iTime;
    rev *= mat2(cos(aa),-sin(aa),sin(aa),cos(aa)); // rot near axis y

	return vec2(fractus(rev, c) - 0.1, 2);
}

vec2 df(vec3 p)
{
	return dfFractus(p);
}

vec3 nor( in vec3 pos, float prec )
{
	vec3 eps = vec3( prec, 0., 0. );
	vec3 nor = vec3(
	    df(pos+eps.xyy).x - df(pos-eps.xyy).x,
	    df(pos+eps.yxy).x - df(pos-eps.yxy).x,
	    df(pos+eps.yyx).x - df(pos-eps.yyx).x );
	return normalize(nor);
}

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float prec, float ms) 
{
	vec3 n;
	float s = 0.;
    const int iter = 10;
	for (int i=0;i<iter;i++)
	{
		n = nor(surfPoint,prec); 
		surfPoint = surfPoint - n * ms; 
		s += df(surfPoint).x*1.5;
	}
	return 1.-s/(ms*float(iter)); // s < 0. => inside df
}

float SubDensity(vec3 p, float s) 
{
	vec3 n = nor(p,s); 							// precise normale at surf point
	return df(p - n * s).x;						// ratio between df step and constant step
}

// from shane sahders
// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: http://http.developer.nvidia.com/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n )
{
    n = max((abs(n) - .2)*7., .001);
    n /= (n.x + n.y + n.z );  
    p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
    return p*p;
}

// from shane sahders
// Texture bump mapping. Four tri-planar lookups, or 12 texture lookups in total. I tried to 
// make it as concise as possible. Whether that translates to speed, or not, I couldn't say.
vec3 doBumpMap( sampler2D tx, in vec3 p, in vec3 n, float bf)
{
    const vec2 e = vec2(0.001, 0);
    mat3 m = mat3( tex3D(tx, p - e.xyy, n), tex3D(tx, p - e.yxy, n), tex3D(tx, p - e.yyx, n));
    vec3 g = vec3(0.299, 0.587, 0.114)*m; // Converting to greyscale.
    g = (g - dot(tex3D(tx,  p , n), vec3(0.299, 0.587, 0.114)) )/e.x; g -= n*dot(n, g);
    return normalize( n + g*bf ); // Bumped normal. "bf" - bump factor.
}

vec4 shade(vec3 ro, vec3 rd, float d, vec3 lp, float li)
{
	vec3 p = ro + rd * d;											// surface point
	float sb = SubDensity(p, 0.01, 0.03);							// deep subdensity (10 iterations)
	vec3 bb = blackbody(80.*sb+320.);								// bb
	vec3 ld = normalize(lp-p); 										// light dir
	vec3 n = nor(p, 0.1);											// normal at surface point
	//n = doBumpMap(iChannel0, p, n, 0.019);
	vec3 refl = reflect(rd,n);										// reflected ray dir at surf point 
	float amb = 0.23; 												// ambiance factor
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 					// diffuse
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 			// fresnel
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.);		// specular
	float sss = 1. - SubDensity(p,8.); 							// one step sub density of df
	return vec4(
        (diff + fre + bb.x * sss * 0.5) * amb * li + spe * 1.2, 
        (diff + fre + bb * sb * 0.8 + sss * 0.5) * amb * li + spe * 1.2 	
    );
}

vec3 cam(vec2 uv, vec3 ro, vec3 cv, float t)
{
	vec3 cu = normalize(vec3(0,1,0));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y= cross(z,x);
  	return normalize(z + uv.x*x + uv.y*y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);

    float t = iTime;

    float ca = 0.;
    float cd = 2.4;
    float ce = 2.;

    vec2 si = iResolution.xy;
    vec2 uv = (2.*fragCoord-si)/si.y;

	vec3 ro = vec3(cos(ca),ce,sin(ca)) * cd;
  	vec3 cv = vec3(0,-3,0);
	vec3 rd = cam(uv, ro, cv, t);

	float md = 15., s = 1., d = 1.;

	const float iter = 250.;
    for(float i=0.;i<iter;i++)
    {      
        if (s<0.2*log(d*d/s/500.)||d>md) break;
        s = df(ro+rd*d).x;
		d += s *0.2;
    }

    if (d<md)
		fragColor.rgb = mix(
            shade(ro, rd, d, ro, 1.2).yzw, 
            vec3(0), 
            1.-exp(-0.02*d*d));
}

