// Circle Attraction


// The number of points in the spiral
#define CT_N 5

// Viewport Axes
struct ct_axes
{
    float xmin;
    float xmax;
    float ymin;
    float ymax;
};


ct_axes ct_axes_from_radius(
    in vec3 vpcircle
){
    return ct_axes(
        vpcircle.x - vpcircle.z,
        vpcircle.x + vpcircle.z,
        vpcircle.y - vpcircle.z,
        vpcircle.y + vpcircle.z
    );
}


// Simple 2d Plane
struct ct_plane2d
{
    ct_axes axes;
    float xstep;
    float ystep;
};


ct_plane2d ct_plane2d_create(
    in ct_axes axes
){
    float awidth = axes.xmax - axes.xmin;
    float aheight = axes.ymax - axes.ymin;

    float daspect = abs(iResolution.y / iResolution.x);
    float waspect = abs(aheight / awidth);

    if (daspect > waspect)
    {
        float excess = aheight * (daspect / waspect - 1.0);
        axes.ymax += excess / 2.0;
        axes.ymin -= excess / 2.0;
    }

    else if (daspect < waspect)
    {
        float excess = awidth * (waspect / daspect - 1.0);
        axes.xmax += excess / 2.0;
        axes.xmin -= excess / 2.0;
    }

    return ct_plane2d(
        axes,
        (axes.xmax - axes.xmin) / iResolution.x,
        (axes.ymax - axes.ymin) / iResolution.y
    );
}


vec2 ct_plane2d_project(
	in ct_plane2d self,
    in vec2 z
){
	return vec2(
        self.axes.xmin + z.x * self.xstep,
        self.axes.ymin + z.y * self.ystep
    );
}


// A vector field point
struct ct_vfpoint
{
    vec2 p;
    float m;
};


// Our global per-pixel points
ct_vfpoint g_vfp[CT_N * CT_N + 1];


// Gain a normalized vector from p, 
// using a power of npow.
vec2 ct_vfield_normal(
    in vec2 p,
    float npow
){
    vec2 g = vec2(0.0, 0.0);

    const int imax = CT_N * CT_N + 1;

    for (int i = 0; i < imax; ++i)
    {
        vec2 dif = g_vfp[i].p - p;
        float sum = dif[0] * dif[0] + dif[1] * dif[1];
        float mass = pow(sum, npow);

      	g[0] = g[0] + g_vfp[i].m * dif[0] / mass;
      	g[1] = g[1] + g_vfp[i].m * dif[1] / mass;
    }

    return normalize(g);
}

float ct_normal_pi(
    in vec2 z
){
    float a = atan(z[1], z[0]);
    if (a < 0.0) a += 6.28318;
    a /= 6.28318;
    return a;
}

float ct_normal_mod_pi(
    in vec2 z
){
    float a = atan(z[1], z[0]);
    if (a < 0.0) a += 6.28318;
    return mod(a, 1.0);
}


// Vector Pixel Iteration
vec4 ct_vpixel(
    in vec2 z,
    in vec2 c,
    in int n,
    in float npow
){
    vec2 vn = ct_vfield_normal(z, npow);
	float vnpin = ct_normal_pi(vn);
    float blah = length(vn);

    if (blah < .001)
    {
       return vec4(.9, .2, .1, 1.0);
    }

    // trying to get more angles...
    return vec4(
        mod(vnpin * 7.0 + vn.x, 1.0),
        mod(vnpin * 11.0 + vn.y, 1.0), 
        mod(vnpin * 5.0 + (vn.x * vn.y), 1.0),
        1.0
    );
}


// High-Level Entry
vec4 ct_main(
    in ct_plane2d plane,
    in vec2 c
){
    const int n = CT_N;

    for (int x = 0; x < n; ++x)
    {
    	float xr = float(x) / float(n - 1);

        for (int y = 0; y < n; ++y)
    	{
            float yr = float(y) / float(n - 1);

        	g_vfp[x + y*n] = ct_vfpoint(
                vec2((-1.0 + xr * 2.0),
                     -1.0 + yr * 2.0), 
                1.0
            );
        }
    }

    g_vfp[n * n] = ct_vfpoint(
    	vec2(
            cos(iTime * .5) * .85, 
            sin(iTime * .5) * .85),
        	-10000000.0
   		);

    return ct_vpixel(c, c, 128, 4.5 + abs(sin(iTime * 2.5)) * 3.0);
}


