float	scene(vec3 p)
{  
    mind = 1e5;
    ming = 1e5;
    mint = 1e5;
	minl = 1e5;

    rotate(p.xz, 1.57-.15*iTime);
    rotate(p.yz, 1.57-.125*iTime);
    vec3 op = p;
    float	var = (atan(p.x, p.z)+ PI)/(TAU);
    var = var*40.;
    p.xz = modA(p.xz, 40.);
    p.xz -= vec2(8., .0);

    rotate(p.xy, iTime*.25*(mod(var-.5, 2.) <= 1. ? 1. : -1.));
    vec2 q = vec2(length(p.xy)-4.,p.z);
    mind = mylength(q)-.05;

    float vir = (atan(p.x, p.y)+ PI)/(TAU);
    var = vir*30.;
    p.xy = modA(p.xy, 30.)-vec2(.0,.0);
    p.xz -= vec2(4., .0);
    q = vec2(length(p.zx)-0.25, p.y-.0);
    ming = mylength(q)-.05;
    mind = min(mind, ming);

    float as = (mind == ming ? 1. : 0.);
    ret_col = step(as, .0)*vec3(.2, .7, .4) + step(1., as)*vec3(.5, .2, .8);
    rotate(p.xz, iTime*1.5*(mod(var, 2.) <= 1. ? 1. : -1.)*(mod(vir, 2.) <= 1. ? 1. : -1.));
    p.xz = modA(p.xz, 20.)-vec2(.0,-.0);
    p.xy -= vec2(.25, .0);
    q = vec2(length(p.xy)-.1, p.z);
    mint = mylength(q)-.02;
    mind = min(mind, mint);
    as = (mind == mint ? 1. : 0.);
    if (as  == 1.)
    ret_col = teal;

    // dodecahedron
    rotate(op.zx, iTime*.5);
    op.xz = modA(op.xz, 25.);
    op -= vec3(6., .0, 0.);
    op /= 1.732; //sqrt(3.)
    vec3	b = vec3(.075);
	minl = max(max(abs(op.x)+.5*abs(op.y)-b.x, abs(op.y)+.5*abs(op.z)-b.y), abs(op.z)+.5*abs(op.x)-b.z);
    b *= .95;
    minl = max(minl, 
           -max(max(abs(op.x)+.5*abs(op.y)-b.x, abs(op.y)-.5*abs(op.z)-b.y), abs(op.z)+.5*abs(op.x)-b.z)
               );
    minl = max(minl, 
           -max(max(abs(op.x)-.5*abs(op.y)-b.x, abs(op.y)+.5*abs(op.z)-b.y), abs(op.z)+.5*abs(op.x)-b.z)
               );
    minl = max(minl, 
           -max(max(abs(op.x)+.5*abs(op.y)-b.x, abs(op.y)+.5*abs(op.z)-b.y), abs(op.z)-.5*abs(op.x)-b.z)
               );
    // end dodecahedron

    mind = min(mind, minl);

    as = mind == minl ? 1. : 0.;

    if (as == 1.)
    ret_col = orange;

    h += .036125*orange/(pow(minl, 25.)+.5);

    return (mind);
}

vec2	march(vec3 pos, vec3 dir)
{
    vec2	dist = vec2(0.0, 0.0);
    vec3	p = vec3(0.0, 0.0, 0.0);
    vec2	s = vec2(0.0, 0.0);
	float dinamyceps = E;
	for (float i = -1.; i < I_MAX; ++i)
	{
		p = pos + dir * dist.y;
		dist.x = scene(p);
		dist.y += dist.x*1.;
        dinamyceps = -dist.x+(dist.y)/(1500.);
        // log trick from aiekick
	    if (log(dist.y*dist.y/dist.x/1e5)>0. || dist.x < dinamyceps || dist.y > FAR)
        {
            break;
        }
	    s.x++;
    }
    s.y = dist.y;
    return (s);
}

float	mylength(vec2 p)
{
	float	ret;

    p = p*p*p*p;
    p = p*p;
    ret = (p.x+p.y);
    ret = pow(ret, 1./8.);

    return ret;
}

// Utilities

void rotate(inout vec2 v, float angle)
{
	v = vec2(cos(angle)*v.x+sin(angle)*v.y,-sin(angle)*v.x+cos(angle)*v.y);
}

vec2	rot(vec2 p, vec2 ang)
{
	float	c = cos(ang.x);
    float	s = sin(ang.y);
    mat2	m = mat2(c, -s, s, c);

    return (p * m);
}

vec3	camera(vec2 uv)
{
    float		fov = 1.;
	vec3		forw  = vec3(0.0, 0.0, -1.0);
	vec3    	right = vec3(1.0, 0.0, 0.0);
	vec3    	up    = vec3(0.0, 1.0, 0.0);

    return (normalize((uv.x) * right + (uv.y) * up + fov * forw));
}

vec3 calcNormal( in vec3 pos, float e, vec3 dir)
{
    vec3 eps = vec3(e,0.0,0.0);

    return normalize(vec3(
           march(pos+eps.xyy, dir).y - march(pos-eps.xyy, dir).y,
           march(pos+eps.yxy, dir).y - march(pos-eps.yxy, dir).y,
           march(pos+eps.yyx, dir).y - march(pos-eps.yyx, dir).y ));
}

/*
general approach to finding tangent wedges:

we're looking to solve the slope factor q for the linear function q*x = y
for the convex bivariate homogeneous equation

f(x,y) = 0

1. substitute y in f(x,y) with q*x to turn it into a univariate g(x)
2. solve first derivative g'(x) = q for x
3. substitute into g(x) the solution for x to receive h()
4. solve h() = 0 for q to receive two solutions

now q0,q1 describe the two bounding slopes

substitute q0,q1 into the solution for x to receive x0,x1
then your two contact points are (x0, q0*x0) and (x1, q1*x1)

This problem also expands to 3D. For a 3D solution, you
need to work with two wedges (a perspective frustum).

for each wedge

1. substitute y|x in f(x,y,z) with q*z, to turn it into a bivariate g(x|y,z)
2. solve partial derivatives dg(x|y,z)/dx|y = 0 and dg(x|y,z)/dz = q for x|y and z
3. substitute into g(x|y,z) the solutions for x|y and z to receive h()
4. solve h() = 0 for q to receive two solutions

Special note for quadratics:
You can reconstruct the screenspace ellipse that bounds the quadratic
from four points and two normals. The resulting equations can likely
be crunched down by getting rid of the intermediates, but I yet lack
the intuition for a direct solution.

*/


struct surface2x2 {
    float c[6];
};

// for a bivariate quadratic
// f(x,y) = A*x^2 + B*y^2 + C*x*y + D*x + E*y + F
// compute the value for the given position (x y z)
float surface2x2_eval(surface2x2 surf, vec2 p) {
	float A = surf.c[0]; float B = surf.c[1]; float C = surf.c[2];
	float D = surf.c[3]; float E = surf.c[4]; float F = surf.c[5];
    return A*p.x*p.x + B*p.y*p.y + C*p.x*p.y + D*p.x + E*p.y + F;
}

// for a convex bivariate quadratic
// f(x,y) = A*x^2 + B*y^2 + C*x*y + D*x + E*y + F
// compute the bounding tangential points of a wedge with its apex at origin
// and return the slope coefficients for both wedge lines
vec2 surface2x2_proj_bounds(surface2x2 surf, out vec2 p0, out vec2 p1) {
	float A = surf.c[0]; float B = surf.c[1]; float C = surf.c[2];
	float D = surf.c[3]; float E = surf.c[4]; float F = surf.c[5];
    float f = 1.0 / (E*E - 4.0*B*F - 1.0);
    float s = -D*E + 2.0*C*F;
    float t = sqrt(-4.0*(4.0*A*B - C*C)*F*F + D*D + 4.0*(B*D*D - C*D*E + A*E*E - A)*F);
	float q0 = (s - t)*f;
    float q1 = (s + t)*f;
    float x0 = -0.5*((E - 1.0)*q0 + D)/(B*q0*q0 + C*q0 + A);
    float x1 = -0.5*((E - 1.0)*q1 + D)/(B*q1*q1 + C*q1 + A);
    p0 = vec2(x0, q0*x0);
    p1 = vec2(x1, q1*x1);
    return vec2(q0, q1);
}

