surface2x2 surf;
float fieldf(vec2 p) {
    return surface2x2_eval(surf, p);
}

void paint() {
    float t = iTime;

    // clear screen

    set_source_rgb(vec3(0.0,0.0,0.5));
    clear();

    // normal
    vec2 n = vec2(cos(t),sin(t));

    // ellipse scaling
    vec2 s = vec2(0.8 - 0.7 * abs(cos(t * 0.2)), 0.5 - 0.4 * abs(sin(t * 0.2)));

    vec2 ofs = vec2(1.0, 0.0) + n*0.5;

    float A = 1.0/(s.x*s.x);
    float B = 1.0/(s.y*s.y);
    float C = -1.0;
    surf.c[0] = A;
    surf.c[1] = B;
    surf.c[2] = 0.0;
    surf.c[3] = -2.0*A*ofs.x;
    surf.c[4] = -2.0*B*ofs.y;
    surf.c[5] = A*ofs.x*ofs.x + B*ofs.y*ofs.y + C;

    vec2 p0, p1;
    vec2 xgrad = surface2x2_proj_bounds(surf, p0, p1);

    translate(-1.0, 0.0);

    set_line_width_px(1.3);
    set_source_rgb(vec3(1.0));
    move_to(-10.0, xgrad[0] * -10.0);
    line_to(10.0, xgrad[0] * 10.0);
    move_to(-10.0, xgrad[1] * -10.0);
    line_to(10.0, xgrad[1] * 10.0);
    stroke();

    // fill ellipse
    graph2D(fieldf);
    set_source_rgb(hsl(0.0, 1.0, 0.5));
    fill_preserve(); // don't reset shape    
    set_source_rgb(vec3(1.0));
   	stroke();

    circle(p0, 0.02);
    fill();
    circle(p1, 0.02);
    fill();


}

