void draw_ranges(float y, vec2 d, vec2 a, vec2 b, Edge c, float h1, float h2) {
    rectangle(a.x, y, a.y - a.x, 0.09);
    set_source_rgba(hsl(h1,0.8,0.8,0.5));
    fill_preserve(); 
    set_source_rgb(hsl(h1,1.0,0.4));
	stroke();
	rectangle(b.x, y + 0.01, b.y - b.x, 0.07);
    set_source_rgba(hsl(h2,0.8,0.8,0.5));
    fill_preserve(); 
    set_source_rgb(hsl(h2,1.0,0.4));
	stroke();
	rectangle(d.x, y + 0.01, d.y - d.x, 0.07);
    set_source_rgba(hsl((h1 + h2)*0.5,0.8,0.8,0.5));
    fill_preserve(); 
    set_source_rgb(hsl((h1 + h2)*0.5,1.0,0.4));
	stroke();    
    if (c > 0.0) {
    	rectangle(c, y + 0.025, 0.1, 0.04);
        set_source_linear_gradient(vec4(vec3(0.0),1.0), vec4(0.0), vec2(c,0.0), vec2(c+0.1, 0.0));
	    fill();
    } else {
        c = -c;
    	rectangle(c - 0.1, y + 0.025, 0.1, 0.04);
        set_source_linear_gradient(vec4(vec3(0.0),1.0), vec4(0.0), vec2(c,0.0), vec2(c-0.1, 0.0));
	    fill();
    }
}

void paint_hit(float y, float t) {
    set_source_rgb(vec3(0.0));
    if (t == infinity) {
        circle(2.5, y + 0.045, 0.025);
        stroke();
    } else {
    	circle(t, y + 0.045, 0.025);
    	fill();
    }
}

void paint() {
    float t = iTime;

    // clear screen with a subtle gradient
    set_source_rgb(vec3(1.0));
    clear();

    set_line_width_px(1.3);
	set_source_rgb(vec3(0.0));

    translate(-1.2,0.0);

    float h = 0.11;
    float y = 1.0 - h;

    float d = (iMouse.z > 0.5)?get_query().x:abs(sin(t*0.1)*2.0);
    move_to(d, -1.0);
    line_to(d, 1.0);
	stroke();

    vec2 r = vec2(0.0,0.5)-0.25 + 0.8;
    vec2 r3 = r + 1.2;
#if 0
#define DRAW_RANGE(OP,H1,H2,R0,R1,R2) draw_ranges(y, R0, R1, R2, OP(edge_new(R1,d), edge_new(R2,d)), H1, H2); y = y - h;
#else
#define DRAW_RAY_HIT(OP,R0,R1,R2) { float t = d; for (int i = 0; i < 5; ++i) { float d = OP(csg_or(edge_new(R1,t),edge_new(R0,t)), edge_new(R2,t)); if (d <= 0.0) break; t = d; if (t == infinity) break; } paint_hit(y, t); }
#define DRAW_RANGE(OP,H1,H2,R0,R1,R2) draw_ranges(y, R0, R1, R2, OP(csg_or(edge_new(R1,d),edge_new(R0,d)), edge_new(R2,d)), H1, H2); DRAW_RAY_HIT(OP,R0,R1,R2); y = y - h;
#endif
    DRAW_RANGE(csg_or, 0.0, 0.3, r3, r-0.5,r+0.5);
    DRAW_RANGE(csg_or, 0.0, 0.3, r3, r-0.1,r+0.1);
    DRAW_RANGE(csg_or, 0.0, 0.3, r3, r, r*0.6+0.32);
    DRAW_RANGE(csg_orn, 0.0, 0.3, r3, r*0.6+0.32, r);
    DRAW_RANGE(csg_orn, 0.0, 0.3, r3, r+0.1,r-0.1);
    DRAW_RANGE(csg_orn, 0.0, 0.3, r3, r+0.5,r-0.5);
    DRAW_RANGE(csg_and, 0.6, 0.15, r3, r-0.5,r+0.5);
    DRAW_RANGE(csg_and, 0.6, 0.15, r3, r-0.1,r+0.1);
    DRAW_RANGE(csg_and, 0.6, 0.15, r3, r, r*0.6+0.32);
    DRAW_RANGE(csg_andn, 0.6, 0.15, r3, r*0.6+0.32, r);
    DRAW_RANGE(csg_andn, 0.6, 0.15, r3, r+0.1,r-0.1);
    DRAW_RANGE(csg_andn, 0.6, 0.15, r3, r+0.5,r-0.5);
    DRAW_RANGE(csg_and_not, 0.8, 0.4, r3, r-0.5,r+0.5);
    DRAW_RANGE(csg_and_not, 0.8, 0.4, r3, r-0.1,r+0.1);
    DRAW_RANGE(csg_and_not, 0.8, 0.4, r3, r, r*0.6+0.32);
    DRAW_RANGE(csg_and_not, 0.8, 0.4, r3, r*0.6+0.32, r);
    DRAW_RANGE(csg_and_not, 0.8, 0.4, r3, r+0.1,r-0.1);
    DRAW_RANGE(csg_and_not, 0.8, 0.4, r3, r+0.5,r-0.5);    
}

