#define PI 3.141592

vec2 mat_uv;

float sphere(vec3 p)
{
    vec3 q = normalize(p);
    vec2 uv = vec2(atan(q.z, q.x) / (2.0 * PI), acos(q.y) / PI);
    mat_uv = uv;
    float d = texture(iChannel0, uv).x;
    return length(p) - (1.0 + d * 0.1 + 0.1 * sin(p.y * 10. + iTime * 10.0) + 0.1 * cos(10.0 * p.x * p.z + iTime * 10.0));
}

mat3 rotateX(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat3( c, -s, 0.0,
               	 s, c, 0.0,
                 0.0 ,0.0 , 1.0);
}


mat3 rotateY(float a)
{
    float c = cos(a);
    float s = sin(a);
    return mat3( s, 0.0, c,
               	 0.0, 1.0, 0.0,
                 c ,0.0 , -s);
}

vec2 modA(vec2 v, float n)
{
    float l = length(v);
    float a = atan(v.y, v.x);
    float an = a / n;
    a = mod(a, an) - an * 0.5;
    return l * vec2(cos(an), sin(an));
}


float map(vec3 p)
{
    p = mod(p, vec3(5.0)) - 2.5;
	//p.xz = modA(p.xz, 5.0);

    //p.x -= 2.5;
    p = rotateY(iTime) * p;   
    return sphere(p);
}

vec3 rm(vec3 ro, vec3 rd)
{
    vec3 p = ro;
	for (int i = 0; i < 128; ++i)
    {
        float d = map(p);
        if (abs(d) < 0.001 || d > 1000.0)
            break;
        p += rd * d * 0.6;
    }
    return p;
}

vec3 grad(vec3 p)
{
    vec2 eps = vec2(0.001, 0.0);
    return normalize(vec3( map(p + eps.xyy) - map(p - eps.xyy),
                          map(p + eps.yxy) - map(p - eps.yxy),
                          map(p + eps.yyx) - map(p - eps.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 ro = vec3(0.0, 0.0, -6.0 + iTime * 10.0);
    vec3 rd = vec3(uv, 0.5 -  length(uv));
    rd = rotateY(PI * sin(iTime)) * rd; 

    vec3 p = rm(ro, rd);

    float d = distance(ro, p);
    vec3 n = grad(p);

    vec3 color = vec3(exp(-d * 0.1));
    color *= n * 0.5 + 0.5;
    //color *= texture(iChannel0, mat_uv).xyz; //* (abs(dot(-n, vec3(0.0, 0.0, 1.0))));


     p = rm(p + n * 0.01, reflect(rd,n));
     n = grad(p);
     color = mix(color, n * 0.5 + 0.5, 0.3);

	fragColor = vec4(color,1.0);
}

