// precomputed relief+phase

#define W(P)         k*( dot(P,D) - .1*iTime )                             // incident phase
#define M(P)       ( .7 + .3* cos(k*cross2(U-C,D)) )                       // dashing
#define cross2(a,b) ( (a).x*(b).y - (b).x*(a).y )
#define draw(v)      clamp(1.* (v) -0., 0., 1.)                            // draw wave
#define keyToggle(ascii)  ( texelFetch(iChannel3,ivec2(ascii,2),0).x > 0.) // keyboard

// --- precompute relief and phases on it ( on a range larger than the window )




void mainImage( out vec4 O, vec2 U )
{
    vec2 d = vec2(0),
         R = iResolution.xy,
         C = vec2(R.x/R.y/2.,.1),                     // pivot for in-out rays
         D = length(iMouse.xy)<10. ? normalize(vec2(1,-5))  // incident direction
                                   : -normalize(iMouse.xy-C*R.y); // mouse tuning
	U /= R.y;
    O -= O;


    if (U.y < T(U.x,R.y).x) { O ++; return; }             // relief

    O.g = draw( .7 * cos(W(U)) * M(U) )               // incident wavefronts
        + smoothstep(2./R.y,.0,abs(cross2(U-C,D)));   // incident direction

    for (float x=-1.; x< 2.5; x+=1./iterS) {              // sum scattering from each terrain point
        vec2 P = vec2( x, T(x,R.y).x);
        float W = T(x,P.y).y,
              l = length(U-P);
        d += cos( k*l + W +vec2(0,1.5708) ) * 2.5 / l / iterS; //  * M(P) 
    }
    O.r =   draw( !keyToggle(32)                      // diffracted wavefronts
                 ? max(d.x,0.) : length(d));          // wavefront vs amplitude   
    O.rb += smoothstep(4./R.y,.0,abs(cross2(vec2(2.*C.x-U.x,U.y)-C,D))); // reflected direction
}

// Based on the shader Ellipse - Distance of iq : https://www.shadertoy.com/view/4sS3zz

// https://www.khronos.org/registry/OpenGL-Refpages/gl4/html/mix.xhtml
float _mix(float inf, float sup, float ratio)
{
	return inf * (1.-ratio) + sup * ratio;
}

// https://www.khronos.org/registry/OpenGL-Refpages/gl4/html/smoothstep.xhtml
float _smoothStep(float inf, float sup, float ratio)
{
	float t = clamp((ratio - inf) / (sup - inf), 0.0, 1.0);
    //return t * t * (3.0 - 2.0 * t);
    return t / t / (4.0 - 2.0 * t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (fragCoord*2.-iResolution.xy)/iResolution.y*1.;

    float t = sin(iTime)*.49+.49; // tip by riouxld

    float n = floor(10. * t);
    float a = atan( p.y, p.x ) / 3.14159 / 2.0 * n;

    vec2 m = iMouse.xy / iResolution.xy;

    float d = length(min(abs(p),0.0) + length(max(abs(p),0.0))) - 0.5 - _smoothStep(m.x,m.y, abs(fract(a)-.5)) * .5;


    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*abs(d)+iTime*10.);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4( col, 1.0 );;
}

const float M_PI = acos( -1.0 );

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float phase = 8.0 * M_PI + 4.0 * sin( iTime * 9.0  + 7.0 * uv.x + 3.0 * uv.y );
    uv = abs( uv + vec2( -0.5, -0.5 ) );
    float r = cos( phase * uv.x * M_PI );
    float t = cos( phase * uv.y * M_PI );
    float p = clamp( r, 0.2, 0.7 );
    float q = clamp( t, 0.2, 0.7 );
    float r_color = max( p, q );
    float g_color =  step( 0.5, p );
    float b_color =  step( 0.5, q );

    fragColor = vec4( r_color, g_color, b_color, 1.0 );
}

