	float f;

    mode=(iFrame/divider)%maxmode;

	/*
#ifdef Use_Linear
	f = Linear(x, v1, v2);
#elif defined Use_Cosine
	f = Cosine(x, v1, v2);
#elif defined Use_Smoothstep
	f = Smoothstep(x, v1, v2);
