float circle( vec2 uv, vec2 pos, float r, float blur )
{
	float d = length( pos - uv );
    float cm = smoothstep( r, r-blur, d );
    return cm;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //	get normalized pixel coordinates
	vec2 uv = fragCoord.xy / iResolution.xy;

    //	correct aspect ratio distortion
    float ar = iResolution.x / iResolution.y;
    uv.x *= ar;

    //	DRAW
    vec3 col = vec3( 0.0 );

    float blr = 0.0042;

    //	left eye
    float lex = 0.4 * ar;
    float lem 	= circle( uv, vec2( lex, 0.7 ), 0.1, blr );	//	whites
    float lem2 	= circle( uv, vec2( lex, 0.6 + sin(iTime * 1.0) * 0.03 ), 0.1, blr );	//	lower lid
    float lem3 	= circle( uv, vec2( lex, 0.7 + -max( sin(iTime * 1.1) * 0.03, 0.0 ) -0.03), 0.1, blr );	//	upper lid
    float lem4 	= circle( uv, vec2( lex, 0.65 ), 0.04 + 0.01*sin(iTime), blr );	//	pupil
    //	composite
    float lec = 0.0;
    lec += lem3;
    lec *= lem;
    lec -= lem2;
    lec = max( lec, 0.0 );
    lec += lem4;
    col += vec3(lec);

    //	right eye
    float rex = 0.6 * ar;
    float rem 	= circle( uv, vec2( rex, 0.7 ), 0.1, blr );	//	whites
    float rem2 	= circle( uv, vec2( rex, 0.6 + sin(iTime * 1.0) * 0.03 ), 0.1, blr );	//	lower lid
    float rem3 	= circle( uv, vec2( rex, 0.7 + -max( sin(iTime * 1.1) * 0.03, 0.0 ) -0.03), 0.1, blr );	//	upper lid
    float rem4 	= circle( uv, vec2( rex, 0.65 ), 0.04 + 0.01*sin(iTime), blr );	//	pupil
    //	composite
    float rec = 0.0;
    rec += rem3;
    rec *= rem;
    rec -= rem2;
    rec = max( rec, 0.0 );
    rec += rem4;
    col += vec3(rec);

    //	mouth
	float mx = 0.5 * ar;
    float mm 	= circle( uv, vec2( mx, 0.4 ), 0.1, blr );
    float mm2 	= circle( uv, vec2( mx, 0.5 + sin(iTime * 10.0) * 0.03 ), 0.15, blr );
    //	composite
    float mc = 0.0;
    mc += mm;
    mc -= mm2;
    col += vec3(mc);

    //	final output
	fragColor = vec4( col ,1.0);
}

const int MAGIC_BOX_ITERS = 12;
const float MAGIC_BOX_MAGIC = 0.77;//55;

float magicBox(vec3 p) {
    // The fractal lives in a 1x1x1 box with mirrors on all sides.
    // Take p anywhere in space and calculate the corresponding position
    // inside the box, 0<(x,y,z)<1
    p = 1.0 - abs(1.0 - mod(p, 2.0));

    float lastLength = length(p);
    float tot = 0.0;
    // This is the fractal.  More iterations gives a more detailed
    // fractal at the expense of more computation.
    for (int i=0; i < MAGIC_BOX_ITERS; i++) {
      // The number subtracted here is a "magic" paremeter that
      // produces rather different fractals for different values.
      p = abs(p)/(lastLength*lastLength) - MAGIC_BOX_MAGIC;
      float newLength = length(p);
      tot += abs(newLength-lastLength);
      lastLength = newLength;
    }

    return tot;
}

// A random 3x3 unitary matrix, used to avoid artifacts from slicing the
// volume along the same axes as the fractal's bounding box.
const mat3 M = mat3(0.288, 0.699, 0.653,
                    0.069, 0.665, -0.743,
                    -0.954, 0.260, 0.143);


void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // uv are screen coordinates, uniformly scaled to go from 0..1 vertically
	vec2 uv = fragCoord.xy / iResolution.yy;

    // scroll a certain number of screenfuls/second
    //uv.x += iTime*0.02;

    float z= iTime*0.05+100.0;    
    if(iMouse.z>0.){ z += iMouse.x/500.0; }

    // Rotate uv onto the random axes given by M, and scale
    // it down a bit so we aren't looking at the entire
    // 1x1x1 fractal volume.  Making the coefficient smaller
    // "zooms in", which may reduce large-scale repetition
    // but requires more fractal iterations to get the same
    // level of detail.    
    vec3 p = 0.25*M*vec3(uv, z);

    float result = magicBox(p);
    // Scale to taste.  Also consider non-linear mappings.
    result *= 0.07;
    float result1 = smoothstep(.4, .51, result);
    float result2 = smoothstep(.15, .4, result);
    vec3 c = mix(vec3(.9,.2,.0),vec3(.1,.05,.15), result2);
    c = mix(c, vec3(0,0,0), result1);
    fragColor = vec4(c,1);

}

#define rotnor(norm) mat2(norm.xy, vec2(-norm.y,norm.x))
#define rotang(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))
#define pi acos(-1.0)
#define chains 6.0

#define gammacorrect

float torus(vec3 p, float r, float l) {

    return length(vec2(length(max(abs(p.xy)-vec2(0,l),vec2(0)))-r,p.z));
}

vec2 torusring(vec3 p) {
    float len = 1e10;
    float material;
    for (float i = 0.0; i < chains; i++) {
        float i2 = i+iTime;
        vec2 rot = vec2(cos(i2*pi*2.0/chains),sin(i2*pi*2.0/chains));

        vec3 p2 = p;
        p2 = p2 - vec3(rot,0)*3.0;
        p2.xy*=rotnor(rot);
        p2.xz*=rotang(i2*pi*0.5);
        float len2 = torus(p2,0.5,1.5)-0.1;
        if (len2 < len) {
            len = len2;
            material = i;
        }
    }
    return vec2(len,material);
}

vec2 torusringintorusrings(vec3 p) {
    vec2 len = vec2(1e10);
    for (float i = 0.0; i < chains; i++) {
        float i2 = i+iTime;
        vec2 rot = vec2(cos(i2*pi*2.0/chains),sin(i2*pi*2.0/chains));

        vec3 p2 = p;
        p2 = p2 - vec3(rot,0)*4.0;
        p2.xy*=rotnor(rot);
        p2.xz*=rotang(i2*2.0);
        vec2 len2 = torusring(p2);
        len2.y += i*chains;
        if (len.x > len2.x) {
            len = len2;
        }
    }
    return len;
}

vec2 map(vec3 p) {
    vec2 len = torusringintorusrings(p);

    return len;
}

vec3 normal(vec3 p) {
    vec2 eps = vec2(0.01,0.0);

    return normalize(vec3(
        map(p+eps.xyy).x-map(p-eps.xyy).x,
        map(p+eps.yxy).x-map(p-eps.yxy).x,
        map(p+eps.yyx).x-map(p-eps.yyx).x));
}

//hash function by Dave_Hoskins https://www.shadertoy.com/view/4djSRW
#define HASHSCALE3 vec3(.131, .130, .1973)
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * HASHSCALE3);
   p3 += dot(p3, p3.yzx+19.19);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}

//shading
vec4 shade(vec3 p, vec3 d, vec3 objnorm, float depth, float material) {
    vec3 sun = normalize(vec3(0,0,1));
    vec3 reflectnorm = reflect(d,objnorm);
    vec3 color = hash31(material)+0.1;
    vec4 fragColor = vec4(color*max(0.4,dot(objnorm,-sun)),1.0);
    fragColor = max(fragColor,(dot(reflectnorm,-sun)-0.9)*12.0);
    //fragColor = mix(background,fragColor,clamp(3.0-depth*0.05,0.0,1.0));
    return clamp(fragColor,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0,0,-8);
    vec3 rd = normalize(vec3(uv,1));
    float dist = 0.0;

    bool hit = false;
    vec2 len;
    for (int i = 0; i < 100; i++) {
        len = map(ro);

        if (len.x < 0.01 || dist > 20.0) {
            hit = len.x < 0.01;
            break;
        }

        dist += len.x;
        ro += rd*len.x;
    }
    if (hit)
	fragColor = shade(ro,rd, normal(ro), dist, len.y);
    else
    fragColor = abs(vec4(uv,sin(iTime)*0.5+0.5,1.0))*0.2;

    #ifdef gammacorrect
    fragColor = sqrt(fragColor);
    #endif
}

