//smoothmin function https://www.shadertoy.com/view/Ml3Gz8
float smin(float a, float b, float k) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

//hash function by Dave_Hoskins https://www.shadertoy.com/view/4djSRW
#define HASHSCALE3 vec3(.131, .130, .1973)
vec3 hash31(float p)
{
   vec3 p3 = fract(vec3(p) * HASHSCALE3);
   p3 += dot(p3, p3.yzx+19.19);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}
float hash11(float p)
{
   float p3 = fract(p * HASHSCALE3.x);
   p3 += 3.0*p3*(p3+19.19);
   return fract(2.0*p3*p3); 
}

float torus(vec3 p, float r, vec2 l) {

    return length(vec2(length(max(abs(p.xy)-l,vec2(0)))-r,p.z));
}

float cube(vec3 p, vec3 s) {
    return length(max(abs(p)-s,vec3(0)));
}

float jumpdroid(vec3 p, float time) {

    p.y -= 2.5+sin(mod(clamp(time,0.0,1.0)*pi,pi))*3.0;
    //body
    float len = cube(p, vec3(0,0.75,0))-1.0;

    //head
    len = max(len, max(0.1-abs(p.y-0.75),0.0));
    vec3 p2 = p-vec3(0,1,0);
    p2.x = abs(p2.x);
    p2.xy *= rotang(2.5);
    p2.y += 1.0;
    len = min(len, cube(p2,vec3(0,0.2,0))-0.1);

    //eyes
    p2 = abs(p-vec3(0,1.15,0))-vec3(0.4,0,0);
    len = max(len, -length(p2.xy)+0.1);

    //legs
    p2 = abs(p-vec3(0,-1.75,0))-vec3(0.5,0,0);
    len = min(len,cube(p2,vec3(0,0.5,0))-0.25);

    //arms
    p2 = p-vec3(0,0.5,0);
    p2.x = abs(p2.x)-1.4;
    p2.yz *= rotang(pi*abs(mod(clamp(time*2.0+.5,0.0,2.0)+1.0,2.0)-1.0));
    p2.y += 0.5;
    len = min(len,cube(p2,vec3(0,0.5,0))-0.25);
    return len;
}

float ropedroid(vec3 p, float time) {

    p.x = abs(p.x)-17.0;
    p.y -= 2.5;
    //body
    float len = cube(p, vec3(0,0.75,0))-1.0;

    //head
    len = max(len, max(0.1-abs(p.y-0.75),0.0));
    vec3 p2 = p-vec3(0,1,0);
    p2.x = abs(p2.x);
    p2.xy *= rotang(2.5);
    p2.y += 1.0;
    len = min(len, cube(p2,vec3(0,0.2,0))-0.1);

    //eyes
    p2 = abs(p-vec3(0,1.15,0))-vec3(0.4,0,0);
    len = max(len, -length(p2.xy)+0.1);

    //legs
    p2 = abs(p-vec3(0,-1.75,0))-vec3(0.5,0,0);
    len = min(len,cube(p2,vec3(0,0.5,0))-0.25);

    //arms
    p2 = p-vec3(1.4,0,0);
    len = min(len,cube(p2,vec3(0,0.5,0))-0.25);
    p2 = p-vec3(-1.4,0.4,0);
    p2.yz *= rotang(time*pi);
    p2.xy *= rotang(0.5);
    p2.x += 0.5;
    len = min(len,cube(p2,vec3(0.5,0.0,0))-0.25);
    return len;
}

float rope(vec3 p, float time) {
    p.y -= 2.9;

    vec2 dir = vec2(cos(time*pi),sin(time*pi));

    ///the rope shape (too lazy to find out what 15*15 is)
    float force = (1.0-p.x*p.x/225.0)*5.0;

    vec2 p2 = dir*(0.4+force);

    //prevents rope from going in the floor
    float ground = smin(-p2.x+2.8,0.0,1.0);
    p2.x += ground;

    //makes the rope "drag along the ground"
    p2.y -= ground*0.5;

    return max(length(p.yz+p2)-0.1,abs(p.x)-14.5);
}

vec2 map(vec3 p) {
    vec2 len = vec2(1e10,0);

    vec3 p2 = p + vec3(2,0,0);
    float person = clamp(floor(p2.x*0.25),-2.0,2.0);
    p2.x = p2.x - person*4.0-2.0;
    float time = mod(iTime+hash11(person+floor(iTime*0.5))*0.2,2.0)-1.0;
    len.x = jumpdroid(p2,time)*0.8;
    len.y = 4.0;

    float len2 = ropedroid(p,-iTime-0.55);
    if (len2 < len.x) {
        len.x = len2;
        len.y = 4.0;
    }

    len2 = rope(p,-iTime-0.55)*0.7;
    if (len2 < len.x) {
        len.x = len2;
        len.y = 4.2;
    }
    return len;
}

vec3 normal(vec3 p, float len) {
    vec2 eps = vec2(0.001,0.0);

    return normalize(vec3(
        len-map(p-eps.xyy).x,
        len-map(p-eps.yxy).x,
        len-map(p-eps.yyx).x));
}

//shading
vec4 shade(vec3 p, vec3 d, vec3 objnorm, float depth, float material) {
    vec3 sun = normalize(vec3(1,1,-1));
    vec3 reflectnorm = reflect(d,objnorm);
    vec3 color = hash31(material)+0.1;
    vec4 fragColor = vec4(color*max(0.4,dot(objnorm,sun)),1.0);
    fragColor = max(fragColor,(dot(reflectnorm,sun)-0.95)*20.0);
    //fragColor = mix(background,fragColor,clamp(3.0-depth*0.05,0.0,1.0));
    return clamp(fragColor,0.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec3 cam = vec3(0,3,-14);
    vec3 ro = cam;
    vec3 rd = normalize(vec3(uv,1));
    float dist = 0.0;

    bool hit = false;
    vec2 len;
    for (int i = 0; i < 100; i++) {
        len = map(ro);

        if (len.x < 0.01 || dist > 30.0) {
            hit = len.x < 0.01;
            break;
        }

        dist += len.x;
        ro += rd*len.x;
    }
    if (hit) {
		fragColor = shade(ro,rd, normal(ro, len.x), dist, len.y);
    } else {
        //ray-plane intersection if the raymarching ray doesen't hit
        float len = (-cam.y/min(rd.y,-0.00001));
        ro = cam+rd*len;
        if (len < 1000.0) {//cheap distance field shadowing and texture
        	fragColor = texture(iChannel0,ro.xz*0.2)*min(map(ro).x,1.0);
            return;
    	} else {//background if ray-plane intersection doesn't hit
        	fragColor = abs(vec4(uv,sin(iTime)*0.5+0.5,1.0))*0.2;
        }
    }
    #ifdef gammacorrect
    fragColor = sqrt(fragColor);
    #endif
}

