void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    int idx = int( floor( 3.0 * uv.x ) + 3.0*floor( 3.0 * uv.y ) );

    const int NUM_SAMPLES = 16;
	const float RCP_NUM_SAMPLES_F = 1.0  / float(NUM_SAMPLES);

    float sum = 0.0;
    for ( int i=0; i<NUM_SAMPLES; ++i )
    {
        if ( idx == 6 )
        {
            sum = hash12n( uv + float(iFrame-i) );
        }
        if ( idx == 3 )
        {
            float rnd = hash12n( uv + float(iFrame-i) );
            sum += RCP_NUM_SAMPLES_F * rnd;
        }
        else if ( idx == 0 )
        {
            float rnd = hash12n( uv );

            float grm = GoldenRatioMultiple( iFrame - i );
            rnd = fract( rnd + grm );

            sum += RCP_NUM_SAMPLES_F * rnd;
        }
        else if ( idx == 8 )
        {
            //note: see https://www.shadertoy.com/view/4sGSDd
            vec2 fc = fragCoord.xy;
            fc += vec2(ivec2(1321,1327)*(iFrame-i));
            vec2 p = fc/iChannelResolution[0].xy;
            float s0 = texture( iChannel0, p, 0.0).r;
            float s1 = texture( iChannel0, p, 1.0).r;
            float rnd = s0 - s1 + 0.5;

            sum = rnd;
        }
        else if ( idx == 5 )
        {
            vec2 fc = fragCoord.xy;
            fc += vec2(ivec2(1321,1327)*(iFrame-i));
            vec2 p = fc/iChannelResolution[0].xy;
            float s0 = texture( iChannel0, p, 0.0).r;
            float s1 = texture( iChannel0, p, 1.0).r;
            float rnd = s0 - s1 + 0.5;

            sum += RCP_NUM_SAMPLES_F*rnd;
        }
        else if ( idx == 2 )
        {
            vec2 fc = fragCoord.xy;
            vec2 p = fc/iChannelResolution[0].xy;
            float s0 = texture( iChannel0, p, 0.0).r;
            float s1 = texture( iChannel0, p, 1.0).r;
            float rnd = s0 - s1 + 0.5;

            float grm = GoldenRatioMultiple(iFrame-i);
            rnd = fract(rnd + grm);

            sum += RCP_NUM_SAMPLES_F*rnd;
        }
        else if ( idx == 7 )
        {
            vec2 fc = fragCoord.xy;
            fc += vec2(-11.0,7.0)*vec2(iFrame-i); //TODO: entirely empirical and likely terrible offset...
            sum = InterleavedGradientNoise( fc );
			//sum = ScreenSpaceDither( fc );
        }
        else if ( idx == 4 )
        {
            vec2 fc = fragCoord.xy;
            fc += vec2(-11.0,7.0)*vec2(iFrame-i); //TODO: entirely empirical and likely terrible offset...

            float ign = InterleavedGradientNoise( fc );
            //float ign = ScreenSpaceDither( fc );

            sum += RCP_NUM_SAMPLES_F * ign;
        }
        else if ( idx == 1 )
        {
            vec2 fc = fragCoord.xy;
            //fc += vec2(-11.0,7.0)*vec2(iFrame-i); //TODO: entirely empirical and likely terrible offset...

            float ign = InterleavedGradientNoise( fc );
            //float ign = ScreenSpaceDither( fc );

            float grm = GoldenRatioMultiple(iFrame-i);
            ign = fract(ign + grm);

            sum += RCP_NUM_SAMPLES_F*ign;
        }
    }

    fragColor = vec4( vec3(sum), 1.0 );

    //lines
    fragColor *= step( 6.0/iResolution.x, 1.0-abs(2.0*fract(3.0*uv.x)-1.0));
    fragColor *= 0.75+0.25*(step( 5.0/iResolution.y, 1.0-abs(2.0*fract(3.0*uv.y)-1.0)));

}

float Circle (vec2 uv, vec2 p, float r, float blur) {

    float d = length(uv - p);
    float c = smoothstep(r, r - blur, d);

    return c;
}

float Band (float t, float start, float end, float blur){
	float step1 = smoothstep (start - blur, start + blur, t);
    float step2 = smoothstep (end + blur, end - blur, t);

   	return step1 * step2;
}

float Rect (vec2 uv, float left, float right, float bottom, float top, float blur) {
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);

    return band1 * band2;
}

float Smiley (vec2 uv, vec2 p, float size) {
    uv -= p;	// translating coordinate system
    uv /= size; // scaling coordinate system

    float mask = Circle(uv, vec2(0.), .4, .01);

    mask -= Circle(uv, vec2(-.08, .08), .05, .01);
    mask -= Circle(uv, vec2(.08, .08), .05, .01);

    float mouth = Circle(uv, vec2(0.), .3, .01);
    mouth -= Circle(uv, vec2(0., 0.1), .33, .01);

    mask -= mouth;

    return mask;
}

float remap01 (float a, float b, float t) {
    return (t-a) / (b-a);
}

float remap (float a, float b, float c, float d, float t) {
    return remap01 (a, b, t) * (d-c) + c;
}

