    pos.xy *= rot(dist*.5);
    amod(pos.xz, 4.);
    pos.x = repeat(pos.x, 1.);
    pos.y = repeat(pos.y, 2.);
    scene = min(scene, sdSphere(pos, .25));
    return scene;
}

vec3 getNormal (vec3 p) {
    vec2 e = vec2(0.01,0.);
    return normalize(vec3(map(p+e.xyy)-map(p-e.xyy),
                          map(p+e.yxy)-map(p-e.yxy),
                          map(p+e.yyx)-map(p-e.yyx)));
}

vec3 applyCamera (vec3 p) {
    float t = iTime*.3;
    p.xz *= rot(t);
    p.xy *= rot(t*.7);
    p.yz *= rot(t*.5);
    return p;
}

void mainImage( out vec4 color, in vec2 coord )
{
	vec2 uv = (coord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 eye = applyCamera(vec3(uv, -10.));
    vec3 ray = applyCamera(normalize(vec3(uv, 1.)));

    vec3 pos = eye;
    float shade = 0.;
    for (float i = 0.; i <= 1.; i += STEPS) {
        float dist = map(pos);
        if (dist < VOLUME_BIAS) {
            shade = 1.-i;
            break;
        }
        dist *= STEP_DAMPING + .1 * rng(uv+fract(iTime));
        dist = max(MIN_DIST, dist);
        pos += dist * ray;
    }
    vec3 normal = getNormal(pos);
	color = vec4(1);
    color.rgb = mix(orange, red, dot(normal, vec3(0,0,1))*.5+.5);
    color.rgb *= shade;
}

void mainImage(
    out vec4 color, // in [0,1]^4
    in vec2 coord   // in [0,iResolution.xy[
) {
    float zoom = (cos(iTime*3.)+2.)*10.;// > 0
    float signedDistLinePixels; // > 0, unit is pixels
    // algebraic distance, we keep the side info by not using abs()

    // Exo 1 ax+by+c=0

    // Method a
    float a = iMouse.x-40.;
    float b = -iMouse.y+120.;
    float c = 140.;

    // distance to the line defined by (ax+by+c=0)
    // unit is pixel
    signedDistLinePixels = (a*coord.x + b*coord.y + c)/sqrt(a*a + b*b);

    // Method b :
    // could also be written like this in homogeneous coordinates
    // + using GLSL special instructions
    vec3 xy1 = vec3(coord,1.);
    vec3 abc = vec3(a,b,c);
    signedDistLinePixels =
         dot(xy1,abc) // vector dot product
       / length( // vector Euclidean norm
           abc.xy  // or vec2(a,b)
    );

	color.rgb = mix( // 1-linear interpolation in 3D
          vec3(1.) // white
        , signedDistLinePixels < 0. ? vec3(1.,.0,.0) : vec3(.0,.0,1.) // red or blue
        , clamp( // here the coeff for the interpolation
            pow(
                abs( // removing the side info
                    signedDistLinePixels/iResolution.x // removing the pixels units
                   *zoom) // make the line thinner or bigger
              , iMouse.z > .5 ? 1.5 : 1. // smoother gradient when mouse pressed
            )
          ,0.,1.) // minimax to put the value in [0,1]
                  // and not have values others than mix of red white blue
                  // not mandatory, just a protection
    );
}

