//ichannel0 is for keyboard input
//, while space, move camera to center fror hall of pirrots position.

//max view distance. (loses its meaning where portals make space noneuclidean)
#define maxdist 100.
//iterations trough space between portals
#define iterRm 200.
//iterations trough portals (max number of portals trough portals)
#define iterTp 20

//ollj made all 3 epsilons smaller by *=.1
//this, fits the small scale of the set better.
//this also required more [iterRm]
//epsilon raymarch
#define epsRm .001
//epsilon for normal, derivative
#define epsDx .001
//epsilon shadows
#define epsShad .01

//simple lazy fov; 1=parallel projection ; 0=180deg;  
#define fov .5

//Do intro where the camera drops down, +1exp() to camera movement.
//#define doCameraDrop

#define portalThickness (cos(iTime)*.1+.1)

//glossyness
#define scalePhong .2

//background color, sky color or microwave background (scatter max)
#define cCMB vec4(.3,.5,1,1)

#define lightdir normalize(vec3(-1,1,.5))
#define lightintensity 1.5
#define dark vec3(.2,.4,.4)
#define floorheight -1.5

/*
i stretched portal scale to test the coonstrains of:
- more obvious portal scaling distortion 
- - portals are still lipschitz continuous, just not classically euclidean.
- portals intersecting each other
- one portal intersecting a solid (the floor)
- hit space and have the camera be inside the object with the portal on it.
- - camera close to portal should tepeport the camera RAYS instantly.
- - even though the camera is static, only its rays are not.


Currently objects to not teleport trough portals
, they just intersect portals.
BUT:
Any object that exists on 2 sides of a portal (crossing a portal)
 is essentially bounding-volume-hierarchy-split in 2 seperate objects
  for as long as its volume intersects the portal.
 A volume that only tangentially touches the portal is excluded here.

instead of 2 intersecting objects, a portal face cuts 1 object in 2.

Such a split object may then have 2 densities and velocities
 on both sides of a portal, to remain continuity along the portal.
Such a split object is likely matrix deformed on one side.
 it is no longer a simple shape, but highly deformed.

This also accounts for portals that exist on 2 sides of a portal
 due to intersecting portals
 To be resolved with a BVH?
 - portal cuts tend to be hierarchical recursive iterations.
 - you would traverse a tree hierarchy to check distances to all cut sub-objects.


there should be a simpler way to do this.
A portal-relativity, where the portal is considdered static.
 but all else moves relative to the portal.
You imagine the world from the POW of a portal.
then you either do;
- Some type of lotenz- transform between portals, keeping all portals relative to another
- a [portal bounding hierarchy] of portals.

todo
, needs quaternion rotation
, and all the stuff you can extend portals with
, including a movable camera that gets teleported.
*/

//material g-buffer enum
#define background 0.
#define mPortal1 1.
#define mPortal2 2.
#define mFrame 3.
#define mFloor 4.
#define mGeometry 5.

#define pi0 6.28318530718
#define pi  acos(-1.)
#define pi2 acos(0.)


float suv(vec2 a){return a.x+a.y;}
float suv(vec3 a){return dot(a,vec3(1));}//dot()can be faster than 2add.
float mav(vec2 a){return max(a.x,a.y);}
float mav(vec3 a){return max(max(a.x,a.y),a.z);}

//dir and up should be a vec4 quaternion instead!
//not just for performance, but aslso to avoid gimbal locking
struct PortalFrame {
    vec3 c;
    vec3 dir;
    vec3 up;
    vec2 dims;
}p1, p2;//i am not a fan of global structs here.
//but portals
//,that make space noneuclidean are a reasonable exception for nor

float boxsdf(vec3 u,vec3 e, out vec3 o){
 e*=.5;o=u/mav(e);return mav(vec3(abs(u)-e));}

float cylindersdf(vec3 u,float r,float h,out vec3 o){
 o=u/max(r,h);return max(length(u.xz)-r,max(-u.y,u.y-h));}

vec2 portalsdf(vec3 u,PortalFrame p, out vec3 a){
 vec3 r=u-p.c;
 vec3 w=normalize(cross(p.dir,p.up));
 vec3 v=cross(w,p.dir);
 //above dual crossproduct rotation is slower than quaternion rotation.
    //but for small angles, this is the fallback of quaternion rotation anyways.
 a=vec3(dot(r,w),dot(r,v),dot(r,p.dir));
 p.dims=p.dims/2.;
 float d=mav(vec3(abs(a.z)-portalThickness*.5,abs(a.xy)-p.dims.xy-portalThickness));
 // float y=all(lessThan(abs(a.xy), p.dims.xy))?portalmat1:mFrame;
 return vec2(d,all(lessThan(abs(a.xy), p.dims.xy))?mPortal1:mFrame);}

//if(distance is smaller than previous) also write textureID
void bgw(inout vec2 d,inout vec3 p,vec3 q,float h,float m){
 if(h<d.x){d=vec2(h,m);p=q;}}
void bgw(inout vec2 d,inout vec3 p,vec3 q,float h){
 bgw(d,p,q,h,mGeometry);}//bgw==buffer-g-write ==write objectID.

//p* and q* are "portal target bijectuive-switcheroo coordinates.
//distance field gradient
vec2 dg(vec3 u,out vec3 p){
 vec3 q1,q2;//potals are structs that will tell you their "exit coordinates"
 vec2 d,d1=portalsdf(u,p1,q1),d2=portalsdf(u,p2,q2);
 if(d1.x<d2.x){d=d1;p=q1;
 }else        {p=q2;d=vec2(d2.x, d2.y < 2.5?mPortal2:mFrame);}
 bgw(d,p,u,max(u.y-floorheight,length(u.xz)-10.),mFloor);//floor
 bgw(d,p,u,length(u-vec3(2,0,0))-.8);//sphere, radius of 8
 bgw(d,p,u,cylindersdf(u-vec3(-0.3, -1.5, 0.), 0.1, 1.5, q1));//cylinder
 bgw(d,p,u,boxsdf(u-vec3(0,-.5,-3),vec3(.4,1.7,.4),q1));//box
 bgw(d,p,u,boxsdf(u-vec3(0,-1,3),vec3(5.,.5,.9),q1));//box
 return d;}//.y is a materialID g-buffer

//sphere tracking, raymarching, linear gradient descent
//i guess for portals you need it to be this explicit
vec2 gd(vec3 u, vec3 t, out vec3 o) {
 float a=0.;//accumulated distance traveled
 vec2 g=dg(u,o);//gradient at u, save travel sphere.
 for(float i=.0;i<iterRm;i++){
  a+=g.x;
  g=dg(u+a*t,o);
  if(abs(g.x)<epsRm){return vec2(a,g.y);//hit a surface
  }else if(g.x>maxdist)break;//too far from camera
 }return vec2(maxdist,background);}//not hit anything
//having o store the position along the ray is infficient.
//it accumulates more precision loss than it gains.

vec3 normal(vec3 u){vec3 c;vec2 e=vec2(epsDx,0);return normalize(vec3
(dg(u+e.xyy,c).x-dg(u-e.xyy,c).x
,dg(u+e.yxy,c).x-dg(u-e.yxy,c).x
,dg(u+e.yyx,c).x-dg(u-e.yyx,c).x));}

vec2 shaderay(vec3 u,vec3 t,float h) {//origin,dirction,hardness
 vec3 c,n=normal(u);
 vec2 ds=gd(u+epsShad*lightdir,lightdir,c);
 float l=max(0.,dot(n,lightdir))*lightintensity;//lambert
 l*=sign(.5-ds.y)*.5+.5;//l=ds.y<.5?l:0.;    
 float p=pow(max(0.,dot(normalize(lightdir-t),n)),h);//phong
 return vec2(l,p*scalePhong);}


//for abstraction and development
//, texturing patterns are their own functions
vec3 portalBorderColor(vec3 c){c=step(mod(c,.5),vec3(.25));
 return mix(vec3(.2),vec3(.2,.7,.6),abs(abs(
 #if 0
  c.x+c.y)+c.z));}//pattern ollj
 #else
  c.x-c.y)-c.z));}//pattern original
 #endif
//return checkerboard pattern of point c
vec3 yellowObjects(vec3 c){c=step(mod(c,.5),vec3(.25));return mix//pattern scaling
(vec3(1.,.7, .2)//sun-yellow (scatter low)
,vec3(.4,.8,.4) //leaf-green (scatter medium)
,abs(abs(c.x-c.z)-c.y));}//3d checkerboard pattern
vec3 ground(vec3 c){c=step(mod(c,1.),vec3(.25));return mix
(vec3(.6)
,vec3(.7)
//,abs(c.x-c.z));}//2d ground pattern original
,abs(c.x)-c.z);}//2d ground pattern ollj

//teleport th point trough a portal
vec4 tp(inout vec3 e,inout vec3 t,inout vec3 c,vec4 o,inout PortalFrame p1,inout PortalFrame p2){
   vec3 u=normalize(cross(p1.dir,p1.up));
   vec3 v=cross(u,p1.dir);
   float x=dot(t,u),y=dot(t,v),z=dot(t,p1.dir);
   u=normalize(cross(p2.dir, p2.up));
   v=cross(u,p2.dir);
   vec2 s=p2.dims/p1.dims;//portal scaling
   t=normalize(x*u*s.x 
              +y*v*s.y
              +z*p2.dir);//teleport ray direction
    //above and below doesnt simplify, because u and v are vec3, .
   e=p2.c
    +c.x*u*s.x 
    +c.y*v*s.y 
    -c.z*p2.dir 
    +2.*epsRm*t;//teleport camera position
 return o;}//debug error output color
//for [max portal levels] or for [disabled portal entrance]
//given that this calculates the differential between 2 rotations
//, tp()  begs for quaternions.

#define sc(a) vec2(sin(a),cos(a))

void mainImage( out vec4 o, in vec2 fragCoord ){o=vec4(1);
 #ifdef doBuffer
  float s=texture(iChannel0,KEY_SPACE).r;
 #else
  float s=0.;
 #endif

 vec2 aa=sc(clamp(iMouse.y/iResolution.y-.5,-.15,1.)*pi2);
 vec2 cph=sc((iMouse.x/iResolution.x-.35)*pi0)*aa.y;     
 vec3 e=(.01+5.*(1.-s))*vec3(cph.x,aa.x
       #ifdef doCameraDrop
        +2.*exp(-iTime)
       #endif
       ,cph.y);//camera rotation
 vec3 w=normalize(-e);
 e+=vec3(-.3,.05,0);//eye position, relevant for "distance to eye"
 vec3 u=normalize(cross(w,vec3(0,1,0)));
 vec3 v=cross(u,w);
 vec3 t=normalize(fov*w
                 +(fragCoord.x/iResolution.x-.5)*u
                  +(fragCoord.y/iResolution.x-.5*iResolution.y/iResolution.x)*v);
 //I have seen worse [camera to ray] functions.

 float ts=(sin(iTime*.2)+1.)*pi;
 p1=PortalFrame(vec3(0)//center
               ,normalize(vec3(1,.1*cos(iTime*2.)*sin(ts),.0))//forward
               ,vec3(0,1,.2*sin(iTime*2.)*cos(ts))//up
               ,vec2(2,3.+cos(ts*.5)));//scale
 p2=PortalFrame(vec3(-1.5,0,0)//center
               ,vec3(sc(ts),0).yzx//forward
               ,vec3(0,1,0)//up
               ,vec2(3,2));//scale
 //above swivels the parameters of 2 "portals"
 // That would mbe much simpler with a quaternion.    

 for(int i=0;i<iterTp;i++){
  vec3 c;//stores position along ray
  vec2 d=gd(e,t,c);
  if(d.y<.5){o=cCMB;break;//cBackground
  }else if(d.y<1.5){o=tp(e,t,c,vec4(.2,.8,1,1),p1,p2);//cPortal1
  //break;//disable cyan-blue portal entrance
 }else if(d.y<2.5){o=tp(e,t,c,vec4(.8,.2,1,1),p2,p1);//cPortal2   
  //break;//disable magenta-pink portal entrance 
 }else if(d.y<3.5){//cPortalBorder
  float s=shaderay(e+d.x*t,t,1.).x;
  o.xyz=max(dark.b,s)*portalBorderColor(c);
  break;//hit surface
 }else if(d.y<4.5){//floor
   vec2 s=shaderay(e+d.x*t,t,10.);
   o=vec4(max(dark.b,s.x)*ground(c)+s.y,1);
   break;//hit surface
  }else{//yellow checkered objects       
  vec2 s=shaderay(e+d.x*t,t,5.4);
   o.xyz=max(dark.b,s.x)*yellowObjects(c)+s.y;
  }
 }}

