#define mmin(v, d, i) (v.x > d ? vec2(d,i) : v)
#define ID_SPHERE 1.0
#define ID_LIGHT 2.0
#define ID_BLACK 3.0
#define PI 3.14159265

float lc;

float sphere(vec3 p, vec3 pos, float id)
{
  vec3 q = normalize(p - pos);
  vec2 uv = vec2(atan(q.z, q.x) / (2.0 * PI), acos(q.y) / PI);
  float d = 0.15 * textureLod(iChannel0, uv + sin(2.0 * PI * id) * iTime * 0.1 + vec2(id * 0.4), 0.0).x;
  return length(p - pos) - (0.35)* (1.0 + d * 0.15);
}

float cylinder(vec3 p)
{
  return length(p.xz) - 0.2 - 0.1 * lc;
}

mat3 rotateY(float a)
{
  float c = cos(a);
  float s = sin(a);
  return mat3(c, 0.0, -s, 
            0.0, 1.0, 0.0, 
              s, 0.0, c);
}

mat3 rotateZ(float a)
{
  float c = cos(a);
  float s = sin(a);
  return mat3(c, -s , 0.0, s, c, 0.0 , 0.0 ,0.0 ,1.0);
}

mat3 rotateX(float a)
{
  float c = cos(a);
  float s = sin(a);
  return mat3(1.0, 0.0 ,0.0,
              0.0 , c, -s,
              0.0, s, c);
}

float modA(inout vec2 v, float n, float len)
{
  float l = length(v);
  float an = PI * 2.0 / n; 

  float a = atan(v.y, v.x);
  float id = floor(a / an);
  a = mod(a, an) - an * 0.5;
  //l = mod(l, len) - len * 0.5;
  v = vec2(cos(a), sin(a)) * l;
  return id;
}

vec2 map(vec3 p)
{
  float d = 100000.0;
  for (int i = 0; i < 8; ++i)
  {
    int j = i + int(iTime * .1);
    vec3 q = rotateY(iTime * (0.1 + 0.3 * fract(0.3 + 0.1451 * float(i)))) * p;
    float id = modA(q.xz, 10.0 + 4.0 * float(i), 4.0);
    float x = 1.0 + (float(j) - iTime * .1) * .5 + 0.3 * fract(id * 0.534);
    d = min(sphere(q, vec3(x, sqrt((x - 1.0) * 0.9) - 0.3 + 0.2 * textureLod(iChannel1, vec2(0.75, fract(id * 0.1554)), 0.0).x, 0.0), fract(id * 0.15614 * float(i))), d);
  }
  vec2 v = vec2(d, ID_SPHERE);
  v = mmin(v, cylinder(p), ID_LIGHT);
  return v;
}

vec4 rm(vec3 ro, vec3 rd)
{
  vec3 p = ro;
  vec2 d;
  float id = ID_BLACK;
  for (int i = 0; i< 64; ++i)
  {
    d = map(p);
    if (abs(d.x) < 0.001)
    {
      id = d.y;
      break;
    }
    else if (d.x > 1000.0)
      break;
    p += rd * d.x * 0.9;
  }
  return vec4(p, id);
}

vec3 grad(vec3 p)
{
  vec2 e = vec2(0.01, 0.0);
  return normalize(vec3(map(p + e.xyy).x - map(p - e.xyy).x, map(p + e.yxy).x - map(p - e.yxy).x, map(p + e.yyx).x - map(p - e.yyx).x));
}

float f(float a)
{
  a *= PI;
  return pow(abs(cos(a)) + abs(sin(a)), -1.0 / 0.05);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  float a = -PI / 2.0;
  vec3 ro = vec3(3.5 * cos(a), 3.0, -2.0 + 3.5 *sin(a));
  vec3 rd = vec3(uv, 1.0);
  //rd = rotateY(-PI / 2.0 + a) * rd;
  rd = rotateX(-0.2) * rd;
  rd = rotateZ(0.2) * rd;

  lc = clamp(1.0-f(.05*iTime) - f(iTime + sin(iTime * 3.0)), 0.0, 1.0);
  vec4 q = rm(ro, rd);
  vec3 p = q.xyz;
  float id = q.w;
  float d = distance(ro, p);

  vec3 n = grad(p);



  vec3 color;
  if (id == ID_LIGHT)
    color = vec3(0.8 + 0.2 * lc);
  else if (id == ID_SPHERE)
  {
    color = vec3(0.1);
    color *= n * 0.5 + 0.5;
    vec3 pp = p;
    q = rm(p + 0.1 * n, reflect(rd, n));
    p = q.xyz;
    id = q.w;
    n = grad(p);
    if (id == ID_LIGHT)
      color =  mix(color, vec3(lc) * exp(-0.2*distance(vec2(0.0), pp.xz)), 0.7);
    else if (id == ID_SPHERE)
      color = mix(color, 0.15 * (n * 0.5 + 0.5), 0.3);
  }

  if (id != ID_LIGHT)
    color *= exp(-d * 0.1);
  color = pow(color, vec3(1.0 / 1.1));
  fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord - iResolution.xy ) / iResolution.x;
    float t = iTime * 5.0;
    uv *= mat2(cos(t),sin(t),-sin(t),cos(t)); // image rotation
	float theta = (atan(uv.y,uv.x) + 3.14)/6.28;
    float dist = length(uv)*2.;
    float spirals = 2.;
    float value = theta + dist*spirals;
    value = fract(value);
    //value = (sin(value*6.28)+1.) * 0.5;
    fragColor = vec4(value);
}

