#define path(t) sin(t * 0.5) * 1.5

vec4 df(vec3 p)
{
    vec3 tex = texture(iChannel0, p.xz * 0.5).rgb;
    float disp = dot(tex, vec3(0.01));

    p.xy += path(p.z);

    float a = cos(p.z * 2.);
    p.xy *= mat2(cos(a),-sin(a),sin(a),cos(a));
    float sp = fract(-atan(p.x,p.y)/3.14159*8.+(mod(p.z,1.)-0.5));

    float tutu = (0.8 - length(p.xy)) * sp + disp;

    return vec4(max(-length(p.xy) + .78, tutu), vec3(0));
}

vec3 nor( vec3 pos, float prec )
{
	vec3 eps = vec3( prec, 0., 0. );
	vec3 nor = vec3(
	    df(pos+eps.xyy).x - df(pos-eps.xyy).x,
	    df(pos+eps.yxy).x - df(pos-eps.yxy).x,
	    df(pos+eps.yyx).x - df(pos-eps.yyx).x );
	return normalize(nor);
}

// return color from temperature 
//http://www.physics.sfasu.edu/astro/color/blackbody.html
//http://www.vendian.org/mncharity/dir3/blackbody/
//http://www.vendian.org/mncharity/dir3/blackbody/UnstableURLs/bbr_color.html
vec3 blackbody(float Temp)
{
	vec3 col = vec3(255.);
    col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
   	col.y = 100.04 * log(Temp) - 623.6;
   	if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
   	col.z = 194.18 * log(Temp) - 1448.6;
   	col = clamp(col, 0., 255.)/255.;
    if (Temp < 1000.) col *= Temp/1000.;
   	return col;
}

// get density of the df at surfPoint
// ratio between constant step and df value
float SubDensity(vec3 surfPoint, float ms) 
{
	vec3 n = nor(surfPoint,0.0001); 
	return df(surfPoint - n * ms).x/ms;
}

// from shane shaders
// Tri-Planar blending function. Based on an old Nvidia writeup:
// GPU Gems 3 - Ryan Geiss: http://http.developer.nvidia.com/GPUGems3/gpugems3_ch01.html
vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n )
{
    n = max((abs(n) - .2)*7., .001);
    n /= (n.x + n.y + n.z );  
    p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;
    return p*p;
}

// from shane shaders
// Texture bump mapping. Four tri-planar lookups, or 12 texture lookups in total. I tried to 
// make it as concise as possible. Whether that translates to speed, or not, I couldn't say.
vec3 doBumpMap( sampler2D tx, in vec3 p, in vec3 n, float bf)
{
    const vec2 e = vec2(0.001, 0);
    mat3 m = mat3( tex3D(tx, p - e.xyy, n), tex3D(tx, p - e.yxy, n), tex3D(tx, p - e.yyx, n));
    vec3 g = vec3(0.299, 0.587, 0.114)*m; // Converting to greyscale.
    g = (g - dot(tex3D(tx,  p , n), vec3(0.299, 0.587, 0.114)) )/e.x; g -= n*dot(n, g);
    return normalize( n + g*bf ); // Bumped normal. "bf" - bump factor.
}

// shading func
vec3 shade(vec3 ro, vec3 rd, float d, vec3 ld, float li)
{
	vec3 p = ro + rd * d;		
	vec3 n = nor(p, 0.1);		

    n = doBumpMap(iChannel0, p*.5, n, 0.005);

	vec3 refl = reflect(rd,n);	

	float amb = 0.6; 											
	float diff = clamp( dot( n, ld ), 0.0, 1.0 ); 				
	float fre = pow( clamp( 1. + dot(n,rd),0.0,1.0), 4. ); 		
	float spe = pow(clamp( dot( refl, ld ), 0.0, 1.0 ),16.); 	
	float sss = 1. - SubDensity(p, 1.); 						

    return vec3(
        (diff + fre + spe * 3.) * amb * li,
        (diff + fre + sss) * amb * li + spe * 1.2,
        (diff + fre - spe * 0.2 + sss * .8) * amb * li
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // vars
	vec2 g = fragCoord;
	vec2 si = iResolution.xy;
	vec2 mo = iMouse.xy;
	float time = iTime * 1.2;

	vec3 col = vec3(0);

	vec3 ro = vec3(0,0,time);
	ro.xy -= path(ro.z);

	vec3 lp = ro + vec3(0,2,5);

    vec3 cv = vec3(0,0,ro.z+1.);
    cv.xy -= path(cv.z);

	vec3 cu = normalize(vec3(0,1,0));
  	vec3 z = normalize(cv-ro);
    vec3 x = normalize(cross(cu,z));
  	vec3 y = cross(z,x);

  	vec2 uv = (g+g-si)/si.y;
   	vec3 rd = normalize(z + uv.x*x + uv.y*y);

	float md = 100.;

	float s = 1., d = 0.;
	for (float i=0.; i<80.; i++)
	{
		if (d*d/s > 1e6 || d > md) break;
		s = df(ro+rd*d).x;
		d += s * 0.8;
	}

    vec3 ld = normalize(lp - ro-rd*d);

    vec3 mat = vec3(0.3,0.5,0.8);

    if (d < md)
    {
        vec3 p = ro+rd*d; // surf point

        // sub surface
        for (float i = 0.; i < 1.; i+=0.1)
        {
            float y = shade(p, rd, i, ld, i).y;
            mat = blackbody(1000. + y * 900.).bgr * 0.8;
            col = mix(col, y * mat, i);
        }

        // surface
        float x = shade(ro, rd, d, ld, 3.).x;
        mat = blackbody(1000. + x * 900.).bgr * 0.2;
        col = mix(col, x * mat, 0.7);
    }

	col = mix(col, mat, 1.0-exp(-0.01*d*d));

    col = sqrt(col * col * .8);

	vec2 v = g/si;
    col *= 0.5 + 0.5*pow( 16.0*v.x*v.y*(1.0-v.x)*(1.0-v.y), 0.25 ); // iq vignette
    fragColor = vec4(col, 1.0);
}

// Star Nest by Pablo RomÃ¡n Andrioli

// This content is under the MIT License.

#define iterations 12
#define formuparam 0.57

#define volsteps 10
#define stepsize 0.2

#define zoom   1.200
#define tile   1.0
#define speed  0.010 

#define brightness 0.0015
#define darkmatter 1.00
#define distfading 0.730
#define saturation 1.0

#define mo (2.0 * iMouse.xy - iResolution.xy) / iResolution.y
#define blackholeCenter vec3(time*2.,time,-2.)
#define blackholeRadius 1.2
#define blackholeIntensity 1.0

float iSphere(vec3 ray, vec3 dir, vec3 center, float radius)
{
	vec3 rc = ray-center;
	float c = dot(rc, rc) - (radius*radius);
	float b = dot(dir, rc);
	float d = b*b - c;
	float t = -b - sqrt(abs(d));
	float st = step(0.0, min(t,d));
	return mix(-1.0, t, st);
}

vec3 iPlane(vec3 ro, vec3 rd, vec3 po, vec3 pd){
    float d = dot(po - ro, pd) / dot(rd, pd);
    return d * rd + ro;
}

vec3 r(vec3 v, vec2 r)//incomplete but ultrafast rotation fcn thnx to rodolphito
{
    vec4 t = sin(vec4(r, r + 1.5707963268));
    float g = dot(v.yz, t.yw);
    return vec3(v.x * t.z - g * t.x,
                v.y * t.w - v.z * t.y,
                v.x * t.x + g * t.z);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//get coords and direction
	vec2 uv=fragCoord.xy/iResolution.xy-.5;
	uv.y*=iResolution.y/iResolution.x;
	vec3 dir=vec3(uv*zoom,1.);
	float time=iTime*speed+.25;

	//mouse rotation
	vec3 from=vec3(0.0, 0.0, -15.0);
    from = r(from, mo / 10.0);
    dir = r(dir, mo / 10.0);
	from+=blackholeCenter;

    vec3 nml = normalize(blackholeCenter - from);
    vec3 pos = iPlane(from, dir, blackholeCenter, nml);
    pos = blackholeCenter - pos;
    float intensity = dot(pos, pos);
    if(intensity > blackholeRadius * blackholeRadius){
        intensity = 1.0 / intensity;
    	dir = mix(dir, pos * sqrt(intensity), blackholeIntensity * intensity);

		//volumetric rendering
		float s=0.1,fade=1.;
		vec3 v=vec3(0.);
		for (int r=0; r<volsteps; r++) {
			vec3 p=from+s*dir*.5;
			p = abs(vec3(tile)-mod(p,vec3(tile*2.))); // tiling fold
			float pa,a=pa=0.;
			for (int i=0; i<iterations; i++) { 
				p=abs(p)/dot(p,p)-formuparam; // the magic formula
				a+=abs(length(p)-pa); // absolute sum of average change
				pa=length(p);
			}
			float dm=max(0.,darkmatter-a*a*.001); //dark matter
			a*=a*a; // add contrast
			if (r>6) fade*=1.-dm; // dark matter, don't render near
			//v+=vec3(dm,dm*.5,0.);
			v+=fade;
			v+=vec3(s,s*s,s*s*s*s)*a*brightness*fade; // coloring based on distance
			fade*=distfading; // distance fading
			s+=stepsize;
		}
		v=mix(vec3(length(v)),v,saturation); //color adjust
		fragColor = vec4(v*.01,1.);	
    }
    else fragColor = vec4(0.0);
}

float PI = 3.1415926535;
vec3 col = vec3(1.0, 1.0, 1.0);
int stop = 0;
bool needshadow = true;
vec3 stoppos = vec3(0.0);
bool needlight = true;
float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}
float map(vec3 p){
    float d = length(max(abs(p) - vec3(1.0),0.0));

    vec3 p1 = p;
    float the = iTime / 2.1;
    p1.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    d = smin(d, length(p1 - vec3(1.2,1.2,1.2)) - 0.7 +sin(iTime*3.5*PI)*0.2, 3.0);
    d = smin(d, length(p1 - vec3(-1.2,1.2,1.2)) - 0.7 +sin(iTime*3.5*PI)*0.2, 3.0);
    d = smin(d, length(p1 - vec3(1.2,1.2,-1.2)) - 0.7 +sin(iTime*3.5*PI)*0.2, 3.0);
    d = smin(d, length(p1 - vec3(-1.2,1.2,-1.2)) - 0.7 +sin(iTime*3.5*PI)*0.2, 3.0);
    if(d < 0.01) col= vec3(0.0, 1.0, 1.0);

    vec3 p2 = (fract((p + vec3(iTime * 5.0, 0, 0.0))  * 0.2) - 0.5) / 0.2;
    float d1 = max( abs(length(p) - 36.0) - 0.05, length(p2) - 1.0);
    if(d1 < 0.01){
        needlight = false;
        col = vec3(1.0, 0.0, 0.8);
    }
    d = min(d, d1);

    float plane = dot(p,vec3(0.0, 1.0, 0.0)) + 1.0;
    if(plane < 0.01 && length(p) > 39.0){
    	col = vec3(0.6, 0.4, 0.8) + vec3(0.85, 1.0, 0.5) * max(0.0, (50.0 - length(vec2(80.0) - vec2(abs(p.x), abs(p.z)))) / 45.0);
    }
    d = min(d, plane);

    float pole = length(vec2(p.x, p.z) - vec2(80.0)) - 3.0;
    pole = min(pole, length(vec2(p.x, p.z) - vec2(-80.0)) - 3.0);
    pole = min(pole, length(vec2(p.x, p.z) - vec2(-80.0, 80.0)) - 3.0);
    pole = min(pole, length(vec2(p.x, p.z) - vec2(80.0, -80.0)) - 3.0);

    if(pole < 0.01){
        needlight = false;
        needshadow = false;
    	col = vec3(0.85, 1.0, 0.5);
    }
    d = min(d, pole);

    return d;
}
vec3 grad(vec3 p) {
    vec2 d = vec2(0.0, 0.001);
	float dFx = map(p + d.yxx) - map(p - d.yxx);
	float dFy = map(p + d.xyx) - map(p - d.xyx);
	float dFz = map(p + d.xxy) - map(p - d.xxy);
	return normalize(vec3(dFx, dFy, dFz));
}
float trace(vec3 o, vec3 r){
	float t = 0.0;
    vec3 p;
    for(int i = 0; i < 256; ++i){
    	p = o + r * t;
        float d = map(p);
        if(d < 0.01){
            stop = 1;
        	break;
        }
        t += d * 0.9;
    }
    stoppos = p;
    return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv - vec2(0.0, PI / 5.0), 1.0));

    float the = iTime / PI;
    vec3 o = vec3(0.0,10.0, -10.0);
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    o.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    float t = trace(o, r);
    float light = 1.0;
    int stopp = stop;
    if(stop == 1 && needlight){
    	vec3 nor = grad(stoppos);
        light = 0.6 * max(dot(nor, vec3(1.0,0.8,0.4)), 0.0) + 0.3;
        stop = 0;
        vec3 col1 = col;
        trace(stoppos + nor * 0.02, normalize(vec3(1.0,0.7,0.4)));
        if(stop == 1){
            if(needshadow){
                light = 0.1;
            }
            col = col1;
        }
    }
    if(stopp == 0){
    	col = vec3(0.2, 0.375, 0.75);
    }

    vec3 fc = vec3(light) * col;
    fragColor = vec4(fc,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //	normalized coordinate of current pixel
	vec2 uv = fragCoord / iResolution.y;
    vec2 mnc = iMouse.xy / iResolution.y;

    //	determine smoothing factor
    float blur = 100.0 / iResolution.y;

    //	draw circles
    float d = sin( length( mnc - uv ) * 100.0);
    float thresh = 0.4 + cos(iTime * 101.) * 0.2;
    float thresh2 = thresh + sin(iTime * 100.0) * 0.3;

    float c = 0.0;
    c += smoothstep( thresh+blur,thresh-blur, d );
    c += smoothstep( thresh2+blur,thresh2-blur, d );

	fragColor = vec4( c );
}

// Created by Stephane Cuillerdier - Aiekick/2017 (twitter:@aiekick)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Tuned via XShade (http://www.funparadigm.com/xshade/)

const vec3 ld = vec3(0.,1., .5);

float t = 0.;

vec2 m;

float fullAtan(vec2 p)
{
    return step(0.0,-p.x)*3.1415926535 + sign(p.x) * atan(p.x, sign(p.x) * p.y);
}

float fractus(vec2 p, vec2 v)
{
	vec2 z = p;
    vec2 c = v;
	float k = 1., h = 1.0;    
    float t = (sin(iTime * .5)*.5+.5) * 5.;
    for (float i=0.;i<5.;i++)
    {
        if (i > t) break;
        h *= 4.*k;
		k = dot(z,z);
        if(k > 4.) break;
		z = vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y) + c;
    }
    float d = sqrt(k/h)*log(k);

    // next iteration
    if (k > 4.)
    	z = vec2(z.x * z.x - z.y * z.y, 2. * z.x * z.y) + c;
    h *= 4.*k;
    k = dot(z,z);
    float d1 = sqrt(k/h)*log(k);

    // df blending
	return mix(d, d1, fract(t));
}

vec4 dfFractus(vec3 p)
{
	float a = fullAtan(p.xz); // axis y

    vec2 c;
    c.x = mix(0.2, -0.5, sin(a * 2.));
    c.y = mix(0.5, 0.0, sin(a * 3.));

    float path = length(p.xz) - 3.;

    vec2 rev = vec2(path, p.y);
    float aa = a + iTime;
    rev *= mat2(cos(aa),-sin(aa),sin(aa),cos(aa)); // rot near axis y

	return vec4(fractus(rev, c) - 0.0, rev, 0);
}

vec4 df(vec3 p)
{
	return dfFractus(p); 
}

vec3 nor( vec3 p, float prec )
{
    vec2 e = vec2( prec, 0. );
    vec3 n = vec3(
		df(p+e.xyy).x - df(p-e.xyy).x,
		df(p+e.yxy).x - df(p-e.yxy).x,
		df(p+e.yyx).x - df(p-e.yyx).x );
    return normalize(n);
}

// from iq code
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<1; i++ )
    {
		float h = df( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += h*.25;
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0., 1. );
}

// from iq code
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<10; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = df( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

