// Grab all sky information for a given ray from camera
// from Dave Hoskins // https://www.shadertoy.com/view/Xsf3zX
vec3 GetSky(in vec3 rd, in vec3 sunDir, in vec3 sunCol)
{
	float sunAmount = max( dot( rd, sunDir), 0.0 );
	float v = pow(1.0-max(rd.y,0.0),6.);
	vec3  sky = mix(vec3(.1, .2, .3), vec3(.32, .32, .32), v);
	sky = sky + sunCol * sunAmount * sunAmount * .25;
	sky = sky + sunCol * min(pow(sunAmount, 800.0)*1.5, .3);
	return clamp(sky, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    m = iMouse.xy/iResolution.xy;
    if (m.x == 0.) m.x = .1;

	fragColor = vec4(1);

	vec2 g = fragCoord.xy;
	vec2 si = iResolution.xy;
	vec2 uv = (2.*g-si)/min(si.x, si.y);

    float a = 1.57;
	vec3 rayOrg = vec3(cos(a),1.5,sin(a)) * 5.;
	vec3 camUp = vec3(0,1,0);
	vec3 camOrg = vec3(0,-1.5,0);

	float fov = .5;
	vec3 axisZ = normalize(camOrg - rayOrg);
	vec3 axisX = normalize(cross(camUp, axisZ));
	vec3 axisY = normalize(cross(axisZ, axisX));
	vec3 rayDir = normalize(axisZ + fov * uv.x * axisX + fov * uv.y * axisY);

	float s = 0.;
    float d = 0.;
	float dMax = 20.;
	float count = 0.;
	for (float i=0.; i<500.; i++)
	{
		if (d*d/s>1e6 || d>dMax) break;
        s = df(rayOrg + rayDir * d).x;
		d += s * 0.2;
		count+=1.;
	}

    vec3 sky = GetSky(rayDir, ld, vec3(1.5));

   	/*vec4 mat = df(rayOrg + rayDir * d);
   	vec3 tex = texture(iChannel0, mat.yz * 0.8).rgb;
    d += dot(tex, vec3(0.05));*/

	if (d<dMax)
	{

		vec3 p = rayOrg + rayDir * d;
        vec3 n = nor(p, 0.001);
		vec4 mat = df(p);

        vec3 tex = texture(iChannel0, mat.yz).rgb; // wood

		// iq lighting
		float occ = calcAO( p, n );
        float amb = clamp( 0.5+0.5*n.y, 0.0, 1.0 );
        float dif = clamp( dot( n, ld ), 0.0, 1.0 ) * (df(p+n*1.16).x);
        float spe = pow(clamp( dot( rayDir, ld ), 0.0, 1.0 ),16.0);
        float sss = df(p - n*0.001).x/0.01;

        dif *= softshadow( p, ld, 0.1, 2. );

        vec3 brdf = vec3(-0.5) * tex;
        brdf += 0.5*dif*vec3(1.00,0.90,0.60);
        brdf += 0.4*spe*vec3(1.00,0.90,0.60)*dif;
        brdf += 0.3*amb*vec3(0.50,0.70,1.00)*occ;
        brdf += 0.5*(1.-sss)*vec3(0.2,0.7,0.2);
        fragColor.rgb *= brdf;

        fragColor.rgb = mix( fragColor.rgb, sky, 1.0-exp( -0.01*d*d*count/150. ) ); 
	}
	else
	{
		fragColor.rgb = sky;
	}

    fragColor.rgb = sqrt(fragColor.rgb * fragColor.rgb * 0.8);
}

// Frustum CSG Tree Intersection
// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)

// note that this version is verbose and purposefully lacks 
// any form of optimization for the sake of education.

// the general principle here is that we first evaluate the near and
// far boundaries of the brushes that cross our line of sight, 
// so we end up with a list of 1D intervals for each brush.
//
// then we can perform CSG in rayspace and raymarch towards the
// nearest resulting visible surface by repeatedly evaluating
// a distance estimator that gives us the distance to the 
// nearest horizon.
//
// the distance estimator evaluates the CSG tree to receive 
// a lower bound on the distance to the edge closest to 
// the right of a given 1D scalar t.
//
// the CSG tree evaluation is sped up by transforming the tree
// to a topologically sorted array of nodes (here done manually) 
// that can be evaluated sequentially.
//
// the CSG tree has four kinds of operands:
// SET X:brush (copy)
// OR A:op B:op (boolean union)
// AND A:op B:op (boolean intersection)
// ANDN A:op B:op (boolean subtraction)
//
// the complexity is O(n) for n union-only instructions,
// but can reach O(n*n) in the worst case; the performance in
// practice however is typically around O(6n). Through hierarchical
// culling one can aggressively reduce the number of instructions
// required to solve a tile:
//
// it is possible, not demonstrated here, to source the brush 
// intervals from a primitive-frustum intersection instead, and
// to cull/rewrite instructions that yield no result, allowing
// for hierarchical quadtree-style raymarching using
// per-tile linked lists for tiles that contain more than one 
// instruction.

// set to 1 to show CSG operator debug setup
#define DEBUG_CSG_OPS 0
#define DEBUG_CSG_OP CSG_OR
#define TEST_SETUP 0

vec2 ray_origin0;
vec2 ray_origin1;
const vec2 ray_dir = vec2(1.0,0.0);

const int op_set = 0;
const int op_or = 1;
const int op_and = 2;
const int op_and_not = 3;

const int empty = -1;
const int solid = -2;

const float infinity = 1.0/0.0;
const vec2 empty_interval = vec2(infinity);

struct Edge {
    // position of next feature
    // if positive, area in front of / behind gray zone is empty
    // if negative, area is solid
    float t;
    // if true, gray zone is right of cursor
    // otherwise cursor is in gray zone
	bool r;
    // +t r=true : _______|????????
    // -t r=true : #######|????????
    // +t r=false: ???????|________
    // -t r=false: ???????|########

};

// these brushes are usually imported using a SSBO
// here we provide a small local array
#if DEBUG_CSG_OPS
#define NUM_BRUSHES 2
#elif TEST_SETUP
#define NUM_BRUSHES 3
#else
#define NUM_BRUSHES 7
#endif
vec3 brushes[NUM_BRUSHES];
// vec2: fuzzy interval, vec2: solid interval
vec4 brush_intervals[NUM_BRUSHES];

vec2 iCircle(vec2 o_ro, vec2 rd, vec3 params) {
	vec2 ro = o_ro - params.xy;
	float k = dot(ro, rd);
    float q = sqrt(k*k - dot(ro, ro) + params.z*params.z);
    vec2 d = (vec2(-q,q) - k);
    return (d.x != d.x)?vec2(infinity,-infinity):d;
}

float dCircle(vec2 p, vec3 params) {
    return length(p - params.xy) - params.z;
}

// dynamic array index hack
vec3 get_brush(int brushid) {
    for (int i = 0; i < NUM_BRUSHES; ++i) {
        if (i == brushid)
            return brushes[i];
    }
    return vec3(0.0);
}

// dynamic array index hack
vec4 get_brush_interval(int brushid) {
    for (int i = 0; i < NUM_BRUSHES; ++i) {
        if (i == brushid)
            return brush_intervals[i];
    }
    return vec4(empty_interval, empty_interval);
}


#if DEBUG_CSG_OPS
#define NUM_CSG_OPS 3
#elif TEST_SETUP
#define NUM_CSG_OPS 5
#else
#define NUM_CSG_OPS 13
#endif
#define CSG_OP ivec3
// opcode, src1, src2
// src1 must be node index, empty, or brush
// src2 must be node index, empty, or brush
// in a realworld example, this would be a SSBO
CSG_OP csg_ops[NUM_CSG_OPS];
void setup_globals(float t) {
#if DEBUG_CSG_OPS
    brushes[0] = vec3(0.5,0.0,0.08);
    brushes[1] = vec3(mix(0.26,0.74,sin(t)*0.5+0.5),0.0,0.1);
