    brushes[4] = vec3(c.x + cos(a1)*r1, c.y + sin(a1)*r1,0.1);
    brushes[5] = vec3(c.x - cos(a1)*r1, c.y - sin(a1)*r1,0.1);
#endif

// dynamic array indexing hack just so we can squeeze out a little convenience
#define CSG_INSTR(I, OP, A, B) csg_ops[I] = CSG_OP(OP, A, B);
#define CSG_SET(I, A) CSG_INSTR(I, op_set, A, A)
#define CSG_OR(I, A, B) CSG_INSTR(I, op_or, A, B)
#define CSG_AND(I, A, B) CSG_INSTR(I, op_and, A, B)
#define CSG_ANDN(I, A, B) CSG_INSTR(I, op_and_not, A, B)
#define BRUSH(X) X
#define OP(X) X
#if DEBUG_CSG_OPS
    CSG_SET(0,BRUSH(0));
    CSG_SET(1,BRUSH(1));
    DEBUG_CSG_OP(2,OP(0),OP(1));
#elif TEST_SETUP
    CSG_SET (0,BRUSH(0));
    CSG_SET (1,BRUSH(1));
    CSG_OR  (2,OP(0), OP(1));
    CSG_SET (3,BRUSH(2));
    CSG_AND (4,OP(2), OP(3));
#else
    CSG_SET	(0,BRUSH(2));
    CSG_SET	(1,BRUSH(3));
    CSG_AND	(2,OP(0), OP(1));

    CSG_SET	(3,BRUSH(4));
    CSG_SET	(4,BRUSH(5));
    CSG_AND	(5,OP(3), OP(4));

    CSG_OR	(6,OP(2),	OP(5));

    CSG_SET (7, BRUSH(0));
    CSG_SET (8, BRUSH(1));
    CSG_ANDN(9, OP(7), OP(8));
    CSG_OR  (10,OP(6), OP(9));
    CSG_SET (11,BRUSH(6));
    CSG_ANDN(12,OP(10), OP(11));
#endif
#undef CSG_INSTR
#undef CSG_OR
#undef CSG_AND
#undef CSG_AND_NOT
#undef BRUSH
#undef OP

    // precalculate brush intervals
    for (int i = 0; i < NUM_BRUSHES; ++i) {
        vec2 d0 = iCircle(ray_origin0, ray_dir, brushes[i]);
        vec2 d1 = iCircle(ray_origin1, ray_dir, brushes[i]);
        vec2 p0 = brushes[i].xy - vec2(brushes[i].z,0.0);
        vec2 p1 = brushes[i].xy + vec2(brushes[i].z,0.0);

        // outer interval
        vec2 od = vec2(min(d0.x,d1.x),max(d0.y,d1.y));
        if (od.x > od.y) {
            od = empty_interval;
        }
        // inner interval
        vec2 id = vec2(max(d0.x,d1.x),min(d0.y,d1.y));
        if (id.x > id.y) {
            id = empty_interval;
        }
        #if 1
        if ((brushes[i].y > ray_origin0.y) && (brushes[i].y < ray_origin1.y)) {
            od.x = min(od.x, p0.x);
            od.y = max(od.y, p1.x);
        }    
        #endif
        brush_intervals[i] = vec4(od, id);
    }
}

// temporary space for intermediate computations
// in a realworld example, these would be in SSBOs
float temp1[NUM_CSG_OPS];
Edge temp2[NUM_CSG_OPS];

CSG_OP get_op(int idx) {
    for (int i = 0; i < NUM_CSG_OPS; ++i) {
        if (i == idx)
            return csg_ops[i];
    }    
    return CSG_OP(0);
}

// dynamic array index hack
float get_temp1(int idx) {
    for (int i = 0; i < NUM_CSG_OPS; ++i) {
        if (i == idx)
            return temp1[i];
    }    
    return infinity;
}

// dynamic array index hack
Edge get_temp2(int idx) {
    for (int i = 0; i < NUM_CSG_OPS; ++i) {
        if (i == idx)
            return temp2[i];
    }    
    return Edge(infinity, true);
}

Edge interval_edge(vec4 range, float t) {
    if (range.x > t) {
        return Edge(range.x, true);
	} else if (range.y > t) {
        if (range.z != infinity) {
            if (range.z > t) {
                return Edge(-range.z, false);
            } else if (range.w > t) {
                return Edge(-range.w, true);
            }
        }
        return Edge(range.y, false);
    } else {
        return Edge(infinity, true);
    }
}

Edge csg_not(Edge x) {
    return Edge(-x.t, x.r);
}

Edge csg_or(Edge a, Edge b) {
    float ct = ((a.r?a.t:abs(a.t)) < (b.r?b.t:abs(b.t)))?a.t:b.t;
    bool cr = (a.r && b.r) || (a.r && (sign(a.t) < 0.0)) || (b.r && (sign(b.t) < 0.0));
    return Edge(ct, cr);    
}
Edge csg_and(Edge a, Edge b) {
#if 0
    return csg_not(csg_or(csg_not(a),csg_not(b)));
#else
    float ct = ((a.r?a.t:-abs(a.t)) > (b.r?b.t:-abs(b.t)))?a.t:b.t;
    bool cr = (a.r && b.r) || (a.r && (sign(a.t) > 0.0)) || (b.r && (sign(b.t) > 0.0));
    return Edge(ct, cr);
#endif
}
Edge csg_and_not(Edge a, Edge b) {
    return csg_and(a, csg_not(b));
}

Edge scene_next_feature(float t) {
    Edge d;
    for (int i = 0; i < NUM_CSG_OPS; ++i) {
        CSG_OP op = get_op(i);
        int opcode = op[0];
        if (opcode == op_set) {
            d = interval_edge(get_brush_interval(op[1]), t);
        } else {
            Edge a = get_temp2(op[1]);
            Edge b = get_temp2(op[2]);
            if (opcode == op_or) {
                d = csg_or(a, b);
            } else if (opcode == op_and) {
                d = csg_and(a, b);
            } else if (opcode == op_and_not) {
                d = csg_and_not(a, b);
            } else {
                d = Edge(infinity, true);
            }
        }
        temp2[i] = d;
    }
    return d;
}

