// Simplex Toolkit
// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)


struct simplex2 {
    vec2 v[3];
};

struct quad2 {
    vec2 v[4];
};

struct simplex3 {
    vec3 v[4];
};

void simplex2_transform(in simplex2 src, in mat3 mtx, out simplex2 dest) {
    for (int i = 0; i < 3; ++i) {
    	dest.v[i] = (vec3(src.v[i],1.0) * mtx).xy;
	}
}

void simplex3_transform(in simplex3 src, in mat4 mtx, out simplex3 dest) {
    for (int i = 0; i < 4; ++i) {
    	dest.v[i] = (vec4(src.v[i],1.0) * mtx).xyz;
	}
}

// d0: dot((p0,1), plane)
// d1: dot((p1,1), plane)
vec3 intersect_plane_edge(vec4 plane, float d0, float d1, vec3 p0, vec3 p1) {
    float D = d1 - d0;
    float t = ((D == 0.0)?0.0:(-d0/D));
    return p0 * (1.0 - t) + p1 * t;
}

#define INTERSECT1(I,A,B) p[I] = intersect_plane_edge(plane, d[A], d[B], src.v[A], src.v[B])
#define INTERSECT3(R,A,B,C) INTERSECT1(0,R,A); INTERSECT1(1,R,B); INTERSECT1(2,R,C); return 3;
#define INTERSECT4(L1,L2,R1,R2) INTERSECT1(0,L1,R1); INTERSECT1(1,L1,R2); INTERSECT1(2,L2,R2); INTERSECT1(3,L2,R1); return 4;

// slice a 3-simplex by a plane to return 0, 3 or 4 points describing
// the resulting polytope embedded in 3-space.
// with support for dynamic indexing, one can store the index map in a table
int simplex3_slice(in simplex3 src, vec4 plane, out vec3 p[4]) {
    float d[4];
    for (int i = 0; i < 4; ++i) {
        d[i] = dot(vec4(src.v[i],1.0), plane);
    }
    int idx = 
          ((sign(d[0]) < 0.0)?0:1)
        + ((sign(d[1]) < 0.0)?0:2)
        + ((sign(d[2]) < 0.0)?0:4)
        + ((sign(d[3]) < 0.0)?0:8);
    idx = (idx > 7)?(15 - idx):idx;
    if (idx == 1) {
        // 3 2 1| 0
        INTERSECT3(0,1,2,3);
    } else if (idx == 2) {
        // 3 2 0 | 1
        INTERSECT3(1,0,2,3);
    } else if (idx == 3) {
        // 3 2 | 1 0
        INTERSECT4(0,1,2,3);
    } else if (idx == 4) {
        // 3 1 0 | 2
        INTERSECT3(2,0,1,3);
    } else if (idx == 5) {
        // 3 1 | 2 0
        INTERSECT4(0,2,1,3);
    } else if (idx == 6) {
        // 2 1 | 3 0
        INTERSECT4(0,3,1,2);
    } else if (idx == 7) {
        // 2 1 0 | 3
        INTERSECT3(3,0,1,2);
    } else {
        // | 3 2 1 0
        return 0;        
    }    
}

#define INTERSECT2(R,A,B) INTERSECT1(0,R,A); INTERSECT1(1,R,B); return 2;

// d0: dot((p0,1), plane)
// d1: dot((p1,1), plane)
vec2 intersect_plane_edge(vec3 plane, float d0, float d1, vec2 p0, vec2 p1) {
    float D = d1 - d0;
    float t = ((D == 0.0)?0.0:(-d0/D));
    return p0 * (1.0 - t) + p1 * t;
}

// slice a 2-simplex by a 2-plane to return 0 or 2 points describing
// the resulting line.
// with support for dynamic indexing, one can store the index map in a table
int simplex2_slice(in simplex2 src, vec3 plane, out vec2 p[2]) {
    float d[3];
    for (int i = 0; i < 3; ++i) {
        d[i] = dot(vec3(src.v[i],1.0), plane);
    }
    int idx = 
          ((sign(d[0]) < 0.0)?0:1)
        + ((sign(d[1]) < 0.0)?0:2)
        + ((sign(d[2]) < 0.0)?0:4);
    idx = (idx > 3)?(7 - idx):idx;
    if (idx == 1) {
        // 2 1 | 0
        INTERSECT2(0,1,2);
    } else if (idx == 2) {
        // 2 0 | 1
        INTERSECT2(1,0,2);
    } else if (idx == 3) {
        // 1 0 | 2
        INTERSECT2(2,0,1);
    } else {
        // | 2 1 0
        return 0;       
    }
}

#define INTERSECT22(A,B,C,D) INTERSECT1(0,A,B); INTERSECT1(1,C,D); return 2;

// slice a quad by a 2-plane to return 0 or 2 points describing
// the resulting line.
// with support for dynamic indexing, one can store the index map in a table
int quad2_slice(in quad2 src, vec3 plane, out vec2 p[2]) {
    float d[4];
    for (int i = 0; i < 4; ++i) {
        d[i] = dot(vec3(src.v[i],1.0), plane);
    }
    int idx = 
          ((sign(d[0]) < 0.0)?0:1)
        + ((sign(d[1]) < 0.0)?0:2)
        + ((sign(d[2]) < 0.0)?0:4)
        + ((sign(d[3]) < 0.0)?0:8);
    idx = (idx > 7)?(15 - idx):idx;
    if (idx == 1) {
        // 3 2 1 | 0
        INTERSECT2(0,1,3);
    } else if (idx == 2) {
        // 3 2 0 | 1
        INTERSECT2(1,0,2);
    } else if (idx == 3) {
        // 3 2 | 1 0
        INTERSECT22(0,3,1,2);
    } else if (idx == 4) {
        // 3 1 0 | 2
        INTERSECT2(2,3,1);
    } else if (idx == 5) {
        // 3 1 | 2 0
        // impossible with convex quad
        return 0;
    } else if (idx == 6) {
        // 2 1 | 3 0
        INTERSECT22(0,1,2,3);
    } else if (idx == 7) {
        // 2 1 0 | 3
        INTERSECT2(3,0,2);
    } else {
        // | 3 2 1 0
        return 0;        
    }
}

#undef INTERSECT1
#undef INTERSECT2
#undef INTERSECT22
#undef INTERSECT3
#undef INTERSECT4

