// construct a quaternion from an axis and angle
vec4 versor(vec3 w, float a) {
    a *= 0.5;
    return vec4(w * sin(a), cos(a));
}

mat4 viewmat;

// rotation matrix constructor from versor
mat3 rotation (vec4 q) {
    float n = dot(q,q);
    vec4 qs = (n == 0.0)?vec4(0.0):(q * (2.0 / n));
    vec3 w = qs.w * q.xyz;
    vec3 x = qs.x * q.xyz;
    vec3 y = qs.y * q.xyz;
    float zz = qs.z * q.z;
    return mat3(
        1.0 - (y.y + zz), x.y + w.z, x.z - w.y,
        x.y - w.z, 1.0 - (x.x + zz), y.z + w.x,
        x.z + w.y, y.z - w.x, 1.0 - (x.x + y.y));
}

vec2 tf(vec3 p) {
    p.xyz = p.yzx;
    p = (vec4(p,1.0) * viewmat).xyz;
    p.z += 2.0;
    vec2 uv = p.xy / p.z;
    return uv * 2.0;
}

void paint() {
    float t = iTime;

    viewmat = mat4(rotation(versor(vec3(1.0,0.0,0.0),0.25)));

    float a = t*0.5;
    float c = cos(a);
