    line_to(tf(obj3.v[2]));
    stroke();
    set_source_rgb(1.0,0.0,0.0);
    move_to(tf(obj3.v[0]));
    line_to(tf(obj3.v[1]));
    stroke();
    set_source_rgb(0.0,1.0,0.0);
    move_to(tf(obj3.v[0]));
    line_to(tf(obj3.v[2]));
