#define EPS 0.001
vec2 rot(in vec2 pos, float angle) {
	float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c) * pos;
}

float plane(in vec3 p) {
	return p.y;
}

float sph(in vec3 p, float rad) {
    p -= vec3(-4.0, 2.3, 8.0);
    p.xy = rot(p.xy, iTime);
    p.xz = rot(p.xz, iTime);
	return length(p) - rad;
}

float box(in vec3 p,in vec3 dim) {
    p -= vec3(-3.5, 1.3, -2.0);
    p.yz = rot(p.yz, iTime);
    p.xy = rot(p.xy, iTime);
	float d3 = length(max(abs(p)-dim, 0.0)) ;
    return d3;
}

float torus(in vec3 pos, in vec2 t) {
    pos -= vec3(3.0, 1.0, 0.3);
    pos.yz = rot(pos.yz, 0.1*iTime);
    pos.xy = rot(pos.xy, 0.5*iTime);
	vec2 q = vec2(length(pos.xz) - t.x, pos.y);
    return length(q) - t.y;
}

float map(in vec3 pos) {
   float d1 = min(plane(pos), sph(pos, 2.0)); 
   float du1 = min(d1,  box(pos, vec3(0.5)));
   float du2 = min(du1,torus(pos, vec2(1.0, 0.5)));
   return du2;
}

vec3 material(in vec3 p) {
	p *= 0.6;
    float f = smoothstep(0.5, 0.51, fract(p.x - sin(p.z) + cos(p.z)));
    return f*vec3(1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv = 2.0*uv -1.0;
    uv.x *= iResolution.x / iResolution.y;
	vec3 ro = vec3(0.0 + 0.8*sin(iTime), 3.0 + 0.5*cos(iTime), -6.0);
	vec3 rd = normalize(vec3(uv, 1.0));
	vec3 col = vec3(0.0);
    float t = 0.0;

	for(int i=0; i<100; i++){
        vec3 pos = ro + t*rd;
		float d = map(pos);
		if( d < EPS ) {
            float ligthD = sph(pos, 1.0);
            float ligthT = torus(pos, vec2(1.5, 0.8));
            float ligthB = box(pos, vec3(0.9));
			col  = 2.4 / (ligthD*ligthD)*vec3(1.6,0.7,0.0)*material(pos);
            col += 0.4 / (ligthT*ligthT)*vec3(1.6,0.0,1.0)*material(pos);
            col += 0.4 / (ligthB*ligthB)*vec3(1.0)*material(pos);
			break;
		}
		ro += d*rd;
	}

	fragColor = vec4(col,1.0);
}

// HEY! Mousedown to change area type

// Constants
#define PI 3.141592654
#define TWO_PI 6.28318530718

// Parameters
#define STRIPE_COLOR1 (vec4(194, 216, 68, 255)/255.)
#define STRIPE_COLOR2 (vec4(194, 216, 68, 0)/255.)
#define STRIPE_WIDTH 50.0
#define STRIPE_ANGLE -45.0
#define BACKGROUND_COLOR (vec4(235, 232, 222, 255)/255.)
#define CIRCLE_RADIUS 150.0
#define SHADOW_COLOR (vec4(0,0, 0, 100)/255.)
#define SHADOW_DEPTH 7.0
#define SHADOW_SIZE 15.0
#define SHADOW_SCALE 2.0
#define GRID_LINE_WIDTH 1.5
#define GRID_COLOR (vec4(255.,255.,255.,255.)/255.)
#define GRID_COLOR2 (vec4(0.,0.,0.,9.)/255.)
#define GRID_X_SIZE 150.0
#define GRID_Y_SIZE 150.0
#define EXCLUSION_COLOR (vec4(.4,.4,.4,1.0))

// Calculations
#define _STRIPE_SCALE (1.0/STRIPE_WIDTH)
#define _HALF_SHADOW_SIZE (SHADOW_SIZE/2.0)

vec4 blend(in vec4 under, in vec4 over) {
    float outA = over.a + under.a * (1.0 - over.a);
    vec3 outCol = vec3(0.0);
    if (outA > 0.0) {
    	outCol = ( over.rgb*over.a + under.rgb*under.a*(1.0-over.a) ) / outA;
    }
    return vec4(outCol,outA);
}

vec4 stripes( in vec2 fragCoord, in bool stripey, float exclusionAmt)
{
    vec2 center = iResolution.xy / 2.0;

    bool inCircle = 
        pow(fragCoord.x - center.x, 2.) + 
        pow(fragCoord.y - center.y, 2.) <
        CIRCLE_RADIUS * CIRCLE_RADIUS;

    if (!stripey) {
        vec4 col = blend(STRIPE_COLOR1, vec4(EXCLUSION_COLOR.rgb,exclusionAmt));
        return inCircle ? col : vec4(col.rgb, 0.0);
    }

    float angle = (STRIPE_ANGLE) / 360.0 * TWO_PI;

    float sx = sin(angle) * (fragCoord.x - iMouse.x);
    float sy = cos(angle) * (fragCoord.y - iMouse.y);

    float amt = sin((sx + sy) * TWO_PI * _STRIPE_SCALE);

    vec4 color = amt > 0.0 ? vec4(STRIPE_COLOR1) : vec4(STRIPE_COLOR2);

    return vec4(color.rgb, inCircle ? color.a : 0.0);
}

float smoothbump(float min, float max, float slope, float val) {
    return smoothstep(min,min+slope,val) - 
        smoothstep(max-slope, max, val);
}

vec4 grid(in vec2 fragCoord) {
    float angle = (STRIPE_ANGLE + iTime*3.6) / 360.0 * TWO_PI;

    vec2 s1 = vec2(
        sin(angle) * (fragCoord.x - iMouse.x),
    	cos(angle) * (fragCoord.y - iMouse.y)
	);
    vec2 s2 = vec2(
        sin(angle + PI/2.0) * (fragCoord.x - iMouse.x),
    	cos(angle + PI/2.0) * (fragCoord.y - iMouse.y)
	);

    float sizeMod = (sin(iTime*0.15)*0.5+0.5) * 50.0;

    float amt = min(1.0,
        smoothbump(0.0, GRID_LINE_WIDTH + 1.0, 1.0, mod(s1.x + s1.y, GRID_X_SIZE + sizeMod) ) +
        smoothbump(0.0, GRID_LINE_WIDTH + 1.0, 1.0, mod(s2.x + s2.y, GRID_Y_SIZE + sizeMod) )
	);

    return vec4(GRID_COLOR.rgb, amt * GRID_COLOR.a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    bool stripey = iMouse.z > 0.0;
    float exclusionAmt = (!stripey ? 1.0 : 0.0) * (1.0 - smoothstep(iResolution.x*0.4 + 0.,iResolution.x*0.4 + 1.,fragCoord.x - fragCoord.y*0.1));

    vec4 grid = grid(fragCoord);

	fragColor  = stripes(fragCoord + vec2(-.25,-.25), stripey, exclusionAmt);
    fragColor += stripes(fragCoord + vec2(-.25, .25), stripey, exclusionAmt);
    fragColor += stripes(fragCoord + vec2( .25,-.25), stripey, exclusionAmt);
    fragColor += stripes(fragCoord + vec2( .25, .25), stripey, exclusionAmt);
    fragColor /= 4.0;

    float grid2Amt = GRID_COLOR2.a * grid.a;

    fragColor = vec4( GRID_COLOR2.rgb * grid2Amt + fragColor.rgb * (1. - grid2Amt),
                     fragColor.a);

    if (!stripey) {

        // outline
        float minOutlineSample = 999999.;

        minOutlineSample = min(minOutlineSample, stripes(fragCoord + vec2( 2.1,-2.1), stripey, 0.0).a);
        minOutlineSample = min(minOutlineSample, stripes(fragCoord + vec2(-2.1,-2.1), stripey, 0.0).a);
        minOutlineSample = min(minOutlineSample, stripes(fragCoord + vec2( 2.1, 2.1), stripey, 0.0).a);
        minOutlineSample = min(minOutlineSample, stripes(fragCoord + vec2(-2.1, 2.1), stripey, 0.0).a);
        minOutlineSample = min(minOutlineSample, stripes(fragCoord + vec2( 0.0,-3.0), stripey, 0.0).a);
        minOutlineSample = min(minOutlineSample, stripes(fragCoord + vec2( 0.0, 3.0), stripey, 0.0).a);
        minOutlineSample = min(minOutlineSample, stripes(fragCoord + vec2( 3.0, 0.0), stripey, 0.0).a);
        minOutlineSample = min(minOutlineSample, stripes(fragCoord + vec2(-3.0, 0.0), stripey, 0.0).a);

        float outlineAmt = (1.0 - minOutlineSample);

        fragColor = vec4(
            outlineAmt * vec3(1.0,1.0,1.0) + (1.-outlineAmt) * fragColor.rgb,
            fragColor.a);

        // shadow
        float totalSamples = 0.0;
        float shadowAmt = 0.0;
        for (float x=0.;x < SHADOW_SIZE; x+=1.) {
            for (float y=0.;y < SHADOW_SIZE; y+=1.) {
                // this is my attempt at a circular (lens) blur 
                float sampleAmt = length(SHADOW_SCALE*(vec2(x,y) - _HALF_SHADOW_SIZE));
                sampleAmt = 1.0 - step(SHADOW_SCALE*_HALF_SHADOW_SIZE, sampleAmt);

                shadowAmt += stripes(
                    fragCoord + vec2(
                        SHADOW_SCALE*(x - _HALF_SHADOW_SIZE),
                        SHADOW_SCALE*(y - _HALF_SHADOW_SIZE) + SHADOW_DEPTH
                    ),
                    stripey, 0.0
                ).a * sampleAmt;
                totalSamples += sampleAmt;
            }
        }
        shadowAmt /= totalSamples;

    	vec4 shadow = vec4(SHADOW_COLOR.rgb, SHADOW_COLOR.a * shadowAmt);
    	fragColor = blend(shadow, fragColor);
    }

    // grid
    fragColor = blend(grid, fragColor);

    fragColor = blend(BACKGROUND_COLOR, fragColor);

}

// "Flexibility" by dr2 - 2017
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float PrCylDf (vec3 p, float r, float h);
float PrRCylDf (vec3 p, float r, float rt, float h);
float PrTorusDf (vec3 p, float ri, float rc);
vec2 Rot2D (vec2 q, float a);
float SmoothBump (float lo, float hi, float w, float x);

vec3 ltDir;
vec2 arcLim, dpLoop, qBlk;
float tCur, dstFar, rLoop, aLoop, rBase, rTop, rIn;
int idObj;
const float pi = 3.14159;

float ObjDf (vec3 p)
{
  vec3 q, qq;
  float dMin, d, rVar, cExt, s, r;
  cExt = 0.3;
  rVar = 0.02;
  p.y -= 8. * cExt;
  dMin = dstFar;
  qq = p;
  qq.xz = Rot2D (p.xz, 2. * pi * (floor (3. * atan (p.z, - p.x) / (2. * pi) + 0.5) / 3.));
  qq.x -= - rBase;
  q = qq;  q.x -= rLoop;
  q.xy = Rot2D (q.xy, aLoop);
  s = mod (30. * atan (q.y, - q.x) / (pi * aLoop), 1.);
  d = PrTorusDf (q, rIn + rVar * SmoothBump (0.25, 0.75, 0.1, s), rLoop);
  d = max (d, dot (vec2 (q.x, abs (q.y)), arcLim));
  if (d < dMin) { dMin = d;  idObj = 4; }
  q = qq;  q.x -= rLoop;  q.xy -= dpLoop;
  q.xy = Rot2D (q.xy, aLoop);
  s = mod (30. * atan (q.y, q.x) / (pi * aLoop), 1.);
  d = PrTorusDf (q, rIn + rVar * SmoothBump (0.25, 0.75, 0.1, s), rLoop);
  d = max (d, dot (vec2 (- q.x, abs (q.y)), arcLim));
  if (d < dMin) { dMin = d;  idObj = 4; }
  q = qq;  q.y -= -4. * cExt;
  d = PrRCylDf (q.xzy, rIn + rVar, 0.05, 4. * cExt + 0.05);
  if (d < dMin) { dMin = d;  idObj = 4; }
  q = qq;  q.xy -= vec2 (2. * rLoop, cExt) + dpLoop;
  d = PrRCylDf (q.xzy, rIn + rVar, 0.05, cExt + 0.05);
  if (d < dMin) { dMin = d;  idObj = 4; }
  q = p;  q.y -= dpLoop.y + 2. * cExt + 0.5 * rIn + 3. * rVar;
  q.xz = Rot2D (q.xz, - pi / 18.);
  r = rBase - 2. * rLoop - dpLoop.x;
  s = mod (9. * atan (q.x, - q.z) / (2. * pi), 1.);
  d = PrTorusDf (q.xzy, 0.5 * rIn + 2. * rVar *
     SmoothBump (0.5 - 0.15 / r, 0.5 + 0.15 / r, 0.01, s), r);
  if (d < dMin) { dMin = d;  idObj = 2; }
  q = p;  q.y -= -4. * cExt;
  q.y = abs (q.y) - cExt;
  d = PrTorusDf (q.xzy, 0.35 * rIn, rBase);
  if (d < dMin) { dMin = d;  idObj = 2; }
  q = p;  q.y -= -8. * cExt - 0.05 - 0.01;
  d = PrCylDf (q.xzy, rBase + 0.3, 0.01);
  if (d < dMin) { dMin = d;  idObj = 1; }
  q = p;  q.y -= dpLoop.y + 2. * cExt + 0.5 * rIn + 3. * rVar;
  d = PrCylDf (q.xzy, rTop, 0.02);
  if (d < dMin) { dMin = d;  idObj = 3; }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.002, -0.002, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

void BlkHit (vec3 ro, vec3 rd)
{
  vec3 v, tm, tp, u, qnBlk;
  float dn, df, bSize;
  bSize = 10. * dstFar;
  if (rd.x == 0.) rd.x = 0.001;
  if (rd.y == 0.) rd.y = 0.001;
  if (rd.z == 0.) rd.z = 0.001;
  v = ro / rd;
  tp = bSize / abs (rd) - v;
  tm = - tp - 2. * v;
  dn = max (max (tm.x, tm.y), tm.z);
  df = min (min (tp.x, tp.y), tp.z);
  if (df > 0. && dn < df) {
    qnBlk = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
    u = (v + dn) * rd;
    qBlk = vec2 (dot (u.zxy, qnBlk), dot (u.yzx, qnBlk)) / bSize;
  }
}

vec3 BgCol (vec3 rd)
{
  float u;
  u = rd.y + 1.;
  return vec3 (0.5, 0.5, 1.) * (0.05 + 0.245 * u * u);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj;
  rLoop = 2. + 1.2 * sin (0.1 * 2. * pi * tCur);
  aLoop = 0.5 * pi / rLoop;
  arcLim = vec2 (sin (1.01 * aLoop), cos (1.01 * aLoop));
  dpLoop = 2. * rLoop * vec2 (- cos (2. * aLoop), sin (2. * aLoop));
  rIn = 0.2;
  rBase = 6.;
  rTop = rBase - 2. * rLoop - dpLoop.x - 0.65 * rIn;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar && idObj == 1) {
    vn = ObjNf (ro + dstObj * rd);
    if (vn.y > 0.99) {
      ro += dstObj * rd;
      rd = reflect (rd, vn);
      ro += 0.01 * rd;
      dstObj = ObjRay (ro, rd);
    }
  }
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 3) {
      col = vec3 (0.9, 0.9, 1.) * (0.5 - 0.5 * dot (rd, vn)) / (1. + 0.6 * pow (length (ro.xz) / rTop, 4.));
    } else if (idObj == 4) {
      col = vec3 (0.9, 0.9, 0.8);
      col = col * (0.1 + 0.9 * max (dot (vn, ltDir), 0.)) +
         0.5 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    } else {
      col = vec3 (0.5, 0.4, 0.);
      col = col * (0.2 + 0.8 * max (dot (vn, ltDir), 0.)) +
         0.2 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 128.);
    }
    if (idObj == 4) {
      BlkHit (ro, reflect (rd, vn));
      qBlk = mod (8. * qBlk, 1.);
      col = mix (col, vec3 (0.5) * (0.5 + 0.5 * SmoothBump (0.1, 0.9, 0.02, qBlk.x) *
         SmoothBump (0.1, 0.9, 0.02, qBlk.y)), 0.2);
    }
  } else {
    col = BgCol (rd);
    BlkHit (ro, rd);
    qBlk = mod (8. * qBlk, 1.);
    col = mix (col, vec3 (0.5) * (0.9 + 0.1 * SmoothBump (0.1, 0.9, 0.02, qBlk.x) *
       SmoothBump (0.1, 0.9, 0.02, qBlk.y)), 0.2);
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = pi / 6.;
  el = -0.2 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  } else {
    az += 0.05 * tCur;
    el += 0.1 * pi * sin (0.03 * 2. * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 2., -30.);
  rd = vuMat * normalize (vec3 (uv, 3.2));
  dstFar = 50.;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  fragColor = vec4 (pow (ShowScene (ro, rd), vec3 (0.8)), 1.);
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRCylDf (vec3 p, float r, float rt, float h)
{
  vec2 dc;
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  dc = vec2 (dxy, dz) + rt;
  return min (min (max (dc.x, dz), max (dc.y, dxy)), length (dc) - rt);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

#define M_PI 3.141592

// tweaked version of this by 'bmodone' - https://www.shadertoy.com/view/4llcDH

float radial(vec2 uv, float offset, float repeat)
{
    float a = mod((atan(uv.y, uv.x) + M_PI + (offset * 2.0 * M_PI)) * repeat / M_PI, 2.0);
    return min(a,2.0-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord - 0.5* iResolution.xy ) / iResolution.y;

    float off = length(uv);
 	float d = off - 0.2;
    float a = radial(uv, sin(off*4.0-iTime*0.5)/16.0, 7.0) - 0.6;

    d = a-d;

    float m = clamp(-d*2.0, 0.0, 1.0);

    vec3 col = mix(vec3(0.6, 0.1, 0.9), vec3(0.82, 0.81, 0.6-d), m);

    col *= (sin(-off * 6.0 - iTime*2.0) / 7.0) + 0.9;

	fragColor = vec4(col,1.0);
}

/* 
    random cubic Truchet flow, by mattz
    License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

	Inspired by:

      Truchet Marbles (Simple) by shau - https://www.shadertoy.com/view/MtlcDs
      Square Truchet Flow by Shane - https://www.shadertoy.com/view/XtfyDX


    Keys:

      D - toggle direction arrows (rotations/reflections of canonical cell)
      M - toggle camera motion
      S - toggle spheres
      T - toggle Truchet torii
      X - toggle X-axis alternation


    Mouse to bottom-left to get default viewing angle.


    Analogous to Shane's 2D shader, this fixes the flow directions along cube faces
    in a repeating fashion. I randomize the torus orientations while confirming to the
    predetermined flow directions.

    See the documentation of the truchet function below for the meat.

    This calls atan to do the shading along each torus segment, but I make sure
    to only call it once per pixel when actually shading. 

*/

// Bunch of globals/constants:
const int rayiter = 60;
const float dmax = 20.0;
vec3 L = normalize(vec3(-0.7, 1.0, -1.0));
mat3 Rview;

float move_camera = 1.0;
float show_spheres = 1.0;
float show_directions = 0.0;
float show_truchet = 1.0;
float alternate_x = 1.0;

const float HALFPI = 1.5707963267948966;
const float TUBE_SIZE = 0.015;
const float SPHERE_SIZE = 0.06;

const float ARROW_RAD = 0.025;
vec2 ARROW_HEAD_SLOPE = normalize(vec2(1, 2));

const float ARROW_BODY_LENGTH = 0.3;
const float ARROW_HEAD_LENGTH = 0.1;

/* RGB from hue. */
vec3 hue(float h) {
    vec3 c = mod(h*6.0 + vec3(2, 0, 4), 6.0);
    return h >= 1.0 ? vec3(h-1.0) : clamp(min(c, -c+4.0), 0.0, 1.0);
}

/* Rotate about x-axis */
mat3 rotX(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0, 0, 
                0, cx, sx,
                0, -sx, cx);
}

/* Rotate about y-axis */
mat3 rotY(in float t) {
    float cy = cos(t), sy = sin(t);
    return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);

}

/* From https://www.shadertoy.com/view/4djSRW */
#define HASHSCALE1 .1031

float hash13(vec3 p3) {
	p3  = fract(p3 * HASHSCALE1);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

/* Distance to 3D arrow - steal this if you like! */
float sdArrow(vec3 p, vec3 d) {

    // component along
    float t = dot(p, d);

    // component perp
    float n = length(p - t*d);

	// distance to cylinder body
    float dist = n - ARROW_RAD;

    // account for arrowhead size
    t += 0.5*ARROW_HEAD_LENGTH;

    // body caps
    dist = max(dist, abs(t)-0.5*ARROW_BODY_LENGTH);

    // move to end of arrow
    t -= 0.5*ARROW_BODY_LENGTH;

    // cone head
    dist = min(dist, max(-t, dot(ARROW_HEAD_SLOPE, vec2(t-ARROW_HEAD_LENGTH, n))));

    return dist;

}


/* Check distance to torus at origin, perpendicular to z axis. */
float sdTorus(vec3 u) {

    vec2 q = vec2(length(u.xy)-0.5,u.z);
    float d = length(q) - TUBE_SIZE;
    d = max(d, max(u.x, u.y));

    return d;

}

/* Distance to ball along torus segment orbiting origin (same frame as sdTorus). */
float sdBall(vec3 u) {

    float theta = HALFPI * fract(0.2*iTime);

	// Is this cheaper than separate sin & cos? I dunno.    
    vec2 sc = 0.5*sin(vec2(theta, theta+HALFPI));

    // Need multiple checks to handle ball crossing cube face boundaries!
    float d = length(u + vec3(sc.x, sc.y, 0));
    d = min(d, length(u + vec3(sc.y, -sc.x, 0)));
    d = min(d, length(u + vec3(-sc.y, sc.x, 0)));

    return d - SPHERE_SIZE;

}

/* Compute edge-relative coords and torus distance given:

    - p is point in cube with vertices at +/- 0.5:
    - src is outward facing normal of flow source face 
    - dst is outward facing normal of flow sink face

   Returns a vec4 (ux uy uz d) where:

     - ux is distance from src face (negative inside cube)
     - uy is distance from dst face (also negative inside cube)
     - uz is projected distance along edge from center (positive or negative)
     - d is distance to torus.

*/
vec4 checkTorus(vec3 p, vec3 src, vec3 dst) {

    vec3 n = cross(dst, src);
    vec3 ctr = 0.5*(src+dst);

    p -= ctr;

    vec3 u = p * mat3(src, dst, n);

    return vec4(u, sdTorus(u));

}

/* Chooses vec4 with least last element (useful for comparing checkTorus output) */
vec4 tmin(vec4 a, vec4 b) {

    return a.w < b.w ? a : b;

}


/* This is the workhorse of the Truchet arrangement. Input argument is
   an arbitrary 3D point, which is modified to become relative to cell
   center and orientation upon output. Also outputs a mat3 of the format
   output by sdTorus above. 

   As you can see by enabling cell directions, every cubic cell in the
   Truchet tiling has three flow inputs and three outputs. The "canonical 
   cell" has flow inputs on the +X, -Y, and +Y faces (and flow outputs on
   the -X, -Z, +Z) faces.

   In order to get these to tile space, we need to swap Y with Z in a
   checkerboard pattern along the YZ plane.

   Also, it looks obviously "fake" (in my opinion, at least) to have all 
   of the flow from +X to -X, so I also alternate the X direction on
   successive Y levels. 

   So, we now have the "canonical cell" potentially with Y/Z swapped, and/or
   X negated. There are four possible torus segment arrangements within 
   the canonical cell. The table below shows for each input face (+X, -Y, +Y),
   what output face it connects to:

     case | +X -Y +Y
     -----+----------
        0 | -Z -X +Z
        1 | -Z +Z -X
        2 | +Z -X -Z
        3 | +Z -Z -X

   We choose one of these cases at random for each cell, and get the torus 
   distance for the given point.

*/
vec4 truchet(inout vec3 pos) {

    // Shift by 0.5 to place world origin at vertex, not cell center
    pos -= 0.5;

    // Find center of nearest cell
    vec3 ctr = floor(pos+0.5);

    // Subtract off difference
    pos -= ctr;

    // Alternating sign on each axis
    vec3 s = sign(mod(ctr, 2.0) - 0.5);

    // Swap Y and Z in checkerboard pattern.
    if (s.y * s.z > 0.0) { pos.yz = pos.zy; }

    // Alternate sign on X within cell if desired
    if (alternate_x > 0.0) { pos.x *= -s.y; }

    // Get case and set up destination axes
    float k = hash13(ctr) * 4.0;

    mat3 dst = mat3(0, 0, -1, 
                    -1, 0, 0, 
                    0, 0, 1);                

    if (k < 2.0) {
        if (k < 1.0) {
            // NOP - just use setup above
            // dst = dst
        } else {
            dst = mat3(dst[0], dst[2], dst[1]); 
        }
    } else {
        if (k < 3.0) {
            dst = mat3(dst[2], dst[0], dst[1]); 
        } else {
            dst = mat3(dst[2], dst[1], dst[0]); 
        }
    }

    // Handle +X face
    vec4 t = checkTorus(pos, vec3(1, 0, 0), dst[0]);

    // Handle +Y face
    t = tmin(t, checkTorus(pos, vec3(0, 1, 0), dst[1]));

    // Handle -Y face
    t = tmin(t, checkTorus(pos, vec3(0, -1, 0), dst[2]));

    return t;


}

/* Boolean union of solids for map function below */
vec2 dmin(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

/* Distance function to scene */
vec2 map(in vec3 pos) {	

    vec4 t = truchet(pos);

    vec2 dm = vec2(1e5, -1);

    if (show_truchet != 0.0) {
        dm = dmin(dm, vec2(t.w, 2.0));
    }

    if (show_spheres != 0.0) {
        dm = dmin(dm, vec2(sdBall(t.xyz), 1.1));
    }

    if (show_directions != 0.0) {
        dm = dmin(dm, vec2(sdArrow(pos - vec3(0.5, 0, 0), vec3(-1, 0, 0)), 0));
        dm = dmin(dm, vec2(sdArrow(pos + vec3(0.5, 0, 0), vec3(-1, 0, 0)), 0));
        dm = dmin(dm, vec2(sdArrow(pos - vec3(0, 0.5, 0), vec3(0, -1, 0)), 0.3333));
        dm = dmin(dm, vec2(sdArrow(pos + vec3(0, 0.5, 0), vec3(0, 1, 0)), 0.3333));
        dm = dmin(dm, vec2(sdArrow(pos - vec3(0, 0, 0.5), vec3(0, 0, 1)), 0.6666));
        dm = dmin(dm, vec2(sdArrow(pos + vec3(0, 0, 0.5), vec3(0, 0, -1)), 0.6666));
    }

    return dm;

}


/* IQ's normal calculation. */
vec3 calcNormal( in vec3 pos ) {
    vec3 eps = vec3( 0.001, 0.0, 0.0 );
    vec3 nor = vec3(
        map(pos+eps.xyy).x - map(pos-eps.xyy).x,
        map(pos+eps.yxy).x - map(pos-eps.yxy).x,
        map(pos+eps.yyx).x - map(pos-eps.yyx).x );
    return normalize(nor);
}


/* IQ's distance marcher. */
vec2 castRay( in vec3 ro, in vec3 rd, in float maxd ) {

    const float precis = 0.002;   
    float h=2.0*precis;

    float t = 0.0;
    float m = -1.0;

    for( int i=0; i<rayiter; i++ )
    {
        if( abs(h)<precis||t>maxd ) continue;//break;
        t += h;
        vec2 res = map( ro+rd*t );
        h = res.x;
        m = res.y;        
    }    

    if (t > maxd) {
        m = -1.0;
    }

    return vec2(t, m);

}

/* Only clever thing about the shading is to postpone the expensive atan call
   until the last possible second. It should buy us a bit of framerate on 
   slower cards, but I was getting 60FPS before I decided to do this. Meh.
 */
vec3 shade( in vec3 ro, in vec3 rd ){

    vec2 tm = castRay(ro, rd, dmax);        

    vec3 c;

    if (tm.y < 0.0) { 

        // miss
        return vec3(1.0);

    } else {        

        // hit
        vec3 pos = ro + tm.x*rd;
        vec3 n = calcNormal(pos);


        if (tm.y >= 2.0) { 

            // material 2 means we hit a torus, so use atan-based rainbow map
            vec4 t = truchet(pos);
            tm.y = fract( atan(t.y, t.x)/HALFPI - 0.25*iTime );

        }

        vec3 color = hue(tm.y);

        vec3 diffamb = (0.5*clamp(dot(n,L), 0.0, 1.0)+0.5) * color;
        vec3 R = 2.0*n*dot(n,L)-L;
        float spec = 0.3*pow(clamp(-dot(R, rd), 0.0, 1.0), 20.0);
        vec3 c = diffamb + spec;

        return mix(c, vec3(1), 1.0-exp(-0.25*tm.x));

    }


}

/* Compare key state to default state. */
float keyState(float key, float default_state) {
    return abs( texture(iChannel0, vec2(key, 0.75)).x - default_state );
}

const float KEY_D = 68.5/256.0;
const float KEY_M = 77.5/256.0;
const float KEY_S = 83.5/256.0;
const float KEY_T = 84.5/256.0;
const float KEY_X = 88.5/256.0;

/* Finally, our main function: */
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    show_directions = keyState(KEY_D, show_directions);
    move_camera = keyState(KEY_M, move_camera);
    show_truchet = keyState(KEY_T, show_truchet);
    show_spheres = keyState(KEY_S, show_spheres);
    alternate_x = keyState(KEY_X, alternate_x);

    vec2 uv = (fragCoord.xy - .5*iResolution.xy) * 0.8 / (iResolution.y);

    const vec3 tgt = vec3(0.0, 0.0, 0.0);
    const vec3 cpos = vec3(0, 0, 5);
    const vec3 up = vec3(0, 1, 0);

    vec3 rz = normalize(tgt - cpos),
        rx = normalize(cross(rz,vec3(0,1.,0))),
        ry = cross(rx,rz);


    vec2 mpos;

    if (max(iMouse.x, iMouse.y) > 20.0) { 
        mpos.xy = iMouse.xy;
    } else {
        mpos = iResolution.xy * vec2(0.432, 0.415);
    }

    float thetax = (mpos.y - .5*iResolution.y) * -4.0*HALFPI/iResolution.y; 
    float thetay = (mpos.x - .5*iResolution.x) * 8.0*HALFPI/iResolution.x; 


    Rview = mat3(rx,ry,rz)*rotY(thetay)*rotX(thetax); 
    L = Rview*L;

   	/* Render. */
    vec3 rd = Rview*normalize(vec3(uv, 1.)),
        ro = tgt + Rview*vec3(0,0,-length(cpos-tgt));

    if (move_camera != 0.0) {
        ro -= 0.15*iTime;
    }

    fragColor.xyz = shade(ro, rd);

}

