// some constants
#define speed 2.0
#define amplitude 0.05
#define freq 6.0
#define number_lines 6.0
#define line_threshold 0.2
#define fg_color vec4(0.8)
#define bg_color vec4(0.1, 0.35, 1.0, 1.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    float decay = (0.5 + (1.0 - uv.y));
    float t = (freq * uv.x) + (sin((smoothstep(0.0, 1.0, uv.y) - iTime) * speed) * decay);
    uv.y += sin(t) * amplitude;

    float depth = mix(1.0, 0.2, uv.y);
    float shadow = fract(uv.y * number_lines);

    float outline = smoothstep(-6.0/iResolution.y, 6.0/iResolution.y, line_threshold - shadow)* depth;
    shadow = mix(0.7, 1.0, shadow) * depth;
    fragColor = shadow*bg_color + outline*fg_color;
}

