// Some nice colors
#define c1 (vec3(238,127,26)/255.)	// Bright orange
#define c2 (vec3(58,153,163)/255.)	// Bright teal
#define c3 (vec3(65,105,108)/255.)	// Dark teal
#define c4 (vec3(109,8,6)/255.)		// Crimson?

#define S(x,w) smoothstep(-w,w,x)

/// Distance functions and smoothMerge from: https://www.shadertoy.com/view/4dfXDn
float boxDist(vec2 p, vec2 size, float radius)
{
	size -= vec2(radius);
	vec2 d = abs(p) - size;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}

vec2 rot(vec2 p, float a)
{
	mat2 m = mat2(cos(a), sin(a), -sin(a), cos(a));
	return p * m;	
}

float smoothMerge(float d1, float d2, float k)
{
    float h = clamp(0.5 + 0.5*(d2 - d1)/k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0-h);
}


// Each pattern is a square ranging from 0,0 to 1,1.
// The orientation vector tells how it is rotated

// Pattern 1
vec3 pat1(vec2 uv, vec2 orient)
{
    uv -= vec2(0.5);
    vec3 color = c4;

    color = mix(c1, color, S(boxDist(uv, vec2(.45), 0.05), 0.01));	// Main box
    color = mix(c4, color, S(boxDist(rot(uv - vec2(0.2*sign(uv.x), -0.2*sign(uv.x)), 3.14159*0.25), vec2(.1), 0.02), 0.01));
    float v = smoothMerge(boxDist(uv - vec2(0.15*sign(uv.x)), vec2(.15), 0.1),
                          boxDist(rot(uv, 3.1415*1./4.), vec2(.15, .1), 0.0),
                          0.05);
    color = mix(c4, color, S(v, 0.01));



    return color;

    return vec3(1.-S(boxDist(uv, vec2(.45), 0.), 0.01));
}

#define smoothround(x, w) (floor(x) + smoothstep(0.5-w, 0.5+w, fract(x)))

// Pattern 2
vec3 pat2(vec2 uv, vec2 orient)
{
    uv -= vec2(0.5);

    if(orient.x == 0.0) {orient.x += 0.1;}
    uv = rot(uv, smoothround(atan(orient.y, orient.x)/(3.14159*0.5), 0.0)*3.14159*0.5-3.14159*0.5);


    vec3 color = c4;

    #define pupilsize (cos(iTime) * cos(iTime) * 0.05 + 0.07)

    // Left Eye
    color = mix(c2, color, S(boxDist(uv + vec2(0.25, -0.25), vec2(.2), 0.01), 0.01));
    color = mix(c1, color, S(boxDist(uv + vec2(0.25, -0.25), vec2(.15), 0.01), 0.01));
    color = mix(c1*0.5, color, S(boxDist(rot(uv + vec2(0.25, -0.25), -iTime), vec2(pupilsize), 0.01), 0.01));

    // Right eye
    color = mix(c2, color, S(boxDist(uv + vec2(-0.25, -0.25), vec2(.2), 0.01), 0.01));
    color = mix(c1, color, S(boxDist(uv + vec2(-0.25, -0.25), vec2(.15), 0.01), 0.01));
    color = mix(c1*0.5, color, S(boxDist(rot(uv + vec2(-0.25, -0.25), iTime), vec2(pupilsize), 0.01), 0.01));


    // Mouth
    color = mix(c2, color, S(boxDist(uv + vec2(0., 0.25), vec2(.45, 0.2), 0.01), 0.01));
    color = mix(c4, color, S(boxDist(mod(uv,vec2(0.17, 1.)) + vec2(0. , -0.65), vec2(.05, 0.1), 0.01), 0.01));

    return color;
}



#define tilesize1 64.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2. / iResolution.xy) - vec2(1);
    vec2 norFragCoord = fragCoord.xy - iResolution.xy * 0.5;
    vec2 uvblocks = floor(norFragCoord.xy / tilesize1);


    vec3 color = pat2(fract(norFragCoord.xy / tilesize1), vec2(sin(uvblocks.y *10.)*0.5, sin(uvblocks.y*10.)));

    if (mod(uvblocks, 2.0) == vec2(0.0) || mod(uvblocks.x + uvblocks.y, 2.) == 0.0)
    {
        color = pat1(fract(norFragCoord.xy / tilesize1), vec2(0));
    }


	fragColor = vec4(color,1.0);
    //fragColor = vec4(uvblocks.x);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 pos = (fragCoord - iResolution.xy * 0.5) / iResolution.xy;

    pos *= (0.9 + 0.1 * sin(iTime * 2. + 13. / (0.3 + length(pos)))) * cos(length(pos) * 2.7) / length(pos);

    float angle = sin(iTime * 0.3341 + 0.5) * 1.3 + cos(iTime * 0.3245 + 0.3453);
    pos = vec2(
    	pos.x * cos(angle) - pos.y * sin(angle),
        pos.x * sin(angle) + pos.y * cos(angle)
	);

    const float TILEDNESS = 50.;
    float a = sin(pos.x * TILEDNESS) < 0. ? 0. : 1.;
    if(sin(pos.y * TILEDNESS) < 0.) a = 1. - a;

    float random = 0.38234;
    for(float i = 0.; i < 5.; i++){
    	random = mod(random * 3457.35242334 + 0.545345, 1.);
        if(pos.x * 2. * (random - 0.5) + pos.y  * sin(random * 1000. + iTime * 0.3) < 0.) a = 1. - a;
    } 

    fragColor = vec4(
        0.4 * (sin(
        	pos.x * sin(iTime * 0.523 + 123.) + pos.y  * sin(iTime * 0.945) + iTime
        ) + 1.), 
        sin(iTime * 0.231 + pos.x + pos.y + pos.x * pos.y) * 0.5 + 0.5, 
        cos(iTime *0.123 + pos.x + pos.y) * 0.5 + 0.5, 
        1.);

    if(a == 1.){
    	fragColor.yz = vec2(1., 1.) - fragColor.xz;    
    }
}

float EPSILON = 0.001;
int MAX_STEPS = 100;
float MAX_DIST = 100.0;

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

float boxSDF(vec3 p, vec3 size, float r) {
    vec3 d = abs(p) - (size / 2.0) + r;
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance - r;
}

float sphereSDF(vec3 p, float size) {
    return length(p) - size;
}

float sceneSDF(vec3 p) {
    float strength = 10.0;
    float size = 1.0;
    vec3 zone1 = vec3(vec2(sin(iTime*0.5)*3.0),0.0);
    vec3 zone2 = vec3(vec2(sin(iTime*0.5)*-3.0),0.0);
    float sdf = min(min(distance(zone1,p),distance(zone2,p)),(distance(zone1,p)*distance(zone2,p))/strength)-size;
    return sdf;
}

float March(vec3 Dir, vec3 eye) {
    float depth = 0.0;
    for (int i = 0; i < MAX_STEPS; i++) {
        float dist = sceneSDF(eye + (Dir*depth));
        if (dist < EPSILON) {
            return depth;
        }
        depth += dist;
        if (depth >= MAX_DIST) {
            return MAX_DIST;
        }
    }
    return MAX_DIST;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

vec3 interp(vec3 a,vec3 b,float t) {
    return a + ((b-a)*t);
}

vec3 Light(vec3 p, vec3 eye, vec3 Amb, vec3 Diff, vec3 Spec) {
    float IntensitySpec = 0.4;
    float IntensityDiff = 0.4;
    float shine = 10.0;
    vec3 source = vec3(sin(2.0*iTime)*5.0,cos(2.0*iTime)*5.0,0.0);
    //source = vec3(0.0,0.0,0.0);
    vec3 N = estimateNormal(p);
    vec3 L = normalize(source - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));
    float millis = iTime*25.0;
    vec3 color = vec3(sin(millis/10.0)*127.0+127.0,sin(millis/10.0+85.0)*127.0+127.0,sin(millis/10.0+175.0)*127.0+127.0);
    //Amb = interp(N,Amb,(1.0+cos(iTime))/2.0);
    Amb = color / 255.0;
    vec3 col = Amb * 0.5;
    if (dot(L,N) < 0.0) {
        return col;
    }
    if (dot(R,V) < 0.0) {
        col += (Diff * IntensityDiff * dot(L,N));
        return col;
    }
    col += (Diff * IntensityDiff * dot(L,N));
    col += (Spec * pow(dot(R,V),shine));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 eye = vec3(sin(iTime)*5.0,cos(iTime)*5.0,6.5);
    //eye = vec3(0.0,0.0,6.5);
    vec3 center = vec3(0.0,0.0,0.0);
    vec3 up = vec3(0.0,1.0,0.0);
    mat3 Matrix = viewMatrix(eye,center,up);
    vec3 dir = rayDirection(90.0,iResolution.xy,fragCoord);
    float mDist = March(Matrix * dir,eye);
    vec3 point = eye + (mDist * (Matrix * dir));
    vec3 light = Light(point, eye, vec3(1.0,1.0,1.0), vec3(1.0,1.0,1.0), vec3(1.0,1.0,1.0));
    if (mDist > MAX_DIST - EPSILON) {
        fragColor = vec4(0.0,0.0,0.0,1.0);
        return;
    }
    fragColor = vec4(light, 1.0);
}

/*
    Left side uses a constant width for blending between layers.
       This reduces the # of pixels that require > 1 texture sample
       Also I think it looks better as less pixels are "blurry"

    Right side uses "standard" triplanar texturing. 
      Corners have almost no blend width, while aligned axis have huge blend width.

    Red/Green/Blue = 1 texture sample
    Yellow/Pink/Cyan = 2 texture sample
    white = 3 texture samples

*/

//"Standard" triplanar blending.
vec3 TriPlanarBlendWeightsStandard(vec3 normal) {
	vec3 blend_weights = abs(normal.xyz); 

    float blendZone = 0.55;//anything over 1/sqrt(3) or .577 will produce negative values in corner
	blend_weights = blend_weights - blendZone; 

	blend_weights = max(blend_weights, 0.0);     
	float rcpBlend = 1.0 / (blend_weights.x + blend_weights.y + blend_weights.z);
	return blend_weights*rcpBlend;
}

//Constant width Triplanar blending
vec3 TriPlanarBlendWeightsConstantOverlap(vec3 normal) {

	vec3 blend_weights = normal*normal;//or abs(normal) for linear falloff(and adjust BlendZone)
	float maxBlend = max(blend_weights.x, max(blend_weights.y, blend_weights.z));

    float BlendZone = 0.8f;
	blend_weights = blend_weights - maxBlend*BlendZone;

	blend_weights = max(blend_weights, 0.0);   

	float rcpBlend = 1.0 / (blend_weights.x + blend_weights.y + blend_weights.z);
	return blend_weights*rcpBlend;
}

