#define countTubes 6.

const vec3 ld = vec3(0.,1., .5);

float t = 0.;

vec2 m;

float fullAtan(vec2 p)
{
    return step(0.0,-p.x)*3.1415926535 + sign(p.x) * atan(p.x, sign(p.x) * p.y);
}

vec2 minObj(vec2 a, vec2 b)
{
	if (a.x < b.x)
        return a;
    return b;
}

vec4 df(vec3 p)
{
    float fa = fullAtan(p.xy) + 3.14159/10.;
    float pathXY = length(p.xy) - 1.5 - (sin(fa*5.)*.5+.5)*.5;
    vec2 rev = vec2(pathXY, p.z);
    float a = iTime*2.;
    float st = 3.14159*1.5 / countTubes;
    vec2 tu = vec2(length(rev + vec2(cos(a), sin(a)) * 0.5) - 0.2,1);
    for (float i=2.;i<countTubes;i++)
    {
    	a += st;
    	tu = minObj(tu,vec2(length(rev - vec2(cos(a), sin(a)) * 0.5) - 0.2,i));;
    }

	return vec4(tu,rev); 
}

vec3 nor( vec3 p, float prec )
{
    vec2 e = vec2( prec, 0. );
    vec3 n = vec3(
		df(p+e.xyy).x - df(p-e.xyy).x,
		df(p+e.yxy).x - df(p-e.yxy).x,
		df(p+e.yyx).x - df(p-e.yyx).x );
    return normalize(n);
}

// from iq code
float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<1; i++ )
    {
		float h = df( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += h*.25;
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0., 1. );
}

// from iq code
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<10; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = df( aopos ).x;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );    
}

