#define BORDER_THICKNESS 2.5

const vec4 fillColor   = vec4(1.0, 0.4, 0.0, 1.0);   // orange
const vec4 borderColor = vec4(0.1, 0.1, 0.1, 1.0);   // darkgray

const float HALF_PI = 1.57079632679;
const float      PI = 3.14159265359;
const float  TWO_PI = 6.28318530718;

vec2 uv = vec2(0);  // centered pixel position -1 .. 1

//////////////////////////////////////
// Combine distance field functions //
//////////////////////////////////////

float smoothMerge(float d1, float d2, float k)
{
    float h = clamp(0.5 + 0.5*(d2 - d1)/k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0-h);
}

float merge(float d1, float d2)
{
	return min(d1, d2);
}

float mergeExclude(float d1, float d2)
{
	return min(max(-d1, d2), max(-d2, d1));
}

float substract(float d1, float d2)
{
	return max(-d1, d2);
}

float intersect(float d1, float d2)
{
	return max(d1, d2);
}

//////////////////////////////
// Rotation and translation //
//////////////////////////////

vec2 rotateCCW (vec2 pos, float angle)
{ float ca = cos(angle),  sa = sin(angle);
  return pos * mat2(ca, sa, -sa, ca);  }


vec2 rotateCW (vec2 pos, float angle)
{ float ca = cos(angle),  sa = sin(angle);
  return pos * mat2(ca, -sa, sa, ca);  }


vec2 translate(vec2 p, vec2 t) 
{ return p - t; }


//////////////////////////////
// Distance field functions //
//////////////////////////////

float pie(vec2 p, float angle)
{
  angle = radians(angle) / 2.0;
  return abs(p.x) * sin(angle) + p.y*cos(angle);
}

float sdCircle (vec2 p, float radius)
{
  return length(p) - radius;
}

float triangleDist(vec2 p, float radius)
{
  return max( abs(p.x) * 0.866025 + p.y * 0.5, -p.y) -radius * 0.5;
}

float triangleDist(vec2 p, float width, float height)
{
  vec2 n = normalize(vec2(height, width / 2.0));
  return max(	abs(p).x*n.x + p.y*n.y - (height*n.y), -p.y);
}

float semiCircleDist(vec2 p, float radius, float angle, float width)
{
  width /= 2.0;
  radius -= width;
  return substract(pie(p, angle + sin(iTime)*55.), 
                       abs(length(p) - radius) - width);
}

//---------------------------------------------------------
float lineDist(vec2 p, vec2 start, vec2 end, float width)
{
  vec2 dir = start - end;
  float lngth = length(dir);
  dir /= lngth;
  vec2 proj = max(0.0, min(lngth, dot((start - p), dir))) * dir;
  return length( (start - p) - proj ) - (width / 2.0);
}

//---------------------------------------------------------
// IQ's signed distance to a 2D rounded box
//---------------------------------------------------------
float sdBox(in vec2 p, in vec2 size, in float radius)
{
  vec2 d = abs(p) - size + radius;
  return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}

//---------------------------------------------------------
// IQ's signed distance to ellipse
//---------------------------------------------------------
float sdEllipse(in vec2 p, in vec2 ab )
{
	p = abs( p ); if( p.x > p.y ){ p=p.yx; ab=ab.yx; }

	float l = ab.y*ab.y - ab.x*ab.x;

    float m = ab.x*p.x/l; 
	float n = ab.y*p.y/l; 
	float m2 = m*m;
	float n2 = n*n;

    float c = (m2 + n2 - 1.0)/3.0; 
	float c3 = c*c*c;

    float q = c3 + m2*n2*2.0;
    float d = c3 + m2*n2;
    float g = m + m*n2;

    float co;

    if( d<0.0 )
    {
        float h = acos(q/c3)/3.0;
        float s = cos(h);
        float t = sin(h)*sqrt(3.0);
        float rx = sqrt( -c*(s + t + 2.0) + m2 );
        float ry = sqrt( -c*(s - t + 2.0) + m2 );
        co = ( ry + sign(l)*rx + abs(g)/(rx*ry) - m)/2.0;
    }
    else
    {
        float h = 2.0*m*n*sqrt( d );
        float s = sign(q+h)*pow( abs(q+h), 1.0/3.0 );
        float u = sign(q-h)*pow( abs(q-h), 1.0/3.0 );
        float rx = -s - u - c*4.0 + 2.0*m2;
        float ry = (s - u)*sqrt(3.0);
        float rm = sqrt( rx*rx + ry*ry );
        co = (ry/sqrt(rm-rx) + 2.0*g/rm - m)/2.0;
    }

    float si = sqrt( 1.0 - co*co );

    vec2 r = ab * vec2(co,si);

    return length(r-p) * sign(p.y-r.y);
}

//---------------------------------------------------------
// draw polygon with given radius n edges at pos 
//---------------------------------------------------------
float polygon(vec2 p, float radius, float vertices)
{
  float angle = atan(p.x, -p.y) + TWO_PI;
  float r = TWO_PI / vertices;
  return cos(floor(0.5 + angle / r) * r - angle) * length(p) - radius;
}
//---------------------------------------------------------
float flower(vec2 p, float radius, float leafs)
{
  return 25.*(length(p)/radius - 0.4*sin(leafs*(atan(p.y,p.x)-iTime)) -0.75);
}
