//---------------------------------------------------------
float pumpkin2D(vec2 p, float radius, float vertices, float curvature)
{
  float angle = atan(p.x, -p.y) / TWO_PI * vertices;
  return length(p) -radius +smoothstep(0., 1., abs(fract(angle)-0.5)) *radius *curvature;
}
