float fillMask(float dist)
{
	return clamp(-dist, 0.0, 1.0);
}

float innerBorderMask(float dist, float width)
{
	//dist += 1.0;
	return clamp(dist + width, 0.0, 1.0) - clamp(dist, 0.0, 1.0);
}

float outerBorderMask(float dist, float width)
{
	//dist += 1.0;
	return clamp(dist, 0.0, 1.0) - clamp(dist - width, 0.0, 1.0);
}


///////////////
// The scene //
///////////////

float sceneDistance(vec2 p)  // p: position
{
    //=== 1st row ===
    int yd = 340;

    float ts = sin(iTime)*.49+.49;
    float edges = 4. + floor(8. * ts);  

    float  m = polygon(			translate(p, vec2(100, yd)), 40.0, edges);
//    float  m = PacMan(			translate(p, vec2(100, yd)), 35.0);

    float p1 = pumpkin2D(		translate(p, vec2(200, yd)), 35.0, edges, -0.5);
    float s1 = pumpkin2D(		translate(p, vec2(300, yd)), 40.0, edges, +0.4);
	float bb = sdBox(			translate(p, vec2(400, yd)), vec2(40, 20), 10.0);
//    float e1 = sdEllipse(		translate(p, vec2(500, yd)), vec2(40, 20));
    float f1 = flower(          translate(p, vec2(500, yd)), 40., edges);

    vec2        pt = rotateCW ( translate(p, vec2(600, yd)), iTime);
    float e2 = sdEllipse(		pt, vec2(40, 20));

    m = merge(m, p1); // not working 
    m = merge(m, s1); 
    m = merge(m, bb); 
    m = merge(m, f1); 
    m = merge(m, e2); 

    //=== 2nd row ===
    yd = 210;

    float c1 = sdCircle(		translate(p, vec2(100, yd)), 40.0);
	float b1 = sdBox(			translate(p, vec2(200, yd)), vec2(40, 40),  0.0);
	float b2 = sdBox(			translate(p, vec2(300, yd)), vec2(40, 40), 10.0);

               pt = rotateCCW( translate(p, vec2(400, yd)), iTime);
	float l1 = lineDist(		pt,  vec2(-30, -30),  vec2(30, 30),	10.0);

                pt = rotateCW(  translate(p, vec2(500, yd-40)), 0.5*sin(iTime));
    float t1 = triangleDist(	pt, 60.0, 80.0);

                pt = rotateCW(  translate(p, vec2(600, yd)), iTime);
	float t2 = triangleDist(	pt, 40.0);

    m = merge(m, c1);  // circle
    m = merge(m, b1);  // square
	m = merge(m, b2);  // rounded box
	m = merge(m, l1);  // line
	m = merge(m, t1);  // triangle 1
	m = merge(m, t2);  // triangle 2

    //=== 3nd row ===
    float yr = 80.;

	// circle - rectangle
    float b3 = sdBox(		translate(p, vec2(100, yr + sin(iTime * 3.0 + 1.0) * 40.0)), 
					   		vec2(40, 15), 	0.0);
	float c2 = sdCircle(	translate(p, vec2(100, yr)),	30.0);
	float sub = substract(b3, c2);

    // circle and rectangle
	float b4 = sdBox(		translate(p, vec2(200, yr + sin(iTime * 3.0 + 2.0) * 40.0)), 
					   		vec2(40, 15), 	0.0);
	float c3 = sdCircle(	translate(p, vec2(200, yr)), 	30.0);
	float and = intersect(b4, c3);

	// circle or rectangle
    float b5 = sdBox(		translate(p, vec2(300, yr + sin(iTime * 3.0 + 3.0) * 40.0)),
                            vec2(40, 15), 	0.0);
	float c4 = sdCircle(	translate(p, vec2(300, yr)), 	30.0);
	float or = merge(b5, c4);

	// soft circle or rectangle
	float b6 = sdBox(		translate(p, vec2(400, yr + sin(iTime * 1.0) * 20.0)),
                            vec2(40, 15), 	0.0);
	float c5 = sdCircle(	translate(p, vec2(400, yr)), 	30.0);
	float sor = smoothMerge(b6, c5, 10.0);

	// circle xor rectangle
    float b7 = sdBox(		translate(p, vec2(500, yr + sin(iTime * 3.0 + 3.0) * 40.0)),
                            vec2(40, 15), 	0.0);
	float c6 = sdCircle(	translate(p, vec2(500, yr)), 	30.0);
	float xor = mergeExclude(b7, c6);

	// arc
	float arc = semiCircleDist(translate(p, vec2(600,yr)), 40.0, 90.0, 10.0);

	m = merge(m, sub);  // sub 
	m = merge(m, and);  // and 
	m = merge(m, or);   // or
	m = merge(m, sor);  // soft or
    m = merge(m, xor);  // xor
	m = merge(m, arc);  // arc

	return m;
}

float sceneSmooth(vec2 p, float r)
{
	float accum = sceneDistance(p);
	accum += sceneDistance(p + vec2(0.0, r));
	accum += sceneDistance(p + vec2(0.0, -r));
	accum += sceneDistance(p + vec2(r, 0.0));
	accum += sceneDistance(p + vec2(-r, 0.0));
	return accum; // / 5.0;
}


//////////////////////
// Shadow and light //
//////////////////////

float shadow(vec2 p, vec2 pos, float radius)
{
	vec2 dir = normalize(pos - p);
	float dl = length(p - pos);

	// fraction of light visible, starts at one radius (second half added in the end);
	float lf = radius * dl;

	// distance traveled
	float dt = 0.01;

	for (int i = 0; i < 64; ++i)
	{				
		// distance to scene at current position
		float sd = sceneDistance(p + dir * dt);

        // early out when this ray is guaranteed to be full shadow
        if (sd < -radius) 
          return 0.0;

		// width of cone-overlap at light
		// 0 in center, so 50% overlap: add one radius outside of loop to get total coverage
		// should be '(sd / dt) * dl', but '*dl' outside of loop
		lf = min(lf, sd / dt);

		// move ahead
		dt += max(1.0, abs(sd));
		if (dt > dl) break;
	}
	// multiply by dl to get the real projected overlap (moved out of loop)
	// add one radius, before between -radius and + radius
	// normalize to 1 ( / 2*radius)
	lf = clamp((lf*dl + radius) / (2.0 * radius), 0.0, 1.0);
	lf = smoothstep(0.0, 1.0, lf);
	return lf;
}

vec4 drawLight(vec2 p, vec2 pos, vec4 color, float dist, float range, float radius)
{
	// distance to light
	float ld = length(p - pos);

	// out of range ?
	if (ld > range) return vec4(0.0);

	// shadow and falloff
	float shad = shadow(p, pos, radius);
	float fall = (range - ld)/range;
	fall *= fall;
	float source = fillMask(sdCircle(p - pos, radius));
	return (shad * fall + source) * color;
}

float luminance(vec4 col)
{
	return 0.2126 * col.r + 0.7152 * col.g + 0.0722 * col.b;
}

void setLuminance(inout vec4 col, float lum)
{
	lum /= luminance(col);
	col *= lum;
}

float AO(vec2 p, float dist, float radius, float intensity)
{
	float a = clamp(dist / radius, 0.0, 1.0) - 1.0;
	return 1.0 - (pow(abs(a), 5.0) + 1.0) * intensity + (1.0 - intensity);
	return smoothstep(0.0, 1.0, dist / radius);
}


