void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float rx = iResolution.x;
	vec2 uv = 700. * (fragCoord.xy + vec2(0.5)) / rx;
	vec2 mp = (iMouse.xy) * 700. / rx;

	float dist = sceneDistance(uv);

    // mouse light 
	vec2 light1Pos = mp.xy;
	vec4 light1Col = vec4(0.75, 1.0, 0.5, 1.0);
	setLuminance(light1Col, 0.4);

	// moving light
    vec2 light2Pos = vec2(rx * (sin(iTime + 3.1415) + 1.2) / 2.4, 150.0);
	vec4 light2Col = vec4(1.0, 0.75, 0.5, 1.0);
	setLuminance(light2Col, 0.5);

//	vec2 light3Pos = vec2(rx * (sin(iTime) + 1.2) / 2.4, 340.0);
//	vec4 light3Col = vec4(0.5, 0.75, 1.0, 1.0);
//	setLuminance(light3Col, 0.6);

	// gradient
	vec4 col = vec4(0.5, 0.5, 0.5, 1.0) * (1.0 - length(iResolution.xy/1.4 - uv)/rx);
	// grid
	col *= clamp(min(mod(uv.y, 10.0), mod(uv.x, 10.0)), 0.9, 1.0);
	// ambient occlusion
	col *= AO(uv, sceneSmooth(uv, 10.0), 40.0, 0.4);
	//col *= 1.0-AO(uv, sceneDistance(uv), 40.0, 1.0);  // object lightning

    // light
	col += drawLight(uv, light1Pos, light1Col, dist, 150.0, 6.0);
	col += drawLight(uv, light2Pos, light2Col, dist, 200.0, 8.0);
//	col += drawLight(uv, light3Pos, light3Col, dist, 300.0, 12.0);

    col = mix(col, fillColor, fillMask(dist));
	col = mix(col, borderColor, innerBorderMask(dist, BORDER_THICKNESS));

	fragColor = clamp(col, 0.0, 1.0);
}

// using 2d vector graphics library (https://www.shadertoy.com/view/lslXW8)

// undefine to show ground truth
#define SHOW_CUBIC_SPLINE

float p[4];

float cubic_spline_approx(float x) {
    float m[2];
    m[0] = p[1] + (p[2] - p[0]) / 8.0;
    m[1] = p[2] - (p[3] - p[1]) / 8.0;
    float c = (m[0] + m[1]) / 2.0;
    x = x*2.0 - 1.0;
    bool left = (x < 0.0);
    vec3 q = left?vec3(p[1],m[0],c):vec3(c, m[1], p[2]);
    x += float(left);
    float t0 = (1.0 - x); t0 *= t0;
    float t1 = 2.0*(1.0 - x)*x;
    float t2 = x*x;
    return t0*q[0] + t1*q[1] + t2*q[2];
}

float cubic_spline(float x) {
	float m[2];    
    // compute control points
    m[0] = p[1] + (p[2] - p[0]) / 6.0;
    m[1] = p[2] - (p[3] - p[1]) / 6.0;

    float rx = (1.0 - x);
    float rxx = rx*rx;
    float xx = x*x;

    float t0 = rxx*rx;
    float t1 = 3.0*x*rxx;
    float t2 = 3.0*xx*rx;
    float t3 = xx*x;

    return t0 * p[1] + t1 * m[0] + t2 * m[1] + t3 * p[2];
}

