/*
Pre-Alpha Vector Field by Chris M. Thomasson
The code is basic, and the math can be streamlined.
This is a basic experiment, not thinking of sheer performance yet.
___________________________________*/


// The number of points in the spiral
#define CT_N 16

// Viewport Axes
struct ct_axes
{
    float xmin;
    float xmax;
    float ymin;
    float ymax;
};


ct_axes ct_axes_from_radius(
    in vec3 vpcircle
){
    return ct_axes(
        vpcircle.x - vpcircle.z,
        vpcircle.x + vpcircle.z,
        vpcircle.y - vpcircle.z,
        vpcircle.y + vpcircle.z
    );
}


// Simple 2d Plane
struct ct_plane2d
{
    ct_axes axes;
    float xstep;
    float ystep;
};


ct_plane2d ct_plane2d_create(
    in ct_axes axes
){
    float awidth = axes.xmax - axes.xmin;
    float aheight = axes.ymax - axes.ymin;

    float daspect = abs(iResolution.y / iResolution.x);
    float waspect = abs(aheight / awidth);

    if (daspect > waspect)
    {
        float excess = aheight * (daspect / waspect - 1.0);
        axes.ymax += excess / 2.0;
        axes.ymin -= excess / 2.0;
    }

    else if (daspect < waspect)
    {
        float excess = awidth * (waspect / daspect - 1.0);
        axes.xmax += excess / 2.0;
        axes.xmin -= excess / 2.0;
    }

    return ct_plane2d(
        axes,
        (axes.xmax - axes.xmin) / iResolution.x,
        (axes.ymax - axes.ymin) / iResolution.y
    );
}


vec2 ct_plane2d_project(
	in ct_plane2d self,
    in vec2 z
){
	return vec2(
        self.axes.xmin + z.x * self.xstep,
        self.axes.ymin + z.y * self.ystep
    );
}


// A vector field point
struct ct_vfpoint
{
    vec2 p;
    float m;
};


// Our global per-pixel points
ct_vfpoint g_vfp[CT_N];


// Gain a normalized vector from p, 
// using a power of npow.
vec2 ct_vfield_normal(
    in vec2 p,
    float npow
){
    vec2 g = vec2(0.0, 0.0);

    const int imax = CT_N;

    for (int i = 0; i < imax; ++i)
    {
        vec2 dif = g_vfp[i].p - p;
        float sum = dif[0] * dif[0] + dif[1] * dif[1];
        float mass = pow(sum, npow);

      	g[0] = g[0] + g_vfp[i].m * dif[0] / mass;
      	g[1] = g[1] + g_vfp[i].m * dif[1] / mass;


    }

    return normalize(g);
}

float ct_normal_pi(
    in vec2 z
){
    vec2 d = vec2(z[0] * z[0] + z[1] * z[1]);
    float a = atan(d[1], d[0]);
    if (a < 0.0) a += 6.28318;
    a /= 6.28318;
    return a;
}


// Vector Pixel Iteration
vec4 ct_vpixel(
    in vec2 z,
    in vec2 c,
    in int n,
    in float npow
){
    vec2 vn = ct_vfield_normal(z, npow);
    float npi = ct_normal_pi(vn);

    float blah = length(vn);

    if (blah < .01)
    {
        return vec4(1.0, 1.0, 0.0, 1.0);
    }

    return vec4(vn.x, vn.y, vn.x * vn.y, 1.0);
}


// High-Level Entry
vec4 ct_main(
    in vec2 c
){

    float bradius = 1.0;
    float abase = 6.28318 / float(CT_N);
    float arbase = bradius / float(CT_N);

    for (int i = 0; i < CT_N; ++i)
    {
        float angle = abase * float(i) + cos(iTime * .1);
        float radius = 1.2 + arbase * float(i) + abs(sin(iTime)) * .7;

        vec2 z = vec2(
            cos(angle) * abs(cos(angle + sin(iTime * .1))) * radius,
            sin(angle) * abs(sin(angle)) * radius
        );

        g_vfp[i] = ct_vfpoint(z, 1.0);
    }

    return ct_vpixel(c, c, 128, 2.0 + abs(cos(iTime * 3.0)) * 7.14);
}


// Raw Entry.
void mainImage(
  	out vec4 fragColor, 
	in vec2 fragCoord
){
    vec3 vpcircle = vec3(0.0, 0.0, 1.618);

    ct_plane2d plane = ct_plane2d_create(
        ct_axes_from_radius(vpcircle)
    );

    vec2 c = ct_plane2d_project(plane, fragCoord);

    // Exec...
    vec4 color = ct_main(c);

    fragColor = color;
}

//
// Gaussian Blue Noise 2
// by Timo Kinnunen 2017
//
// Comparison between approximated Gaussian blue noise,
// approximated Gaussian noise and triangular noise.
//
// The idea is to approximate a Gaussian distribution by
// adding a bunch of uncorrelated random values together.
// Given enough values the familiar bell shaped curve
// should emerge, but we only add 4 to reduce the cost.
//
// To approximate blue noise, the resulting random values
// are filtered. Using a 5x5 filter kernel causing
// "ringing" would give a nice result, but we only
// use a 3x1 kernel on the theory that on an LCD screen
// vertical stripes are less apparent than
//
// __________ horizontal lines. ___________
//
// The end result is a self-contained noise function that
// hopefully should optimize well.
//

//
// Segments from top to bottom and left to right are:
//
// Triangular noise w/ a 2to2-hash
// Triangular noise w/ 2x 2to1-hash
// Triangular noise w/ a 2to1-hash, remapped
// Exponential noise w/ a 2to1-hash, remapped from ^1 to ^8 (use mouse!)
// Gaussian noise w/ 4x 2to1-hash, summed
// Gaussian noise w/ 2x 2to1-hash, remapped
// Gaussian blue noise w/ 3 taps of 2x 2to1-hash, remapped
// Gaussian blue noise w/ 3 taps of a 2to4-hash, summed
//

//
// Based on A faster triangle noise by TomF
// @ https://www.shadertoy.com/view/4t2SDh
//
// Based on Triangular PDF Noise Dithering by andyborrell
// @ https://www.shadertoy.com/view/Mllczf
//

// Modification of https://www.shadertoy.com/view/4ssXRX
// Added a second way to do triangle noise without doing
// a second random sample.

const int NUM_BUCKETS = 32;
const int ITER_PER_BUCKET = 1024;
const float HIST_SCALE = 8.0;

const float NUM_BUCKETS_F = float(NUM_BUCKETS);
const float ITER_PER_BUCKET_F = float(ITER_PER_BUCKET);

float iiTime;
vec2 hash22(vec2 p) {
    vec3 p3 = fract(p.xyx * vec3(9.1031, 8.1030, 7.0973));
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.x+p3.yz)*p3.zy);
}

//note: uniformly distributed, normalized rand, [0;1[
float nrand( vec2 n )
{
	return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);
}
//note: remaps v to [0;1] in interval [a;b]
float remap( float a, float b, float v )
{
	return clamp( (v-a) / (b-a), 0.0, 1.0 );
}
//note: quantizes in l levels
float truncate( float a, float l )
{
	return floor(a*l)/l;
}

float n1rand( vec2 n )
{
	float t = fract( iiTime );
	float nrnd0 = nrand( n + 0.07*t );
	return nrnd0;
}
float n2rand( vec2 n )
{
	float t = fract( iiTime );
	float nrnd0 = nrand( n + 0.07*t );
	float nrnd1 = nrand( n + 0.11*t );
	return (nrnd0+nrnd1) / 2.0;
}

float n2rand_faster( vec2 n )
{
	float t = fract( iiTime );
	float nrnd0 = nrand( n + 0.07*t );

    // Convert uniform distribution into triangle-shaped distribution.
    float orig = nrnd0*2.0-1.0;
    nrnd0 = orig*inversesqrt(abs(orig));
    nrnd0 = max(-1.0,nrnd0); // Nerf the NaN generated by 0*rsqrt(0). Thanks @FioraAeterna!
    nrnd0 = nrnd0-sign(orig)+0.5;

    // Result is range [-0.5,1.5] which is
    // useful for actual dithering.
    // convert to [0,1] for histogram.
    return (nrnd0+0.5) * 0.5;
}
float n2rand_tri(vec2 n) {
    n += 0.07* fract(iiTime);
    return dot(hash22(n), vec2(1))*0.5;
}
float n3rand( vec2 n )
{
	float t = fract( iiTime );
	float nrnd0 = nrand( n + 0.07*t );
	float nrnd1 = nrand( n + 0.11*t );
	float nrnd2 = nrand( n + 0.13*t );
	return (nrnd0+nrnd1+nrnd2) / 3.0;
}
float n4rand( vec2 n )
{
	float t = fract( iiTime );
	float nrnd0 = nrand( n + 0.07*t );
	float nrnd1 = nrand( n + 0.11*t );	
	float nrnd2 = nrand( n + 0.13*t );
	float nrnd3 = nrand( n + 0.17*t );
	return (nrnd0+nrnd1+nrnd2+nrnd3) / 4.0;
}
float n4rand_inv( vec2 n )
{
	float t = fract( iiTime );
	float nrnd0 = nrand( n + 0.07*t );
	float nrnd1 = nrand( n + 0.11*t );	
	float nrnd2 = nrand( n + 0.13*t );
	float nrnd3 = nrand( n + 0.17*t );
    float nrnd4 = nrand( n + 0.19*t );
	float v1 = (nrnd0+nrnd1+nrnd2+nrnd3) / 4.0;
    float v2 = 0.5 * remap( 0.0, 0.5, v1 ) + 0.5;
    float v3 = 0.5 * remap( 0.5, 1.0, v1 );
    return (nrnd4<0.5) ? v2 : v3;
}

//alternate Gaussian,
//thanks to @self_shadow
float n4rand_ss( vec2 n )
{
	float nrnd0 = nrand( n + 0.07*fract( iiTime ) );
	float nrnd1 = nrand( n + 0.11*fract( iiTime + 0.573953 ) );	
	return 0.23*sqrt(-log(nrnd0+0.00001))*cos(2.0*3.141592*nrnd1)+0.5;
}
//Mouse Y give you a curve distribution of ^1 to ^8
//thanks to Trisomie21
float n4rand_cus( vec2 n )
{
	float t = fract( iiTime );
	float nrnd0 = nrand( n + 0.07*t );

	float p = 1. / (1. + iMouse.y * 8. / iResolution.y);
	nrnd0 -= .5;
	nrnd0 *= 2.;
	if(nrnd0<0.)
		nrnd0 = pow(1.+nrnd0, p)*.5;
	else
		nrnd0 = 1.-pow(nrnd0, p)*.5;
	return nrnd0; 
}
float blugausnoise(vec2 c1) {
    c1 += 0.07* fract(iiTime);
    //vec2 c0 = vec2(c1.x- 1.,c1.y);
    //vec2 c2 = vec2(c1.x+ 1.,c1.y);
    vec3 cx = c1.x+ vec3(-1,0,1);
    vec4 f0 = fract(vec4(cx* 9.1031,c1.y* 8.1030));
    vec4 f1 = fract(vec4(cx* 7.0973,c1.y* 6.0970));
	vec4 t0 = vec4(f0.xw,f1.xw);//fract(c0.xyxy* vec4(.1031,.1030,.0973,.0970));
	vec4 t1 = vec4(f0.yw,f1.yw);//fract(c1.xyxy* vec4(.1031,.1030,.0973,.0970));
	vec4 t2 = vec4(f0.zw,f1.zw);//fract(c2.xyxy* vec4(.1031,.1030,.0973,.0970));
    vec4 p0 = t0+ dot(t0,t0.wzxy+ 19.19);
    vec4 p1 = t1+ dot(t1,t1.wzxy+ 19.19);
    vec4 p2 = t2+ dot(t2,t2.wzxy+ 19.19);
	vec4 n0 = fract(p0.zywx* (p0.xxyz+ p0.yzzw));
	vec4 n1 = fract(p1.zywx* (p1.xxyz+ p1.yzzw));
	vec4 n2 = fract(p2.zywx* (p2.xxyz+ p2.yzzw));
    return dot(0.5* n1- 0.125* (n0+ n2),vec4(1));
}
float blugausnoise2(vec2 c1) {
    float nrand1 = n4rand_ss(c1);
    float nrand0 = n4rand_ss(vec2(c1.x- 1.,c1.y));
    float nrand2 = n4rand_ss(vec2(c1.x+ 1.,c1.y));
    return 2.0* nrand1- 0.5* (nrand0+ nrand2);
}

float histogram( int iter, vec2 uv, vec2 interval, float height, float scale )
{
	float t = remap( interval.x, interval.y, uv.x );
	vec2 bucket = vec2( truncate(t,NUM_BUCKETS_F), truncate(t,NUM_BUCKETS_F)+1.0/NUM_BUCKETS_F);
	float bucketval = 0.0;
	for ( int i=0;i<ITER_PER_BUCKET;++i)
	{
		float seed = float(i)/ITER_PER_BUCKET_F;

		float r;
        if        ( iter < 2 ) { r = n2rand_tri   ((vec2(uv.x,0.5) + seed)* iResolution.xy );
		} else if ( iter < 3 ) { r = n2rand       ( vec2(uv.x,0.5) + seed );
		} else if ( iter < 4 ) { r = n2rand_faster( vec2(uv.x,0.5) + seed );
		} else if ( iter < 5 ) { r = n4rand_cus   ( vec2(uv.x,0.5) + seed );
		} else if ( iter < 6 ) { r = n4rand       ( vec2(uv.x,0.5) + seed );
		} else if ( iter < 7 ) { r = n4rand_ss    ( vec2(uv.x,0.5) + seed );
		} else if ( iter < 8 ) { r = blugausnoise2((vec2(uv.x,0.5) + seed)* iResolution.xy );
		} else                 { r = blugausnoise ((vec2(uv.x,0.5) + seed)* iResolution.xy );
		}
        bucketval += step(bucket.x,r) * step(r,bucket.y);
	}
	bucketval /= ITER_PER_BUCKET_F;
	bucketval *= scale;

    float v0 = step( uv.y / height, bucketval );
    float v1 = step( (uv.y-1.0/iResolution.y) / height, bucketval );
    float v2 = step( (uv.y+1.0/iResolution.y) / height, bucketval );
	return 0.5 * v0 + v1-v2;
}
vec4 mainImageTop(vec2 fragCoord) {
    iiTime = 0.0;
    vec2 uv = fragCoord.xy/ iResolution.xy;
    uv.y = fract(uv.y* 2.0)* 0.5;

    const float bands = 4.;
    const float den = 0.5/8.0;
    float o = fract(uv.x+ iTime* 0.1);
    if        (uv.y > 7.0 * den) {o += (1.0/ bands)* (-1.0+ 2.0* n2rand_tri   (fragCoord));  // Triangular PDF noise
    } else if (uv.y > 6.0 * den) {o += (1.0/ bands)* (-1.0+ 2.0* n2rand       ( uv ));
    } else if (uv.y > 5.0 * den) {o += (1.0/ bands)* (-1.0+ 2.0* n2rand_faster( uv ));
    } else if (uv.y > 4.0 * den) {o += (1.0/ bands)* (-1.0+ 2.0* n4rand_cus   ( uv ));
    } else if (uv.y > 3.0 * den) {o += (1.0/ bands)* (-1.0+ 2.0* n4rand       ( uv ));
    } else if (uv.y > 2.0 * den) {o += (1.0/ bands)* (-1.0+ 2.0* n4rand_ss    ( uv ));
    } else if (uv.y > 1.0 * den) {o += (1.0/ bands)* (-1.0+ 2.0* blugausnoise2(fragCoord));  // Gaussian blue noise
    } else                       {o += (1.0/ bands)* (-1.0+ 2.0* blugausnoise (fragCoord));  // Gaussian blue noise
    }
    o = floor(o* bands+ 0.5)/ bands; // Quantisation

    return vec4(o,o,o,1);
}
vec4 mainImageBottom(vec2 fragCoord) {
    iiTime = iTime;
	vec2 uv = fragCoord.xy / iResolution.xy;
	const float num = 8.0;
	float o = 0.0;
	if        ( uv.x < 1.0 / num ) { o += n2rand_tri   ( fragCoord );
	} else if ( uv.x < 2.0 / num ) { o += n2rand       ( uv );
	} else if ( uv.x < 3.0 / num ) { o += n2rand_faster( uv );
	} else if ( uv.x < 4.0 / num ) { o += n4rand_cus   ( uv );
	} else if ( uv.x < 5.0 / num ) { o += n4rand       ( uv );
	} else if ( uv.x < 6.0 / num ) { o += n4rand_ss    ( uv );
	} else if ( uv.x < 7.0 / num ) { o += blugausnoise2( fragCoord );
	} else                         { o += blugausnoise ( fragCoord );
	}
    int idx = int(1.0+ uv.x* num);
    vec2 uvrange = vec2( float(idx- 1)/ num, float(idx)/ num );

    //display histogram
    if ( uv.y < 1.0 / 4.0 )
		o = 0.125 + histogram( idx, uv, uvrange, 1.0/4.0, HIST_SCALE );

	//display lines
	if ( abs(mod(uv.x- 0.5 / num,1.0 / num) - 0.5 / num) < 0.002 ) o = 0.0;
	if ( abs(mod(uv.y- 0.5 / 4.0,1.0 / 4.0) - 0.5 / 4.0) < 0.002 ) o = 0.0;


	return vec4( vec3(o), 1.0 );
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragColor = fragCoord.y* 2.0 < iResolution.y ? mainImageBottom(fragCoord) : mainImageTop(fragCoord);
}

float time(float t){
    float tt = floor(t)+smoothstep(0.0,1.0,fract(t));
    //t=floor(tt)+smoothstep(0.0,1.0,fract(tt));
    return tt;
}


float map(vec3 p){
    p+=0.25;
    vec3 q = fract(p+0.5) * 2.0 - 1.0;
    vec3 q2 = fract(p) * 2.0 - 1.0;
	vec3 r = floor(p);
    vec3 s = mod(r, 1.0);

	return min(
        length(q2)-0.5
        ,
        min(length(q.xy), min(length(q.xz), length(q.yz))) - 0.02
    );    
}


float trace(vec3 o, vec3 r){
    float t = 0.0;

    for (int i=0;i<int(pow(2.0,3.5+sin(iTime/4.0)*2.0)); ++i){
    	vec3 p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    uv = uv * 2.0 - 1.0;

    uv.x *= iResolution.x / iResolution.y;
    float off = sin(uv.x+time(iTime/2.0))*sin(uv.y-iTime/2.0);
    vec3 r = normalize(vec3(uv, 2.0+off));


    float the = time(iTime/3.0);
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    the = time(iTime/4.0);
    r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));
    the = time(iTime/6.0);
    r.yz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    vec3 o = vec3(time(iTime/3.0), 0.0, time(iTime));

	float t = trace(o,r);

	float fog = 1.0 / (1.0 + t * t * 0.1);
    fog = pow(fog,4.5-sin(time(iTime/4.0))*4.0);
   	vec3 fc = vec3(fog); 

	fragColor = vec4(fc,1.0);
}

#define POINTS 32 // try between 2 and 256, gets slow fast
#define PI 3.1415926536
#define TAU (2.0 * PI)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float bias = 0.0001;
    float power = 2.0;
    // mouse x controls bias, mouse y controls power
	vec2 mouse = iMouse.xy / iResolution.xy;
    bias = iMouse.z <= 0.0 ? bias : pow(10.0, (-0.5 + mouse.x) * 10.0);
	power = iMouse.z <= 0.0 ? power : 0.5 + mouse.y * 9.5;

    float cN = 0.0;
    // array used to store contributions in first loop
    float contribution[POINTS];

    for (int i = 0; i < POINTS; i++)
    {
        float f = float(i) / float(POINTS) * TAU;
	    vec2 pos = 0.5 + 0.35 * vec2(cos(iTime * 0.25 + f), sin(iTime * 0.8 + f * 2.0));
		pos = uv - pos;
        float dist = length(pos);
        // calculate contribution
    	float c = 1.0 / (bias + pow(dist, power));
        contribution[i] = c;
        // sum total contribution
        cN += c;
    }

    // normalize contributions and weigh colors
    vec3 col = vec3(0, 0, 0);
    cN = 1.0 / cN;
    for (int i = 0; i < POINTS; i++)
    {
        float f = float(i) / float(POINTS) * TAU + iTime * 0.1;
	    vec3 pcol = 0.5 + 0.5 * cos(vec3(f * 2.0, f, f * 4.0));
        col += contribution[i] * cN * pcol;
    }

    fragColor = vec4(col, 1.0);
}

