float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}
float PI = 3.1415926535;
vec3 col = vec3(1.0, 1.0, 1.0);
int stop = 0;
vec3 stoppos = vec3(0.0);
float map(vec3 p){
    float d = length(max(abs(p) - vec3(1.0),0.0));
    vec3 p1 = p;
    float the = iTime / 2.1;
    p1.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    d = smin(d, length(p1 - vec3(1.2,1.2,1.2)) - 0.7, 3.0);
    d = smin(d, length(p1 - vec3(-1.2,1.2,1.2)) - 0.7, 3.0);
    d = smin(d, length(p1 - vec3(1.2,1.2,-1.2)) - 0.7, 3.0);
    d = smin(d, length(p1 - vec3(-1.2,1.2,-1.2)) - 0.7, 3.0);
    if(d < 0.001) col= vec3(0.0, 1.0, 1.0);
    d = min(d, dot(p,vec3(0.0, 1.0, 0.0)) + 1.0);
    return d;
}
vec3 grad(vec3 p) {
    vec2 d = vec2(0.0, 0.001);
	float dFx = map(p + d.yxx) - map(p - d.yxx);
	float dFy = map(p + d.xyx) - map(p - d.xyx);
	float dFz = map(p + d.xxy) - map(p - d.xxy);
	return normalize(vec3(dFx, dFy, dFz));
}
float trace(vec3 o, vec3 r){
	float t = 0.0;
    vec3 p;
    for(int i = 0; i < 256; ++i){
    	p = o + r * t;
        float d = map(p);
        if(d < 0.001){
            stop = 1;
        	break;
        }
        t += d * 0.9;
    }
    stoppos = p;
    return t;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;
    vec3 r = normalize(vec3(uv - vec2(0.0, PI / 3.0), 1.0));

    float the = iTime;
    vec3 o = vec3(0.0,10.0, -10.0);
    r.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));
    o.xz *= mat2(cos(the), -sin(the), sin(the), cos(the));

    float t = trace(o, r);
    float light = 0.0;
    if(stop == 1){
    	vec3 nor = grad(stoppos);
        light = 0.6 * max(dot(nor, vec3(1.0,0.8,0.4)), 0.0) + 0.3;
        stop = 0;
        trace(stoppos + nor * 0.002, normalize(vec3(1.0,0.7,0.4)));
        if(stop == 1) light = 0.1;
    }

    vec3 fc = vec3(light) * col;
    fragColor = vec4(fc,1.0);
}

// Grid marching from iq's Grid of Cylinders
// https://www.shadertoy.com/view/4dSGW1

const float streetDistance = 0.6;
const vec3 streetColor = vec3(4.0, 8.0, 10.0);

const float fogDensity = 0.5;
const float fogDistance = 4.0;
const vec3 fogColor = vec3(0.34, 0.37, 0.4);

const float windowSize = 0.1;
const float windowDivergence = 0.2;
const vec3 windowColor = vec3(0.1, 0.2, 0.5);

const float beaconProb = 0.0003;
const float beaconFreq = 0.6;
const vec3 beaconColor = vec3(1.5, 0.2, 0.0);


const float tau = 6.283185;

float hash1(vec2 p2) {
    p2 = fract(p2 * vec2(5.3983, 5.4427));
    p2 += dot(p2.yx, p2.xy + vec2(21.5351, 14.3137));
    return fract(p2.x * p2.y * 95.4337);
}

float hash1(vec2 p2, float p) {
    vec3 p3 = fract(vec3(5.3983 * p2.x, 5.4427 * p2.y, 6.9371 * p));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash2(vec2 p2) {
    vec3 p3 = fract(vec3(5.3983 * p2.x, 5.4427 * p2.y, 6.9371 * p2.x));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.xx + p3.yz) * p3.zy);
}

vec2 hash2(vec2 p2, float p) {
    vec3 p3 = fract(vec3(5.3983 * p2.x, 5.4427 * p2.y, 6.9371 * p));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.xx + p3.yz) * p3.zy);
}

vec3 hash3(vec2 p2) {
    vec3 p3 = fract(vec3(p2.xyx) * vec3(5.3983, 5.4427, 6.9371));
    p3 += dot(p3, p3.yxz + 19.19);
    return fract((p3.xxy + p3.yzz) * p3.zyx);
}

float noise1(vec2 p) {
    vec2 i = floor(p);
    vec2 f = fract(p);
    vec2 u = f * f * (3.0 - 2.0 * f);
    return mix(mix(hash1(i + vec2(0.0, 0.0)), 
                   hash1(i + vec2(1.0, 0.0)), u.x),
               mix(hash1(i + vec2(0.0, 1.0)), 
                   hash1(i + vec2(1.0, 1.0)), u.x), u.y);
}

vec4 castRay(vec3 eye, vec3 ray) {
    vec2 block = floor(eye.xy);
    vec3 ri = 1.0 / ray;
    vec3 rs = sign(ray);
    vec3 side = 0.5 + 0.5 * rs;
    vec2 ris = ri.xy * rs.xy;
    vec2 dis = (block - eye.xy + 0.5 + rs.xy * 0.5) * ri.xy;

    float beacon = 0.0;

    for (int i = 0; i < 200; ++i) {
        vec2 lo0 = vec2(block + 0.01);
        vec2 loX = vec2(0.3, 0.3);
        vec2 hi0 = vec2(block + 0.69);
        vec2 hiX = vec2(0.3, 0.3);
        float height = (0.5 + hash1(block)) * (2.0 + 4.0 * pow(noise1(0.1 * block), 2.5));

        float dist = 500.0;
        float face = 0.0;
        for (int j = 0; j < 3; ++j) {
            float top = height * (1.0 - 0.1 * float(j));
            vec3 lo = vec3(lo0 + loX * hash2(block, float(j)), 0.0);
            vec3 hi = vec3(hi0 + hiX * hash2(block, float(j) + 0.5), top);

            vec3 wall = mix(hi, lo, side);
            vec3 t = (wall - eye) * ri;

            vec3 dim = step(t.zxy, t) * step(t.yzx, t);            
            float maxT = dot(dim, t);
            float maxFace = 1.0 - dim.z;

            vec3 p = eye + maxT * ray;
            dim += step(lo, p) * step(p, hi);
            if (dim.x * dim.y * dim.z > 0.5 && maxT < dist) {
                dist = maxT;
                face = maxFace;
            }
        }

        float prob = beaconProb * pow(height, 3.0);
        vec2 h = hash2(block);
        if (h.x < prob) {
            vec3 center = vec3(block + 0.5, height + 0.2);
            float t = dot(center - eye, ray);
            if (t < dist) {
                vec3 p = eye + t * ray;
                float fog = (exp(-p.z / fogDistance) - exp(-eye.z / fogDistance)) / ray.z;
                fog = exp(fogDensity * fog);

                t = distance(center, p);
                fog *= smoothstep(1.0, 0.5, cos(tau * (beaconFreq * iTime + h.y)));
                beacon += fog * pow(clamp(1.0 - 2.0 * t, 0.0, 1.0), 4.0);
            }
        }

        if (dist < 400.0) {
            return vec4(dist, beacon, face, 1.0);
        }

        vec2 dim = step(dis.xy, dis.yx); 
        dis += dim * ris;
        block += dim * rs.xy;
    }

    if (ray.z < 0.0) {
        return vec4(-eye.z * ri.z, beacon, 0.0, 1.0);
    }

    return vec4(0.0, beacon, 0.0, 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 m = vec2(0.03 * iTime, 0.8);
    if (iMouse.z > 0.0)
        m = iMouse.xy / iResolution.xy;
    m *= tau * vec2(1.0, 0.25);

    vec3 center = vec3(6.0 * iTime, 0.5, 3.0);
    float dist = 20.0;
    vec3 eye = center + vec3(dist * sin(m.x) * sin(m.y), dist * cos(m.x) * sin(m.y), dist * cos(m.y));
    float zoom = 3.0;

    vec3 forward = normalize(center - eye);
    vec3 right = normalize(cross(forward, vec3(0.0, 0.0, 1.0)));
    vec3 up = cross(right, forward);
    vec2 xy = 2.0 * fragCoord - iResolution.xy;
    zoom *= iResolution.y;
    vec3 ray = normalize(xy.x * right + xy.y * up + zoom * forward);

    vec4 res = castRay(eye, ray);
    vec3 p = eye + res.x * ray;

    vec2 block = floor(p.xy);
    vec3 window = floor(p / windowSize);
    float x = hash1(block, window.x);
    float y = hash1(block, window.y);
    float z = hash1(block, window.z);
    vec3 color = windowColor + windowDivergence * (hash3(block) - 0.5);
    color *= smoothstep(0.1, 0.9, fract(2.5 * (x * y * z)));

    vec3 streetLevel = streetColor * exp(-p.z / streetDistance);
    color += streetLevel;
    color = clamp(mix(0.25 * streetLevel, color, res.z), 0.0, 1.0);

    float fog = (exp(-p.z / fogDistance) - exp(-eye.z / fogDistance)) / ray.z;
    fog = exp(fogDensity * fog);
    color = mix(fogColor, color, fog);

    color = mix(fogColor, color, res.w);
    color += res.y * beaconColor;
    color += pow(res.y, 2.0);

    fragColor = vec4(color, 1.0);
}

#define ENABLE_SCANLINE 0

mat2 r2d(float a) {
	float c = cos(a);
	float s = sin(a);
	return mat2(c, s, -s, c);
}

vec2 amod(vec2 p, float m) {
	float a = mod(atan(p.x, p.y), m) - m * .5;
	return vec2(cos(a), sin(a)) * length(p);
}

float sc(vec3 p) {
	p = abs(p);
	return min(min(max(p.x, p.y), max(p.y, p.z)), max(p.z, p.x));
}

float sc2(vec3 p) {
	p = abs(p);
	vec3 g = max(p.xyz, p.yzx);
	return min(g.x, g.z);

}

const float pi = 3.14159;

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord / iResolution.xy - .5;
	uv.x *= iResolution.x / iResolution.y;

	vec3 ro = vec3(0., 0., -iTime), p;
	p = ro;
	vec3 rd = normalize(vec3(uv, -1));

	//p.z = -iTime* 0.5;
	//p.x = cos(iTime*.2);

	p.z = -iTime;
	p.x = cos(iTime);

	float ti = 64., e = 1.;
	for (float i = 64.; i > 0.; i--) {

		ti = i;

		if (.1 > e*i) {
			break;
		}



		vec3 q = p;

		//q.xy *= r2d(pi / 2. + .2*cos(iTime*.8));
		q.xy *= r2d(iTime*.4);


		q = mod(q, 4.) - 2.;


		vec3 newp = q;
		newp.yz = amod(newp.yz, pi / 16.);

		newp.xz *= r2d(pi / (7. + 5.*cos(iTime)));


		q.zy = amod(q.zy, pi / 2.);
		e = min(sc2(q) - .5, sc(newp) - .4);

		p += rd * e;
	}

	vec3 c = vec3(.8, .7, .7);
	//c = vec3(1.);

	c = mix(c, 1. - sqrt(abs(p + cos(iTime))) * ti / 64., ti / 64.);

    if(ENABLE_SCANLINE == 1)
		c = c + cos(1e3*uv.y) * .2 * tan(iTime*.5);

    fragColor.xyz = c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pixelP = fragCoord.xy / iResolution.xy;

    vec3 color = vec3( 1.0, 1.0, 1.0 );

    vec2 point = iMouse.xy / iResolution.xy;
    vec2 dist2Point = pixelP - point;
    float angle2Point = atan( dist2Point.y, dist2Point.x );
    float radius = 0.1 + 0.4*tan( angle2Point * iTime * iTime * iTime );

    color *= smoothstep( radius, radius + 0.01, length(dist2Point ) );

	fragColor = vec4( color ,1.0);
}

