float zoom=0.28;


//vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }
vec2 csqr( vec2 a )  { return vec2( a.x*a.x - a.y*a.y, 2.*a.x*a.y  ); }


mat2 rot(float a) {
	return mat2(cos(a),sin(a),-sin(a),cos(a));	
}


vec2 iSphere( in vec3 ro, in vec3 rd, in vec4 sph, bool has0 )//from iq
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 )
        if(has0) return vec2(-4.0);
        else     return vec2(-.50);

	h = sqrt(h);
	return vec2(-b-h, -b+h );
}

float map(in vec3 p, bool has1, bool has2, bool has3, bool has4) {
	float res = 0.;

    vec3 pw = vec3(sin((iTime + 5.)/10.)*0.35 + 1.);

    vec3 c = p;
	for (int i = 0; i < 5; ++i) {
        p =.7*pow(abs(p), pw)/dot(p,p) -.7;
        if(has1) p.yz = csqr(p.yz);
        if(has2) p    = p.zxy;

        if(has3) res += exp(-19. *     (abs(dot(p,c))));
        else     res += exp(-19. * sqrt(abs(dot(p,c))));

	}

    if(!has4) return    (res/2.); 
    else      return log(res/2.);
}


vec3 raymarch( in vec3 ro, vec3 rd, vec2 tminmax, vec2 m )
{
    float t = tminmax.x;
    float dt = .02;
    // dt = .2 - .195*cos(iTime*.05);//animated

    vec3 col= vec3(0.);
    float c = 0.;
    float sc = 0.14;

    //moving these computations outside of the main loop
    bool has1  = mod(m.x *  .5, 1.) > 0.5;
    bool has2  = mod(m.x      , 1.) > 0.5;
    bool has3  = mod(m.x *  2., 1.) > 0.5;
    bool has4  = mod(m.x *  4., 1.) > 0.5;
    bool has5  = mod(m.x *  8., 1.) > 0.5;


    bool has6  = mod(m.y * .25, 1.) > 0.5;
    bool has7  = mod(m.y *  .5, 1.) > 0.5;
    bool has8  = mod(m.y      , 1.) > 0.5;
    bool has9  = mod(m.y *  2., 1.) > 0.5;
    bool has10 = mod(m.y *  4., 1.) > 0.5;
    bool has11 = mod(m.y *  8., 1.) > 0.5;


    for( int i=0; i<68; i++ )
	{
        if(    has6) 
            if(has7) t+=dt*exp(-11.*c);
            else     t+=dt*exp(-1.8*c);
        else
            if(has7) t+=dt*exp(-3.6*c);
            else     t+=dt*exp(-7.8*c);

        vec3 pos = ro+t*rd;

        c = map(pos, has1, has2, has3, has4);           
        if(t>tminmax.y && tminmax.x != tminmax.y && has4 ) break; // c /= 1.25;

        //There are some interactions between flags 3&4 and 6&7
        if(has3){
            c=c*c;
            if(has6 || has7) sc = 0.005;
            if(has4) sc = sc/5.;
        }

        if(    !has9)
            if(!has10) col +=  sc*vec3(c*c*c, c*c, c);//blue
            else       col +=  sc*vec3(c*c, c, c*c*c);//green
        else
        	if(!has10) col +=  sc*vec3(c, c*c, c*c*c);//red
            else       col +=  sc*vec3(c*c, c*c, c*c);//white
    }

    if(! has11) col *= log(1.25 + 2.*abs(rd.yxz));
    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float time = iTime;
    vec2 q = fragCoord.xy / iResolution.xy;
