void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 orip = uv-0.5;
   	orip.y += 0.15;
    orip.x*=iResolution.x/iResolution.y;
    orip*=5.;

    vec2 p = orip;
    p.y = mix(-2.5*orip.y,orip.y,step(0.,orip.y));
    p.x += 0.2*sin(21.*orip.y*orip.y+13.*orip.x*orip.x+time)*sin(41.*orip.x*orip.y+12.*orip.x+time)*step(0.,-orip.y)*smoothstep(0.,1.,-orip.y);

    vec3 col = vec3(255.,253.,208.)/255.;

    float aa = 5./iResolution.y;

    float s = smoothstep(-aa,aa,p.y-pow(abs(p.x),3.5+2.5*cos(time)*cos(time)));
    s = min(s, smoothstep(-aa,aa,pow(abs(p.x)+0.05,0.2)-p.y+0.5));
    s = min(s, smoothstep(-aa,aa,sin(-30.*abs(p.x)+20.*p.y+time)));
    s *= 0.1+0.9*smoothstep(-.5,0.2,orip.y);
    col=mix(col,vec3(0.,100.,0.)/255.,s);

    s = smoothstep(-aa,aa,p.y-0.55-pow(abs(p.x)+0.05,0.2));
    s = min(s, smoothstep(-aa,aa,0.4-length(p-vec2(0.,1.4+0.1*sin(time)*sin(time)))));
    s *= 0.1+0.9*smoothstep(-1.,-0.,orip.y);
    col=mix(col,vec3(255.,125.,0.)/255.,s);
    fragColor = vec4(col, 1.0);
}

