void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = q - 0.5;
	p.x*=iResolution.x/iResolution.y;

    vec3 ro = vec3(0,0,-6.7);
    vec3 rd = normalize(vec3(p,1.3));
    vec2 mo = iMouse.xy / iResolution.xy-.5;
    mo = (mo==vec2(-.5))?mo=vec2(-0.1,0.1):mo;
	mo.x *= iResolution.x/iResolution.y;
    rd.yz *= mm2(mo.y);
    rd.xz *= mm2(mo.x + sin(time*0.05)*0.2);

    vec3 col = vec3(0.);
    vec3 brd = rd;
    float fade = smoothstep(0.,0.01,abs(brd.y))*0.1+0.9;

    col = bg(rd)*fade;

    if (rd.y > 0.){
        vec4 aur = smoothstep(0.,1.5,aurora(ro,rd))*fade;
        col += stars(rd);
        col = col*(1.-aur.a) + aur.rgb;
    }
    else //Reflections
    {
        rd.y = abs(rd.y);
        col = bg(rd)*fade*0.6;
        vec4 aur = smoothstep(0.0,2.5,aurora(ro,rd));
        col += stars(rd)*0.1;
        col = col*(1.-aur.a) + aur.rgb;
        vec3 pos = ro + ((0.5-ro.y)/rd.y)*rd;
        float nz2 = triNoise2d(pos.xz*vec2(.5,.7), 0.);
        col += mix(vec3(0.2,0.25,0.5)*0.08,vec3(0.3,0.3,0.5)*0.7, nz2*0.4);
    }

	fragColor = vec4(col, 1.);
}

// Regular sigmoid curve with the exception of
// x * 10 - 5, which seems to normalize the curve.
vec3 sigmoid(vec3 x) {
    return vec3(1.0) / (vec3(1.0) + exp(-(x * 10.0 - 5.0)));
}

void mainImage(out vec4 color, vec2 coord) {
    vec2 ps = vec2(1.0) / iResolution.xy;
    vec2 uv = coord * ps;

    color = texture(iChannel0, uv);

    // The time factor is multiplied by 4
    // for the filter flashing.
    vec2 t = sin(vec2(iTime) * vec2(4.0, 1.0)) * 0.5 + 0.5;

    // If mouse is clicking on the canvas.
    if (iMouse.z > 0.0)
        t = iMouse.xy * ps;

    if (uv.y > t.y)
    	color.rgb = mix(color.rgb, sigmoid(color.rgb), t.x);
    else
        color.rgb = mix(color.rgb, smoothstep(0.0, 1.0, color.rgb), t.x);

    // Create a separator, inverting the colors.
    if (uv.y < t.y + ps.y && uv.y > t.y - ps.y)
        color.rgb = vec3(1.0) - color.rgb;
}

#define depth 0.1
#define fdist 0.7
#define background 0.
#define portalmat1 1.
#define portalmat2 2.
#define framemat 3.
#define floormat 4.
#define geommat 5.
#define maxdist 100.
#define maxstep 100
#define threshold 0.0025
#define lightdir normalize(vec3(-1., 1., 0.5))
#define lightintensity 1.5
#define eps 0.01
#define shadoweps 0.1
#define dark vec3(0.2, 0.4, 0.4)
#define floorheight -1.5
#define maxpasses 20

#define KEY_SPACE	(vec2 (32.5, 0.5) / 256.0)

#define TWO_PI 6.28318530718
#define PI 3.14159265359

struct PortalFrame {
    vec3 c;
    vec3 dir;
    vec3 up;
    vec2 dims;
} p1, p2;


float spheresdf(vec3 pos, vec3 c, float r, out vec3 coords) {
    pos -= c;
    coords = pos/(r*2. + 0.1);
    return length(pos)-r;
}

float boxsdf(vec3 pos, vec3 c, vec3 dims, out vec3 coords) {
    pos -= c;
    dims /= 2.;
    float d = max(pos.x - dims.x, -pos.x - dims.x);
    d = max(d, max(pos.y - dims.y, -pos.y - dims.y));
    d = max(d, max(pos.z - dims.z, -pos.z - dims.z));
    coords = pos/max(dims.x, max(dims.y, dims.z));
    return d;
}

float cylindersdf(vec3 pos, vec3 c, float r, float h, out vec3 coords) {
    pos -= c;
    coords = pos/max(r, h);
    return max(length(pos.xz)-r, max(-pos.y, pos.y - h));
}

vec2 portalsdf(vec3 pos, PortalFrame p, out vec3 coords) {
    vec3 r = pos - p.c;
    float h = dot(r, p.dir);
    vec3 u = normalize(cross(p.dir, p.up));
    vec3 v = cross(u, p.dir);
    float x = dot(r, u);
    float y = dot(r, v);
    p.dims = p.dims/2.;

    //frame
    //float d2 = max(x-dims.x, -x-dims.x);
    //d2 = -max(d2, max(y-dims.y, -y-dims.y));

    float d = max(h-depth/2., -h-depth/2.);
    d = max(d, max(x-p.dims.x-depth, -x-p.dims.x-depth));
    d = max(d, max(y-p.dims.y-depth, -y-p.dims.y-depth));
    //d = max(d, d2);

    coords = vec3(x, y, h);
    return vec2(d, abs(x) < p.dims.x && abs(y) < p.dims.y ? portalmat1 : framemat);
}

vec2 sdf(vec3 pos, out vec3 coords) {
    vec3 coords1;
    vec3 coords2;
    vec2 d1 = portalsdf(pos, p1, coords1);
    vec2 d2 = portalsdf(pos, p2, coords2);
	vec2 d;
    if (d1.x < d2.x) {
        d = d1;
        coords = coords1;
    } else {
        d = vec2(d2.x, d2.y < 2.5 ? portalmat2 : framemat);
        coords = coords2;
    }
    float mindist = d.x;
    float mat = d.y;
    float h = max(pos.y-floorheight, length(pos.xz)-10.);
    if (h < mindist) {
        coords = pos;
        mindist = h;
        mat = floormat;
    }
    //primitives
    h = spheresdf(pos, vec3(2., 0., 0.), 0.8, coords1);
    if (h < mindist) {
        coords = coords1;
        mindist = h;
        mat = geommat;
    }
    h = cylindersdf(pos, vec3(-0.3, -1.5, 0.), 0.1, 1.5, coords1);
    if (h < mindist) {
        coords = coords1;
        mindist = h;
        mat = geommat;
    }
    h = boxsdf(pos, vec3(0., -0.5, -3.), vec3(0.4, 1.7, 0.4), coords1);
    if (h < mindist) {
        coords = coords1;
        mindist = h;
        mat = geommat;
    }
    h = boxsdf(pos, vec3(0., -1., 3.), vec3(5., 0.5, 0.9), coords1);
    if (h < mindist) {
        coords = coords1;
        mindist = h;
        mat = geommat;
    }
    return vec2(mindist, mat);
}

vec2 raytrace(vec3 rp, vec3 rd, out vec3 coords) {
    float t = 0.;
    vec2 d = sdf(rp, coords);
    int i;
    for (i=0; i<maxstep; i++) {
        t += d.x;
        if (t > maxdist) break;
        d = sdf(rp + t*rd, coords);
        if (abs(d.x)<threshold) {
            return vec2(t, d.y); 
        }
    }
    return vec2(maxdist, background);
}

vec3 getnormal(vec3 pos) {
    vec3 coords;
    vec2 diff = vec2(eps, 0.);
    float dx = sdf(pos+diff.xyy, coords).x-sdf(pos-diff.xyy, coords).x;
    float dy = sdf(pos+diff.yxy, coords).x-sdf(pos-diff.yxy, coords).x;
    float dz = sdf(pos+diff.yyx, coords).x-sdf(pos-diff.yyx, coords).x;
	return normalize(vec3(dx, dy, dz));
}

vec2 shaderay(vec3 pos, vec3 rd, float hardness) {
    vec3 coords;
	vec3 n = getnormal(pos);
    float lambert = max(0., dot(n, lightdir))*lightintensity;
    vec2 ds = raytrace(pos + shadoweps*lightdir, lightdir, coords);
    lambert = ds.y < 0.5 ? lambert : 0.;
    vec3 h = normalize(-rd + lightdir);
    float phong = pow(clamp(dot(h, n), 0., 1.), hardness);
    return vec2(lambert, phong);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float space = texture (iChannel0, KEY_SPACE).r;
    float alt = clamp(iMouse.y/iResolution.y-0.5, -0.15, 1.) * PI/2.;
    float azi = (iMouse.x/iResolution.x-0.35) * TWO_PI;
    float cphi = cos(alt);
    vec3 eye = (0.01 + 5. * (1.-space)) * vec3(sin(azi)*cphi, sin(alt) + 2.*exp(-iTime), cos(azi)*cphi);
    vec3 w = -normalize(eye);
    eye += vec3(-0.3, 0.05, 0.);
    vec3 u = normalize(cross(w, vec3(0., 1., 0.)));
    vec3 v = cross(u, w);
    vec3 rd = normalize(fdist*w + (fragCoord.x/iResolution.x-0.5)*u + (fragCoord.y/iResolution.x-0.5*iResolution.y/iResolution.x)*v);


    float t = (sin(iTime/5.)+1.) * PI;
    p1 = PortalFrame(vec3(0., 0., 0.), normalize(vec3(1., 0.1*cos(iTime*2.)*sin(t), 0.0)), vec3(0., 1., 0.2*sin(iTime*2.)*cos(t)), vec2(1.5 - 0.2*cos(t/2.), 2.+ 0.7*cos(t / 2.)));
    p2 = PortalFrame(vec3(-1.5, 0., 0.), normalize(vec3(cos(t), 0., sin(t))), vec3(0., 1., 0.), vec2(1.5, 2.));


    int i;
    for (i=0; i<maxpasses; i++) {
        vec3 coords;
    	vec2 d = raytrace(eye, rd, coords);
        if (d.y < 0.5) {
            //background
            fragColor = vec4(0., 0.5, 1., 1.);
            break;
        } else if (d.y < 1.5) {
            //portal1
            u = normalize(cross(p1.dir, p1.up));
    		v = cross(u, p1.dir);
    		float x = dot(rd, u);
    		float y = dot(rd, v);
            float z = dot(rd, p1.dir);
            u = normalize(cross(p2.dir, p2.up));
    		v = cross(u, p2.dir);
            vec2 scale = p2.dims/p1.dims;
            rd = normalize(x*u*scale.x + y*v*scale.y + z*p2.dir);
            eye = p2.c + coords.x*u*scale.x + coords.y*v*scale.y - coords.z * p2.dir + 2.*threshold*rd;
            //dummy frag color to indicate that we can't render any further into the portals
            fragColor = vec4(0.2, 0.8, 1., 1.);
        } else if (d.y < 2.5) {
            //portal2
            u = normalize(cross(p2.dir, p2.up));
    		v = cross(u, p2.dir);
    		float x = dot(rd, u);
    		float y = dot(rd, v);
            float z = dot(rd, p2.dir);
            u = normalize(cross(p1.dir, p1.up));
    		v = cross(u, p1.dir);
            vec2 scale = p1.dims/p2.dims;
            rd = normalize(x*u*scale.x + y*v*scale.y + z*p1.dir);
            eye = p1.c + coords.x*u*scale.x + coords.y*v*scale.y - coords.z * p1.dir + 2.*threshold*rd;

            fragColor = vec4(0.8, 0.2, 1., 1.);
        } else if (d.y < 3.5) {
            //portal border
            vec3 col = mix(vec3(0.2, 0.2, 0.2), vec3(0.2, 0.7, 0.6), abs(abs(step(mod(coords.x, 0.5), 0.25)-step(mod(coords.z, 0.5), 0.25))-step(mod(coords.y, 0.5), 0.25)));
            vec2 shade = shaderay(eye + d.x*rd, rd, 1.);
            fragColor = vec4(max(dark.b, shade.x)*col, 1.0);
            break;
        } else if (d.y < 4.5) {
            //floor
            vec3 col = mix(vec3(0.4, 0.4, 0.4), vec3(0.7, 0.7, 0.7), abs(step(mod(coords.x, 1.), 0.25)-step(mod(coords.z, 1.), 0.25)));
            vec2 shade = shaderay(eye + d.x*rd, rd, 10.);
            fragColor = vec4(max(dark.b, shade.x)*col+shade.y/3., 1.0);
            break;
        } else {
            //yellow checkered objects
            vec3 col = mix(vec3(0.4, 0.8, 0.4), vec3(1., 0.7, 0.2), abs(abs(step(mod(coords.x, 0.5), 0.25)-step(mod(coords.z, 0.5), 0.25))-step(mod(coords.y, 0.5), 0.25)));
            vec2 shade = shaderay(eye + d.x*rd, rd, 20.);
            fragColor = vec4(max(dark.b, shade.x)*col+shade.y/3., 1.0);
            break;
        }
    }
}

vec2 rotate(vec2 p, float ang){
    float c = cos(ang), s = sin(ang);
    return vec2(p.y * c - p.x * s, p.y * s + p.x * c);
}

vec3 hsb2rgb( in vec3 c )
{
  vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0)
		  , 6.0)-3.0)-1.0, 0.0, 10.0 );
  rgb *= rgb*(11.5-0.0*rgb);
  return c.z * mix( vec3(1.0), rgb, c.y);
}

float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*4758.5453123);
}

float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = 1.0*(fragCoord.xy / iResolution.xy)-0.5;


	p*=4.0;

	vec2 r=rotate(p,iTime);
	r*=1.0+sin(iTime)/2.0;
	vec2 r1=vec2((p.x+iTime),(p.y+iTime));
	float n1=1.0*(r.x+noise(r1)/10.0)-0.4;
	vec2 r2=vec2((p.x-iTime),(p.y-iTime));
	float n2=1.0*(r.y+noise(r2)/10.0)-0.0;
	vec2 v=vec2(n1,n2);

	//
	float cre=n1;
	float cim=n2;
	float zre=0.0;
	float zim=0.0;
	float max=32.0;
	float j=0.0;
	float cm=0.0;

	for(float i=0.0;i<256.0;i++)
	{
		if(i>max)
		{
			cm=0.0;
			break;
		}
		float dam=zre*zre-zim*zim+cre;
		zim=2.0*zre*zim+cim;
		zre=dam;
		if( (zre*zre+zim*zim)>4.0)
		{
			cm=i/max;
			break;
		}
	}


	//
	float color = 0.0;
	vec2 r3=vec2(sin(p.x+iTime),cos(p.y+iTime));
	float n3=noise(r3);
	vec2 r4=vec2(sin(p.x-iTime),cos(p.y-iTime));
	float n4=noise(r4);
	vec2 v1=vec2(n3,n4);

	color= (length(v1 - p));
	vec3 cc=vec3(color, cm, 1.0-color);

    fragColor = vec4( hsb2rgb(cc), 1.0 );

}

float PI = atan(1.0) * 4.0;
float TAU = atan(1.0) * 8.0;


float circle (vec2 center, vec2 fragCoord, float radius, float pixelWidth) {
    return smoothstep(radius + pixelWidth, radius - pixelWidth, length(fragCoord - center));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float pixelWidth = 1.0 / min(iResolution.x, iResolution.y);
	vec2 pos = (fragCoord - 0.5 * iResolution.xy) * pixelWidth;
    vec2 mouse = (iMouse.xy - 0.5 * iResolution.xy) * pixelWidth;

    if(iMouse.xy == vec2(0.0)) {
        mouse += 0.5 * iResolution.xy * pixelWidth;
    }

    float phase = -(iTime / 2.0) * TAU;
    float radius = 0.05 + (cos(phase) * 0.01);
    fragColor.r += circle(vec2(cos(phase), sin(phase)) *  0.15 + mouse, pos, radius, pixelWidth);
    fragColor.g += circle(vec2(0.0) + mouse, pos, radius, pixelWidth);
    fragColor.b += circle(vec2(cos(phase), sin(phase)) * -0.15 + mouse, pos, radius, pixelWidth);
}

#define RED vec4(1., 0., 0., 1.)
#define BLACK vec4(0.)

float PI = acos(-1.);

float deg2rad(float x){
    return x / 180. * PI;
}

vec2 rad2cod(float r, float t){
    return vec2(r * cos(t), r * sin(t));
}

float norm2(vec2 xy){
    return sqrt(xy.x * xy.x + xy.y * xy.y);
}

int seed = 20150118;
int rand(){
    return seed = seed * 0xdefaced + 1;
}

float dir = -1.;

vec4 draw(vec2 fragCoord, vec2 center){
    float r = iTime * dir;
    mat2 rot = mat2(cos(r), -sin(r), sin(r), cos(r));
    float var = sin(texture(iChannel0, vec2(iTime, 0.)).x);
    vec2 xy = rot * (fragCoord.xy - center);
    vec4 col = vec4(-1.);
    if(norm2(xy) < 100. + var * 20.){
        col = RED;
    }

    if(norm2(xy) < 15. + var * 20.){
        col = BLACK;
    }
    if(70. <= norm2(xy) && norm2(xy) <= 72.5){
        col = BLACK;
    }

    if(norm2(xy - rad2cod(70., deg2rad(0.0))) < 15. + var * 20.){
        col = BLACK;  
    }
    if(norm2(xy - rad2cod(70., deg2rad(120.0))) < 15. + var * 20.){
        col = BLACK;  
    }
    if(norm2(xy - rad2cod(70., deg2rad(240.0))) < 15. + var * 20.){
        col = BLACK;  
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    float offset = mod(iTime * 100., 360.);
    vec4 col = vec4(1.);
    vec2 xy = fragCoord - iResolution.xy / 2.0;
    float var = sin(texture(iChannel0, vec2(iTime, 0.)).x);
    if(norm2(xy) < 400.){
        col = BLACK;
    }
    if(norm2(xy) < 40. + var * 60.){
        col = RED;
    }
    if(240. < norm2(xy) && norm2(xy) < 245.){
        col = RED;
    }

    if(norm2(xy - rad2cod(250., deg2rad(0.0   + offset))) < 300.){
        vec4 tmp = draw(fragCoord, iResolution.xy / 2. + rad2cod(250., deg2rad(0.0   + offset)));
        if(tmp != vec4(-1.))
            col = tmp;
    }
    if(norm2(xy - rad2cod(250., deg2rad(120.0   + offset))) < 300.){
        vec4 tmp = draw(fragCoord, iResolution.xy / 2. + rad2cod(250., deg2rad(120.0   + offset)));
        if(tmp != vec4(-1.))
            col = tmp;
    }
    if(norm2(xy - rad2cod(250., deg2rad(240.0   + offset))) < 300.){
        vec4 tmp = draw(fragCoord, iResolution.xy / 2. + rad2cod(250., deg2rad(240.0   + offset)));
        if(tmp != vec4(-1.))
            col = tmp;
    }
    if(var > 0.4)
        col = vec4(1.) - col;
    fragColor = col;
}

