//hash function by Dave_Hoskins https://www.shadertoy.com/view/4djSRW
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * HASHSCALE4);
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

float map(vec4 p) {



    //vec4 p2 = abs(mod(p,4.0)-2.0)-1.0;
    //return box(p2,0.5);
    vec4 p2 = mod(p,2.0)-1.0;
    vec4 floorpos = floor(p*0.5);
    float len = 1e10;

    //the truchet flipping
    vec4 orientation = floor(hash44(floorpos)+0.5)*2.0-1.0;
    //orientation.yz = vec2(1.0);

    //actually flipping the truchet
    vec4 p3 = p2*orientation;

    //positions relative to truchet centers

    float x = 1.0;

    vec4[4] truchet = vec4[4] (
        vec4(p3.xy+vec2(+1,-1),p3.zw),// +x +y
        vec4(p3.xz+vec2(-1,-1),p3.yw),// +x    +z
        vec4(p3.yw+vec2(+1,-1),p3.xz),//    +y    +w
        vec4(p3.zw+vec2(+1,+1),p3.xy) //       +z +w
    );

    //finding distance to truchet
    len = min(min(min(
        torus2(truchet[0],vec2(1.0,0.05)),
        torus2(truchet[1],vec2(1.0,0.05))),
        torus2(truchet[2],vec2(1.0,0.05))),
        torus2(truchet[3],vec2(1.0,0.05)));

    return len;
}

vec4 findnormal(vec4 p, float len) {
    vec2 eps = vec2(0.01,0.0);

    return normalize(vec4(
        map(p+eps.xyyy)-len,
        map(p+eps.yxyy)-len,
        map(p+eps.yyxy)-len,
        map(p+eps.yyyx)-len));
}

vec4 findnormal(vec4 p) {
    vec2 eps = vec2(0.01,0.0);

    return normalize(vec4(
        map(p+eps.xyyy)-map(p-eps.xyyy),
        map(p+eps.yxyy)-map(p-eps.yxyy),
        map(p+eps.yyxy)-map(p-eps.yyxy),
        map(p+eps.yyyx)-map(p-eps.yyyx)));
}

vec4 shade(vec4 p, vec4 d, vec4 objnorm, float depth) {
    vec4 sun = normalize(vec4(-1.0));
    vec4 reflectnorm = reflect(d,objnorm);
    vec4 color = objnorm*0.5+0.5;
    vec4 fragColor = vec4(color*max(0.4,0.8*dot(objnorm,sun)));
    fragColor /= depth*depth*0.01+1.0;
    fragColor = max(fragColor,(dot(reflectnorm,sun)-0.9)*3.0/depth/depth);
    return clamp(fragColor,0.0,1.0);
}

