vec2 iSphere( in vec3 ro, in vec3 rd, in vec4 sph )//from iq
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 ) return vec2(-2.0);
	h = sqrt(h);
	return vec2(-b-h, -b+h );
}

float map(in vec3 p) {
	float res = 0.;

    vec3 c = p;
	for (int i = 0; i < 10; ++i) {
        p =.7*abs(p)/dot(p,p) -.7;
        //p.yz= csqr(p.yz);
        //p=p.zxy;
        res += exp(-19. * sqrt(abs(dot(p,c))));

	}
	return (res/2.); //log(res/2.)
}


vec3 raymarch( in vec3 ro, vec3 rd, vec2 tminmax )
{
    float t = tminmax.x;
    float dt = .02;
    //float dt = .2 - .195*cos(iTime*.05);//animated
    vec3 col= vec3(0.);
    float c = 0.;
    for( int i=0; i<68; i++ )
	{
        t+=dt*exp(-7.8*c);
        vec3 pos = ro+t*rd;

        c = map(pos);               
        if(t>tminmax.y && tminmax.x != tminmax.y) break; // c /= 1.25;

        //col += .12*vec3(c*c, c, c*c*c);
        col +=  .14*vec3(c*c*c, c*c, c);//blue
    }

    col *= log(1.25 + 2.*abs(rd.yxz));
    return col;
}


