// Rotational symmetry with regular mod() domain repetition. Since the repeated objects extend 
// past half the repeating space, one extra neighbor need to be checked for distance too. This
// can be done cheaply without extra trigonometry (line 46).
//    
// Idea from aiekick's https://www.shadertoy.com/view/XtBcRV but with fixed discontinuities at
// minimal cost, and euclidean distance


const int n = 8;
const float an = 6.2831/float(n);
const mat2 inc = mat2( cos(an),sin(an),-sin(an),cos(an)); // can replace by constants
//const mat2 inc = mat2(1.0,1.0,-1.0,1.0)/1.41421; // same as above for n=8

//#define SIMULATE_BUG
#define BOX_SIZE 0.5

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = (fragCoord*2.-iResolution.xy)/iResolution.y*3.;

    float fa = (atan(q.y,q.x)+an*0.5)/an;
    float ia = floor( fa );

    float sym = an*ia;
    vec2 r = mat2(cos(sym),-sin(sym),
                  sin(sym), cos(sym))*q;

    #ifndef SIMULATE_BUG
    if( fract(fa)>0.5 ) r = r*inc; // rotate one backwards
    #endif

    float d = 4.0 + length(q);
    #ifndef SIMULATE_BUG
    for( int i=0; i<2; i++ )
    #endif
    {
                                            // int id = (int(ia)-i) % n; or float id = mod(ia-float(i),float(n));
        float rot = iTime;                  // + 2.0*float(id); // try this for different angles

        // translate and rotate each box
        vec2 p = mat2( cos(rot),sin(rot),
                      -sin(rot),cos(rot))*(r - vec2(2.0,0.0));

        // box distance
        p = abs(p) - BOX_SIZE;                   // + 0.4*sin(3.0*float(id)); // try this for different size
        d = min( d, min(max(p.x,p.y),0.0) + length(max(p,0.0)) );

        // next angle
        r = inc*r;
	}

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4( col, 1.0 );;
}

float checkboard(vec2 p)
{
    p = mod(floor(p/100.0), 2.0);
    return mod(p.x + p.y, 2.0) < 1.0 ? 0.5 : 0.1;
}

//vec3 Light = vec3(-2.0 * cos(iTime * 2.0), 3.0, 2.0 * sin(iTime * 2.0));
vec3 ambient = vec3(0.1, 0.1, 0.3);
vec3 vEye = vec3(0.0, 0.0, 3.5);

float shadow(vec3 sRay, vec3 Pos);

vec3 calcLight(vec3 N, vec3 L, vec3 V, vec3 matColor)
{
    vec3 color;

	vec3 Rf = reflect(-L,N);

    float diffuse = max(dot(N, L), 0.0);
    float specular = 0.0;
    if (diffuse > 0.0) specular = pow(max(dot(Rf, V),0.0), 16.0);

    color = diffuse * matColor;
	color += specular;
    color += ambient;

    return color;
}

vec3 plane(vec3 vRay)
{
vec3 Light = vec3(-2.0 * cos(iTime * 2.0), 3.0, 2.0 * sin(iTime * 2.0));
    vec3 color;
    vec3 N = vec3(0.0, 1.0, 0.0);
    float D = -1.0;

	float Det = dot(N, vRay); 
    if (Det < 0.0) { // Floor with chess texture

    	float t = (D - dot(vEye, N)) / Det;

        vec3 Pos = vEye + t * vRay;
    	vec3 L = normalize(Light - Pos);
    	vec3 V = normalize(vEye - Pos);

		vec3 matColor = vec3(1.0, 1.0, 1.0);
		matColor *= checkboard(Pos.xz * 100.0);

        color = calcLight(N, L, V, matColor);
        vec3 sRay = L;
        color *= shadow(sRay, Pos);

	}
	else { // Sky
		color = vec3(0.5, 0.7, 1.0); // Sky color
	}
    return color;
}

vec3 sphere(vec3 vRay)
{
    vec3 color;
vec3 Light = vec3(-2.0 * cos(iTime * 2.0), 3.0, 2.0 * sin(iTime * 2.0));    
    vec3 vSphere = vec3(0.0, 0.5 * sin(iTime), -0.5);
    float R = 1.0;
	vec3 vE2S = vSphere - vEye;
	float glass = 0.96;

    float A = dot(vRay, vRay);
    float B = -2.0 * dot(vRay, vE2S);
    float C = dot(vE2S, vE2S) - R*R;

	float Det = B*B - 4.0*A*C;

    vec3 matColor = vec3(1.0, 0.0, 1.0);

    if (Det > 0.0) { // Sphere

        float t = (-B - sqrt(Det)) / (2.0*A);

        vec3 Pos = vEye + t * vRay;

        vec3 N = normalize(Pos - vSphere);
        vec3 L = normalize(Light - Pos);
        vec3 V = normalize(vEye - Pos);

        vec3 Rf = refract(-V, N, glass);
        //vec3 Rf = reflect(-V, N);
        vec3 RfColor = plane(Rf);

        color = calcLight(N, L, V, matColor);
		color += RfColor;
    }
    else { // No Sphere
		color = plane(vRay);
    }

    return color;
}

bool sphereCheck(vec3 sRay, vec3 Pos)
{
    vec3 vSphere = vec3(0.0, 0.5 * sin(iTime), 0.0);
    float R = 1.0;
	vec3 vE2S = vSphere - Pos;

    float A = dot(sRay, sRay);
    float B = -2.0 * dot(sRay, vE2S);
    float C = dot(vE2S, vE2S) - R*R;

	float Det = B*B - 4.0*A*C;

    if (Det > 0.0) return true;
    else return false;
}

float shadow(vec3 sRay, vec3 Pos)
{
 	if (sphereCheck(sRay, Pos)) return 0.4;
    else return 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float aspectRatio = iResolution.x / iResolution.y;
    uv = 2.0 * uv - vec2(1.0, 1.0);
    uv.x *= aspectRatio;

	vec3 vRay = vec3(uv, 1.5) - vEye;
    //vRay = normalize(vRay);

    vec3 color;
    color = sphere(vRay);
	fragColor = vec4(color, 1.0);
}

// uncomment to solve for quadratic surface only
#define HIT_TET_PLANES

void compute_edges(vec3 p[4], out vec3 e[6]) {
    e[0] = p[2] - p[1];
    e[1] = p[0] - p[2];
    e[2] = p[1] - p[0];
    e[3] = p[1] - p[3];
    e[4] = p[2] - p[3];
    e[5] = p[0] - p[3];
}

void compute_planes(vec3 p[4], out vec3 n[3]) {
    vec3 e[6];
    compute_edges(p, e);
    n[0] = cross(e[0], e[3]);
    n[1] = cross(e[1], e[4]);
    n[2] = cross(e[2], e[5]);
    float det = 1.0/dot(n[0], e[1]);
    // premultiply the plane orthogonals by the inverse determinant
    n[0] *= det;
    n[1] *= det;
    n[2] *= det;
}

// computes barycentric coordinates for point from the fourth vertex,
// three plane normals with premultiplied inverse determinant
vec4 to_bary(vec3 p3, vec3 n[3], vec3 t) {
    // weights are scaled distance of point to individual planes
    t -= p3;
    float w0 = dot(t, n[0]); 
    float w1 = dot(t, n[1]); 
    float w2 = dot(t, n[2]); 
    float w3 = 1.0 - w0 - w1 - w2;
    return vec4(w0, w1, w2, w3);
}

vec3 from_bary(vec3 p[4], vec4 w) {
	return p[0] * w.x + p[1] * w.y + p[2] * w.z + p[3] * w.w;
}

vec3 normal_from_bary(vec3 n[3], vec4 w) {
    return normalize(n[0] * (w.w - w.x) + n[1] * (w.w - w.y) + n[2] * (w.w - w.z));
}

// evaluate quadratic in tetrahedron with given corners and midweights
// first set of weights are the weights on face xyz opposite of each vertex
// second set of weights are on edges xyz-w
float eval_quadratic(vec4 w, vec4 c, vec3 m1, vec3 m2) {
    return w.x*w.x*c.x + w.y*w.y*c.y + w.z*w.z*c.z + w.w*w.w*c.w
        + 2.0*(m1.x*w.y*w.z + m1.y*w.x*w.z + m1.z*w.x*w.y
             + m2.x*w.w*w.x + m2.y*w.w*w.y + m2.z*w.w*w.z);
}

// return first derivative at given point in barycentric basis
vec4 eval_quadratic_diff(vec4 w, vec4 c, vec3 m1, vec3 m2) {
    return 2.0*vec4(
    m2.x*w.w + c.x*w.x + m1.z*w.y + m1.y*w.z,
    m2.y*w.w + m1.z*w.x + c.y*w.y + m1.x*w.z,
    m2.z*w.w + m1.y*w.x + m1.x*w.y + c.z*w.z,
    c.w*w.w + m2.x*w.x + m2.y*w.y + m2.z*w.z
	);
}

struct Hit {
    // ray scalar
    float t;
    // barycenter
    vec4 b;
    // normal
    vec3 n;
};

// return normal of nearest plane in barycentric coordinates
vec4 select_plane_normal(vec4 b) {
    float lc = min(min(b.x, b.y), min(b.z, b.w));
    return step(b, vec4(lc));
}

// return the intersection of ray, tetrahedron and a quadratic function
// with the corner weights c and the edge weights m1 and m2
// as well as the barycentric coordinates and normals of the hit points
bool iSimplexPrim(vec3 p[4], 
	vec4 c, vec3 m1, vec3 m2, 
	vec3 ro, vec3 rd, 
	out Hit near, out Hit far) {

    vec3 sn[3];
    compute_planes(p, sn);

    // convert ray endpoints to barycentric basis
    // this can be optimized further by caching the determinant
    vec4 r0 = to_bary(p[3], sn, ro);
    vec4 r1 = to_bary(p[3], sn, ro + rd);

