    } else if ((d1 > 0.0) && (d1 < 1.0)) {
        n0 = eval_quadratic_diff(far.b, c, m1, m2);
    }  else {
        return false;
    }
#endif

#else
#endif
    near.n = normal_from_bary(sn, n0);
    far.n = normal_from_bary(sn, n1);
    return true;
}

