const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;
const float PI = 3.14;

float sphereSDF(vec3 samplePoint, vec3 center, float radius) {
    return length(center - samplePoint) - radius;
}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}


float sceneSDF(vec3 samplePoint) {
    float dst = sphereSDF(samplePoint, vec3(sin(iTime *PI)/2.5, 0.,0.), 0.2);
    dst = smin(dst, sphereSDF(samplePoint, vec3(0., sin(iTime)/3.,0.), 0.25), 0.1);
    dst = smin(dst, sphereSDF(samplePoint, vec3(sin(iTime + 0.3)/3., 0.,sin(iTime + 0.5)/3.), 0.2), 0.1);
    dst = smin(dst, sphereSDF(samplePoint, vec3(sin(iTime)/3., sin(iTime + 4.)/3.,0.), 0.2), 0.1);
    return smin(sphereSDF(samplePoint, vec3(0),0.5), dst, .4);
}


float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = estimateNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye) {
    const vec3 ambientLight = 0.5 * vec3(1.0, 1.0, 1.0);
    vec3 color = ambientLight * k_a;

    vec3 light1Pos = vec3(4.0,
                          2.0,
                          4.0);
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light1Pos,
                                  light1Intensity);

    vec3 light2Pos = vec3(2.0 * sin(0.37),
                          2.0 * cos(0.37),
                          2.0);
    vec3 light2Intensity = vec3(0.4, 0.4, 0.4);

    color += phongContribForLight(k_d, k_s, alpha, p, eye,
                                  light2Pos,
                                  light2Intensity);    
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, 5.0);
    float dist = shortestDistanceToSurface(eye, dir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    vec3 p = eye + dist * dir;

    vec3 K_a = vec3(0.2, 0.2, 0.2);
    vec3 K_d = vec3(0.1, 0.7, 0.3);
    vec3 K_s = vec3(1.0, 1.0, 1.0);
    float shininess = 10.0;

    vec3 color = phongIllumination(K_a, K_d, K_s, shininess, p, eye);

    fragColor = vec4(color, 1.);
}

/* 
    GVD of cubic Truchet cell, by mattz
    License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

	If you've spent way too much time thinking about cubic Truchet tilings,
    you might be tempted to ask how the three torus segments divide the cube
    into three equal volumes.

    The answer is given by the Generalized Voronoi Diagram (GVD) of the cell.
    This is the set of surfaces that are equidistant to pairs of torii. 
    See, for instance: http://sci.utah.edu/~jedwards/research/gvd/

    These particular Voronoi boundaries appear similar to hyperbolic paraboloids:
    http://mathworld.wolfram.com/HyperbolicParaboloid.html -- but I don't think
    they actually are hyperbolic paraboloids. Press H to compare.

    Thanks to Shane for posing the question that took me here!


*/

// Bunch of globals/constants:
const int rayiter = 60;
const float dmax = 20.0;
vec3 L = normalize(vec3(-0.7, 1.0, -1.0));
mat3 Rview;

float show_t2 = 1.;

float show_voronoi = 1.;
float show_hyperb = 0.;

const float HALFPI = 1.5707963267948966;

const int HSTEPS = 10;

/* RGB from hue. */
vec3 hue(float h) {
    vec3 c = mod(h*6.0 + vec3(2, 0, 4), 6.0);
    return h >= 1.0 ? vec3(h-1.0) : clamp(min(c, -c+4.0), 0.0, 1.0);
}

/* Rotate about x-axis */
mat3 rotX(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0, 0, 
                0, cx, sx,
                0, -sx, cx);
}

/* Rotate about y-axis */
mat3 rotY(in float t) {
    float cy = cos(t), sy = sin(t);
    return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);

}

/* Compute torus distance and normal vector given:

    - p is point in cube with vertices at +/- 0.5:
    - src is outward facing normal of flow source face 
    - dst is outward facing normal of flow sink face

   Returns a vec4 (nx, ny, nd, d) where:

     - nx, ny, nz is unit normal pointing away from torus
     - d is distance to torus.

*/
vec4 checkTorus(vec3 p, vec3 src, vec3 dst) {

    vec3 z = cross(dst, src);
    vec3 ctr = 0.5*(src+dst);

    p -= ctr;

    vec3 pc = 0.5*normalize(src*dot(p, src) + dst*dot(p, dst));

    vec3 n = p - pc;
    float l = length(n);

    float d = l;

    return vec4(n/l, d);

}

/* Boolean union of solids for map function below */
vec2 dmin(vec2 a, vec2 b) {
    return a.x < b.x ? a : b;
}

/* Sort vec3 coordinates descending - for box edges. */
vec3 psort(vec3 p) {
    p = abs(p);
    if (p.y < p.z) { p.yz = p.zy; } // y & z are sorted
    if (p.x < p.z) { p.xz = p.zx; } // x & z are sorted
    if (p.x < p.y) { p.xy = p.yx; } // x & y are sorted
    return p;
}

/* Inf. norm for box edges. */
float infnorm(vec3 a) {
    a = abs(a);
    return max(a.x, max(a.y, a.z));
}

/* Make an approximate distance field out of the difference of two 
   distance fields whose gradients (normals) are known. This is 
   exactly the method used in iq's "Distance estimation to implicits"
   article: http://iquilezles.org/www/articles/distance/distance.htm
*/
float vdiff(vec4 a, vec4 b) {
    return abs(a.w - b.w) / length(a.xyz - b.xyz);
}

/* Construct a doubly-ruled surface - I *think* this is a hyperbolic paraboloid? */
vec3 psurf(vec2 uv) {

    const vec3 a = 0.5*vec3(1, 1, -1);
    const vec3 b = 0.5*vec3(-1, 1, -1);
    const vec3 c = 0.5*vec3(1, 1, 1);
    const vec3 d = 0.5*vec3(1, -1, 1);

    vec3 p = mix(a, b, uv.x);
    vec3 q = mix(c, d, uv.x);

   	return mix(p, q, uv.y);

}

float dline(vec3 p, vec3 a, vec3 b) {

    vec3 pa = p-a;
    vec3 ba = b-a;

    float u = clamp(dot(pa,ba) / dot(ba, ba), 0., 1.);
    return length(pa - u*ba);

}

/* Distance function to scene */
vec2 map(in vec3 pos) {	

    // Get distances and normals for each of 3 torus segments 
    vec4 t0 = checkTorus(pos, vec3(0, 1, 0), vec3(0, 0, 1));
    vec4 t1 = checkTorus(pos, vec3(1, 0, 0), vec3(0, 0, -1));

    // Minimum distance to torus & material color
    vec2 tm = vec2(t0.w, 0./6.); 
    tm = dmin(tm, vec2(t1.w, 2./6.)); 



    // Distance to generalized Voronoi boundaries depends upon closest feature --
    // we will only check 2 of 3 possible boundaries.
    vec2 vm = vec2(1e5, -1);

    if (show_t2 != 0.) {

        vec4 t2 = checkTorus(pos, vec3(0, -1, 0), vec3(-1, 0, 0));
        tm = dmin(tm, vec2(t2.w, 4./6.)); 

        if (tm.x == t0.w) { 
            vm = dmin(vm, vec2(vdiff(t0, t1), 1./6.));
            vm = dmin(vm, vec2(vdiff(t2, t0), 5./6.));
        } else if (tm.x == t1.w) {
            vm = dmin(vm, vec2(vdiff(t0, t1), 1./6.));
            vm = dmin(vm, vec2(vdiff(t1, t2), 3./6.));
        } else {
            vm = dmin(vm, vec2(vdiff(t1, t2), 3./6.));
            vm = dmin(vm, vec2(vdiff(t2, t0), 5./6.));
        }

    } else {

        vm = dmin(vm, vec2(vdiff(t0, t1), 1./6.));

    }

    // Give torii some radius    
    tm.x -= 0.04;

    // Since we are raymarching an implicit, scaling the
    // estimated distance down a bit gives our raymarcher
    // more of a chance to intersect.
    vm.x *= 0.5;

    // Give implicit surface a bit of thickness
    vm.x -= 0.004;

    // Punch some holes in the voronoi boundaries to make netting
    vec3 cctr = floor(12.0*pos+0.5)/12.0;
    vec3 cpos = abs(pos - cctr);
    vm.x = max(vm.x, min(cpos.x, min(cpos.y, cpos.z))-0.01);

    // Prepare the final return value for the map function
    vec2 dm = show_voronoi != 0.0 ? dmin(tm, vm) : tm;

    if (show_hyperb != 0.) {
        for (int n=0; n<=HSTEPS; ++n) {
            float i = float(n)/float(HSTEPS);
            dm = dmin(dm, vec2(dline(pos, psurf(vec2(i, 0)), psurf(vec2(i, 1)))-0.008, 1.5));
            dm = dmin(dm, vec2(dline(pos, psurf(vec2(0, i)), psurf(vec2(1, i)))-0.008, 1.5));
        }
    }

    // Handle box edge
    vec3 ps = psort(pos);
    float u = clamp(ps.z, 0.0, 0.5);
    vec3 pc = vec3(0.5, 0.5, u);
    dm = dmin(dm, vec2(infnorm(ps - pc)-0.02, 1.01));

    // Clamp to cube extents
    dm.x = max(dm.x, ps.x - 0.5);

    // Return value
    return dm;


}


/* IQ's normal calculation. */
vec3 calcNormal( in vec3 pos ) {
    vec3 eps = vec3( 0.001, 0.0, 0.0 );
    vec3 nor = vec3(
        map(pos+eps.xyy).x - map(pos-eps.xyy).x,
        map(pos+eps.yxy).x - map(pos-eps.yxy).x,
        map(pos+eps.yyx).x - map(pos-eps.yyx).x );
    return normalize(nor);
}


/* IQ's distance marcher. */
vec2 castRay( in vec3 ro, in vec3 rd, in float maxd ) {

    const float precis = 0.001;   
    float h=2.0*precis;

    float t = 0.0;
    float m = -1.0;

    for( int i=0; i<rayiter; i++ )
    {
        if( abs(h)<precis||t>maxd ) continue;//break;
        t += h; 
        vec2 res = map( ro+rd*t );
        h = res.x;
        m = res.y;        
    }    

    if (t > maxd) {
        m = -1.0;
    }

    return vec2(t, m);

}

/* Pretty standard shading function. */
vec3 shade( in vec3 ro, in vec3 rd ){

    vec2 tm = castRay(ro, rd, dmax);        

    vec3 c;

    if (tm.y < 0.0) { 

        // miss
        return vec3(1.0);

    } else {        

        // hit
        vec3 pos = ro + tm.x*rd;
        vec3 n = calcNormal(pos);

        vec3 color = hue(tm.y);

        vec3 diffamb = (0.5*clamp(dot(n,L), 0.0, 1.0)+0.5) * color;
        vec3 R = 2.0*n*dot(n,L)-L;
        float spec = 0.3*pow(clamp(-dot(R, rd), 0.0, 1.0), 20.0);
        vec3 c = diffamb + spec;

        return c;

    }


}

const float KEY_H = 72.5 / 256.0;
const float KEY_T = 84.5 / 256.0;
const float KEY_V = 86.5 / 256.0;

/* Compare key state to default state. */
float keyState(float key, float default_state) {
    return abs( texture(iChannel0, vec2(key, 0.75)).x - default_state );
}

/* Finally, our main function: */
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord.xy - .5*iResolution.xy) * 0.8 / (iResolution.y);

    const vec3 tgt = vec3(0.0, 0.0, 0.0);
    const vec3 cpos = vec3(0, 0, 2);
    const vec3 up = vec3(0, 1, 0);


    show_t2 = keyState(KEY_T, show_t2);
    show_voronoi = keyState(KEY_V, show_voronoi);
    show_hyperb = keyState(KEY_H, show_hyperb);

    vec3 rz = normalize(tgt - cpos),
        rx = normalize(cross(rz,vec3(0,1.,0))),
        ry = cross(rx,rz);

    vec2 mpos;

    if (max(iMouse.x, iMouse.y) > 20.0) { 
        mpos.xy = iMouse.xy;
    } else {
        mpos = iResolution.xy * vec2(0.432 + 0.015*iTime, 0.415);
    }

    float thetax = (mpos.y - .5*iResolution.y) * -4.0*HALFPI/iResolution.y; 
    float thetay = (mpos.x - .5*iResolution.x) * 8.0*HALFPI/iResolution.x; 


    Rview = mat3(rx,ry,rz)*rotY(thetay)*rotX(thetax); 
    L = Rview*L;

   	/* Render. */
    vec3 rd = Rview*normalize(vec3(uv, 1.)),
        ro = tgt + Rview*vec3(0,0,-length(cpos-tgt));

    fragColor.xyz = shade(ro, rd);

}
