image_code:
//THIS IS WIP, DOESN'T FULLY WORK YET !!! (Feel free to contribute)
//there's an issue when wsPos is negative (comment #define NEGATIVE_POS_BUG_WORKAROUND to see)
//and at grid boundaries

//Adapted from cpu by Florent Tournade
//original algorithm by "YankeeMinstrel" ( https://www.reddit.com/r/proceduralgeneration/comments/797fgw/iterative_pseudoerosion/ )
//cpu re-implementation by "smcameron" ( https://github.com/smcameron/pseudo-erosion )
//uses bicubic filtered noise from https://www.shadertoy.com/view/4df3Dn

//TODO: -try adding some distortion

#define BICUBIC //how the baseheighmap samplesthe noise texture
#define EROSION_OCTAVE_MODE 3 //0: only base map / 1: one erosion octave / 2: fbm style / 3: MacSearlas style 
#define NEGATIVE_POS_BUG_WORKAROUND
#define GRIDSIZE 100.0
#define BASE_HEIGHT 0.9
#define EROSION_HEIGHT 0.6

// w0, w1, w2, and w3 are the four cubic B-spline basis functions
float w0(float a)
{
    return (1.0/6.0)*(a*(a*(-a + 3.0) - 3.0) + 1.0);
}

float w1(float a)
{
    return (1.0/6.0)*(a*a*(3.0*a - 6.0) + 4.0);
}

float w2(float a)
{
    return (1.0/6.0)*(a*(a*(-3.0*a + 3.0) + 3.0) + 1.0);
}

float w3(float a)
{
    return (1.0/6.0)*(a*a*a);
}

// g0 and g1 are the two amplitude functions
float g0(float a)
{
    return w0(a) + w1(a);
}

float g1(float a)
{
    return w2(a) + w3(a);
}

// h0 and h1 are the two offset functions
float h0(float a)
{
    return -1.0 + w1(a) / (w0(a) + w1(a));
}

float h1(float a)
{
    return 1.0 + w3(a) / (w2(a) + w3(a));
}

vec4 texture_bicubic(sampler2D tex, vec2 uv, vec4 texelSize)
{
	uv = uv*texelSize.zw + 0.5;
	vec2 iuv = floor( uv );
	vec2 fuv = fract( uv );

    float g0x = g0(fuv.x);
    float g1x = g1(fuv.x);
    float h0x = h0(fuv.x);
    float h1x = h1(fuv.x);
    float h0y = h0(fuv.y);
    float h1y = h1(fuv.y);

	vec2 p0 = (vec2(iuv.x + h0x, iuv.y + h0y) - 0.5) * texelSize.xy;
	vec2 p1 = (vec2(iuv.x + h1x, iuv.y + h0y) - 0.5) * texelSize.xy;
	vec2 p2 = (vec2(iuv.x + h0x, iuv.y + h1y) - 0.5) * texelSize.xy;
	vec2 p3 = (vec2(iuv.x + h1x, iuv.y + h1y) - 0.5) * texelSize.xy;

    return g0(fuv.y) * (g0x * texture(tex, p0)  +
                        g1x * texture(tex, p1)) +
           g1(fuv.y) * (g0x * texture(tex, p2)  +
                        g1x * texture(tex, p3));
}

float BaseHeightmap( vec2 uv )
{
    uv *= 0.00001;

#ifdef BICUBIC
    vec4 texelSize = vec4( 1.0 / iChannelResolution[0].xy,  iChannelResolution[0].xy); 
	float noise = texture_bicubic( iChannel0, uv, texelSize ).x;
#else
    float noise = texture( iChannel0, uv ).x;
#endif
    return noise;
}

float sqr( float x ) { return x * x; }

vec2 hash2( ivec2 p ) { return fract(sin( float(p.x) + float(p.y) * vec2(793.34,934.78) ) * vec2(12345.0,5432.0))-0.5; }

float sdLine( vec2 p, vec2 a, vec2 b )
{
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

float PseudoErosion( vec2 pos, float gridSize )
{
    ivec2 pi = ivec2(0.5 + pos/gridSize);

    float minh = 1e20;

    float dm = 0.0;
    for( int j=-1; j<=1 ; j++ )
    for( int i=-1; i<=1 ; i++ )
    {
        ivec2 pa = pi + ivec2(i,j);

        vec2  p1 = (vec2(pa) + hash2(pa)) * gridSize;

        vec2 p2;
        float lowestNeighbor = 1e20;

        for( int n=-1 ; n<=1 ; n++ )
        for( int m=-1 ; m<=1 ; m++ )
        {
            ivec2 pb = pa + ivec2(m,n);
            vec2 candidate = (vec2(pb) + hash2(pb)) * gridSize;

            float height = BaseHeightmap( candidate );
            if( height < lowestNeighbor )
            {
                p2 = candidate;
                lowestNeighbor = height;
            }
        }

        float h = sdLine( pos, p1, p2 );
        minh = min(h,minh);
    }

    minh = minh/gridSize;
    return minh;
}

float PseudoErosionFractal( vec2 wsPos )
{
	float baseHeight = BaseHeightmap( wsPos );

#if (EROSION_OCTAVE_MODE == 0)
	float erosion = 0.0;

#elif (EROSION_OCTAVE_MODE == 1)
	//1 erosion octave
    float erosion = PseudoErosion( wsPos, GRIDSIZE );

#elif (EROSION_OCTAVE_MODE == 2)
    //fbm style

    float erosion = 0.0;

    const int numOctaves = 4;

    float scale = 1.0f;
    float gridSize = GRIDSIZE;

    for( int i=0 ; i < numOctaves ; ++i )
    {
	    erosion += PseudoErosion( wsPos, gridSize ) * scale;
        scale *= 0.5;
        gridSize *= 0.5;
    }

#elif (EROSION_OCTAVE_MODE == 3)
    //MacSearlas  style

    float r1 = PseudoErosion( wsPos, GRIDSIZE );
	float erosion = sqr(r1);

    float r2 = PseudoErosion( wsPos, GRIDSIZE / 2.0 );
    erosion += r1 * r2 / 2.0;

    float r3 = PseudoErosion( wsPos, GRIDSIZE / 4.0 );
	erosion += sqrt(r1*r2)*r3 / 3.0;

#endif
    return baseHeight * BASE_HEIGHT + erosion * EROSION_HEIGHT;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy) / iResolution.xy;
    vec2 wsPos = (p - 0.5) * 4000.0 * ( sin( iTime * 0.3 ) + 1.0 );

#ifdef NEGATIVE_POS_BUG_WORKAROUND
    wsPos += 8000.0f;
#endif

    float map = PseudoErosionFractal( wsPos );

    fragColor = vec4( map, map, map, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 tile = floor(uv.xy * iResolution.xy / 16.0);
    if (mod(tile.x, 2.0) == 0.0) {
        if (mod(tile.y, 2.0) == 0.0) {
	   		fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
        } else {
            fragColor = vec4(0.0,0.0,0.0,1.0);
        }
    } else {
        if (mod(tile.y, 2.0) == 0.0) {
            fragColor = vec4(0.0,0.0,0.0,1.0);
        } else {
         	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);   
        }
    }
}

