To smoother curvature on a mirror axis (here a heart shape) (and for better performance), use;
x=abs(p)
x=almostIdentity(s,a,a*.5);

image_code:
//sign 3d almost identity smooth heart curvature
//self: https://www.shadertoy.com/new

/*
to smoother curvature on a mirror axis (here a heart shape) (and better performance) just use;
x=abs(p)
x=almostIdentity(s,a,a*.5);
 x is a mirrored domain, assumed to have an L1 discontinuity (a sharp corner) , unless x is orthogonal to the mirror)
 a is "smoothing delta" == "smoothing area"

this is VERY useful for "superprim" generalizations.
this is a game changer for all typefaces 
 and symbols and architecture with mirror symmetry.
this can solve all the continuity issues that you get with teleporters.
 as such, it even evades uncertainty of double-slit scenarios.
 by asserting that the double slit experiment is just a
  special case of 2 linked-list-portals.

parent shader had the solution to smoothing curvature along a mirror 
 1 day after i asked for it
 and 1 year before I noticed that it had it
  and han I made this fork for it.
*/

//parent: https://www.shadertoy.com/view/4lK3Rc
// Created by inigo quilez - iq/2017
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.

// Code for the making of this video: https://www.youtube.com/watch?v=aNR4n0i2ZlM


float hash1( float n )
{
    return fract(sin(n)*43758.5453123);
}


const float PI = 3.1415926535897932384626433832795;
const float PHI = 1.6180339887498948482045868343656;

vec3 forwardSF( float i, float n) 
{
    float phi = 2.0*PI*fract(i/PHI);
    float zi = 1.0 - (2.0*i+1.0)/n;
    float sinTheta = sqrt( 1.0 - zi*zi);
    return vec3( cos(phi)*sinTheta, sin(phi)*sinTheta, zi);
}

float almostIdentity( float x, float m, float n )
{
    if( x>m ) return x;
    float a = 2.0*n - m;
    float b = 2.0*m - 3.0*n;
    float t = x/m;
    return (a*t + b)*t*t + n;
}
//below is special case of above; n=m*.5;
float almostIdentity( float x, float m){
    //x is asserted to be x=abs(x);
    if(x>=m) return x;//sadly essential to also catch /0 case!
    float t = x/m;
    return (t*t+1.)*(.5*m);
}


vec2 map( vec3 q )
{
    q *= 100.0;

    vec2 res = vec2( q.y, 2.0 );


    float r = 15.0;
    q.y -= r;
    float ani = pow( 0.5+0.5*sin(6.28318*iTime + q.y/25.0), 4.0 );
    q *= 1.0 - 0.2*vec3(1.0,0.5,1.0)*ani;
    q.y -= 1.5*ani;
    float x = abs(q.x);

    float m=iMouse.y*20./iResolution.y;
   // x = (almostIdentity((x), m, m*.5)); // remove discontinuity (http://www.iquilezles.org/www/articles/functions/functions.htm)
    x = almostIdentity(x, m); // remove discontinuity (http://www.iquilezles.org/www/articles/functions/functions.htm)


    float y = q.y;
    float z = q.z;
    y = 4.0 + y*1.2 - x*sqrt(max((20.0-x)/15.0,0.0));
    z *= 2.0 - y/15.0;
    float d = sqrt(x*x+y*y+z*z) - r;
    d = d/3.0;
    if( d<res.x ) res = vec2( d, 1.0 );

    res.x /= 100.0;
    return res;
}

vec2 intersect( in vec3 ro, in vec3 rd )
{
	const float maxd = 1.0;

    vec2 res = vec2(0.0);
    float t = 0.2;
    for( int i=0; i<300; i++ )
    {
	    vec2 h = map( ro+rd*t );
        if( (h.x<0.0) || (t>maxd) ) break;
        t += h.x;
        res = vec2( t, h.y );
    }

    if( t>maxd ) res=vec2(-1.0);
	return res;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.005,0.0,0.0);
	return normalize( vec3(
           map(pos+eps.xyy).x - map(pos-eps.xyy).x,
           map(pos+eps.yxy).x - map(pos-eps.yxy).x,
           map(pos+eps.yyx).x - map(pos-eps.yyx).x ) );
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float ao = 0.0;
    for( int i=0; i<64; i++ )
    {
        vec3 kk;
        vec3 ap = forwardSF( float(i), 64.0 );
		ap *= sign( dot(ap,nor) ) * hash1(float(i));
        ao += clamp( map( pos + nor*0.01 + ap*0.2 ).x*20.0, 0.0, 1.0 );
    }
	ao /= 64.0;

    return clamp( ao, 0.0, 1.0 );
}

vec3 render( in vec2 p )
{
    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

	float an = 0.2*(iMouse.x*40./iResolution.x+2.);

	vec3 ro = vec3(0.4*sin(an),0.25,0.4*cos(an));
    vec3 ta = vec3(0.0,0.15,0.0);
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	// create view ray
	vec3 rd = normalize( p.x*uu + p.y*vv + 1.7*ww );


    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = vec3(1.0,0.82,0.9);

	// raymarch
    vec3 uvw;
    vec2 res = intersect(ro,rd);
    float t = res.x;

    if( t>0.0 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);
		vec3 ref = reflect( rd, nor );
        float fre = clamp( 1.0 + dot(nor,rd), 0.0, 1.0 );

        float occ = calcAO( pos, nor ); occ = occ*occ;

        if( res.y<1.5 ) // heart
        {
            col = vec3(0.9,0.02,0.01);
            col = col*0.72 + 0.2*fre*vec3(1.0,0.8,0.2);

            vec3 lin  = 4.0*vec3(0.7,0.80,1.00)*(0.5+0.5*nor.y)*occ;
                 lin += 0.5*fre*vec3(1.0,1.0,1.00)*(0.6+0.4*occ);
            col = col * lin;
            col += 4.0*vec3(0.7,0.8,1.00)*smoothstep(0.0,0.4,ref.y)*(0.06+0.94*pow(fre,5.0))*occ;

            col = pow(col,vec3(0.4545));
        }
        else // ground
        {
            col *= clamp(sqrt(occ*1.8),0.0,1.0);
        }
    }

    col = clamp(col,0.0,1.0);
	return col;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = (-iResolution.xy+2.0*fragCoord)/iResolution.y;

    vec3 col = render( p );

    vec2 q = fragCoord/iResolution.xy;
   // col *= 0.2 + 0.8*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.2);

    fragColor = vec4( col, 1.0 );
}

// THE PIECES

// Rotate the space around (0, 0) by angle.
vec2 rotate(vec2 uv, float angle)
{
	return vec2(
		uv.x*cos(angle) - uv.y*sin(angle),
		uv.x*sin(angle) + uv.y*cos(angle)
	);
}

// Return 1 if even, 0 if odd.
float is_even(float n)
{
    return 1.0 - step(1.0, mod(n, 2.0));
}

// Tile space, repeats 0->1 for number of times (per square unit space).
vec4 tile_space(vec2 uv, float times)
{
    vec2 scaledUV = uv * times;
    return vec4(fract(scaledUV), floor(scaledUV));
}

// Values from Wikipedia page on YUV colorspace.
vec3 yuv_to_rgb(vec3 yuvCol)
{
    return vec3(
        yuvCol.x +                    1.13983*yuvCol.z,
		yuvCol.x - 0.39465*yuvCol.y - 0.58060*yuvCol.z,
		yuvCol.x + 2.03211*yuvCol.y
    );
}

// From pixel spirit deck.
float rectSDF(vec2 uv, vec2 size)
{
	uv = uv * 2. - 1.;
	return max(
		abs(uv.x/size.x),
		abs(uv.y/size.y)
	);
}

// Adapted from pixel spirit deck.
float crossSDF(vec2 uv, float s, float thick)
{
	return min(
		rectSDF(uv, vec2(s, thick)),
		rectSDF(uv, vec2(thick, s))
	);
}

// From pixel spirit deck.
float fill(float x, float size)
{
	return 1.0 - step(size, x);
}


// THE CONSTRUCTION

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalize UVs.
	vec2 uv = fragCoord.xy / iResolution.xy;

    // Background color parameter.
    vec4 bgCol = mix(vec4(.05, .05, .05, 1), vec4(.25, .25, .25, 1.), distance(uv, vec2(.5))*1.4);

    // Correct UV aspect ratio.
    uv.x *= iResolution.x / iResolution.y;

    // More parameters.
    vec4 strokeCol = vec4(yuv_to_rgb(vec3(.5, uv.x, uv.y-.1)), 1.);
    const float tileAmt = 4.0;
    const float crossSize = 4.;
    float crossThickness = .9;

    // Build on these.
    float sdf = 0.;
    vec4 col;

    // Tile the space.
    vec4 tiledSpace = tile_space(uv, tileAmt);
    uv = tiledSpace.xy;

    // Make checkerboard mask to flip rotations for the effect.
    float xor = is_even(tiledSpace.w) + is_even(tiledSpace.z);
    xor = (1.0 - step(1.5, xor)) * xor;

    // Rotate the space (works per tile).
    uv -= vec2(.5);
    uv = rotate(uv, mix(1., -1., xor)*iTime*0.5f);
    uv += vec2(.5);

    // Shape the SDF with a cross.
    sdf = crossSDF(uv, crossSize, crossThickness);

    // Mix the colors for the final result.
    col = mix(bgCol, strokeCol, fill(sdf, 0.5));

    fragColor = col;
}

