const float FOV = 5.0;
const float MarchDumping = 0.7579;
const float Far = 38.925;
const int MaxSteps = 128;
const float CameraSpeed = 5.5099998;
const float TunnelSmoothFactor = 2.5;
const float TunnelRadius = 4.85660005;
const float TunnelFreqA = 0.18003;
const float TunnelFreqB = 0.25;
const float TunnelAmpA = 3.6230998;
const float TunnelAmpB = 2.4324;
const float NoiseIsoline = 0.319;
const float NoiseScale = 25.9980001;
const vec3 Color = vec3(1.0, 1.0, 1.0);

#define M_NONE -1.0
#define M_NOISE 1.0

float hash(float h) {
	return fract(sin(h) * 43758.5453123);
}

float noise(vec3 x) {
	vec3 p = floor(x);
	vec3 f = fract(x);
	f = f * f * (3.0 - 2.0 * f);

	float n = p.x + p.y * 157.0 + 113.0 * p.z;
	return mix(
			mix(mix(hash(n + 0.0), hash(n + 1.0), f.x),
					mix(hash(n + 200.0), hash(n + 200.0), f.x), f.y),
			mix(mix(hash(n + 11.0), hash(n + 11.0), f.x),
					mix(hash(n + 27.0), hash(n + 27.0), f.x), f.y), f.z);
}

float fbm(vec3 p) {
	float f = 0.0;
	f = 0.5000 * noise(p);
	p *= 2.01;
	f += 0.2500 * noise(p);
	p *= 2.02;
	f += 0.1250 * noise(p);

	return f;
}

// by iq. http://iquilezles.org/www/articles/smin/smin.htm
float smax(float a, float b, float k) {
	float h = clamp(0.5 + 0.9 * (b - a) / k, 0.0, 1.0);
	return mix(a, b, h) + k * h * (1.0 - h);
}

// From "Subterranean Fly-Through" by Shane https://www.shadertoy.com/view/XlXXWj
vec2 path(float z) {
	return vec2(TunnelAmpA * sin(z * TunnelFreqA), TunnelAmpB * cos(z * TunnelFreqB));
}

float noiseDist(vec3 p) {
	p = p / NoiseScale;
	return (fbm(p) - NoiseIsoline) * NoiseScale;
}

vec2 map(vec3 p) {
	float d = noiseDist(p);
	float d2 = length(p.xy - path(p.z)) - TunnelRadius;
	d = smax(d, -d2, TunnelSmoothFactor);

	vec2 res = vec2(d, M_NOISE);
	return res;
}

vec2 castRay(vec3 ro, vec3 rd) {
	float tmin = 0.0;
	float tmax = Far;

	float precis = 0.002;
	float t = tmin;
	float m = M_NONE;

	for (int i = 0; i < MaxSteps; i++) {
		vec2 res = map(ro + rd * t);
		if (res.x < precis || t > tmax) {
			break;
		}
		t += res.x * MarchDumping;
		m = res.y;
	}
	if (t > tmax) {
		m = M_NONE;
	}
	return vec2(t, m);
}

float softshadow(vec3 ro, vec3 rd, float mint, float tmax) {
	float res = 1.0;
	float t = mint;

	for (int i = 0; i < 16; i++) {
		float h = map(ro + rd * t).x;

		res = min(res, 10.0 * h / t);
		t += clamp(h, 0.02, 0.10);

		if (h < 0.001 || t > tmax) {
			break;
		}
	}
	return clamp(res, 0.0, 1.0);
}

vec3 calcNormal(vec3 pos) {
	vec2 eps = vec2(0.001, 0.0);

	vec3 nor = vec3(map(pos + eps.xyy).x - map(pos - eps.xyy).x,
			map(pos + eps.yxy).x - map(pos - eps.yxy).x,
			map(pos + eps.yyx).x - map(pos - eps.yyx).x);
	return normalize(nor);
}

float calcAO(vec3 pos, vec3 nor) {
	float occ = 0.0;
	float sca = 1.0;

	for (int i = 0; i < 5; i++) {
		float hr = 0.01 + 0.12 * float(i) / 4.0;
		vec3 aopos = nor * hr + pos;
		float dd = map(aopos).x;

		occ += -(dd - hr) * sca;
		sca *= 0.95;
	}
	return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

vec3 render(vec3 ro, vec3 rd) {
	vec3 col = vec3(0.0);
	vec2 res = castRay(ro, rd);
	float t = res.x;
	float m = res.y;

	if (m > -0.5) {
		vec3 pos = ro + t * rd;
		vec3 nor = calcNormal(pos);

		// material
		col = Color + sin(t * 0.1) * 0.1;
		col += 0.3 * sin(vec3(0.15, 0.02, 0.10) * iTime * 6.0);

		// lighitng
		float occ = calcAO(pos, nor);
		vec3 lig = -rd;
		float amb = clamp(0.5 + 0.005 * nor.y, 0.0, 1.0);
		float dif = clamp(dot(nor, lig), 0.0, 1.0);

		float fre = pow(clamp(1.0 + dot(nor, rd), 0.0, 1.0), 2.0);

		vec3 ref = reflect(rd, nor);
		float spe = pow(clamp(dot(ref, lig), 0.0, 1.0), 100.0);

		dif *= softshadow(pos, lig, 0.02, 2.5);

		vec3 brdf = vec3(0.0);
		brdf += 1.20 * dif * vec3(1.00, 0.90, 0.60);
		brdf += 1.20 * spe * vec3(1.00, 0.90, 0.60) * dif;

		// Additional specular lighting trick,
		// taken from "Wet stone" by TDM
		// https://www.shadertoy.com/view/ldSSzV
		nor = normalize(nor - normalize(pos) * 0.2);
		ref = reflect(rd, nor);
		spe = pow(clamp(dot(ref, lig), 0.0, 1.0), 100.0);
		brdf += 2.20 * spe * vec3(1.00, 0.90, 0.60) * dif;

		brdf += 0.40 * amb * vec3(0.00, 0.70, 1.00) * occ;
		brdf += 0.40 * fre * vec3(1.00, 1.00, 1.00) * occ;

		col = col * brdf;

		col = mix(col, vec3(0.0), 1.0 - exp(-0.005 * t * t));
	}
	return vec3(clamp(col, 0.0, 1.0));
}

mat3 rotationZ(float a) {
	float sa = sin(a);
	float ca = cos(a);

	return mat3(ca, sa, 0.0, -sa, ca, 0.0, 0.0, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 q = fragCoord / iResolution.xy;
	vec2 coord = 2.0 * q - 1.0;
	coord.x *= iResolution.x / iResolution.y;
	coord *= FOV;

    float t = iTime * CameraSpeed + 4.0 * 60.0;
    vec3 ro = vec3(path(t), t);

    t += 0.5;
    vec3 target = vec3(path(t), t);
    vec3 dir = normalize(target - ro);
    vec3 up = vec3(-0.9309864, -0.33987653, 0.1332234) * rotationZ(iTime * 0.05);
    vec3 upOrtho = normalize(up - dot(dir, up) * dir);
    vec3 right = normalize(cross(dir, upOrtho));

    vec3 rd = normalize(dir + coord.x * right + coord.y * upOrtho);

    vec3 col = render(ro, rd);

    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

const float FLT_MAX = 1.0 / 0.0;   
const int MAX_NB_BOUNCES = 10;
const int NB_SHOT_RAY = 15;
const float FLT_NB_SHOT_RAY = 15.0;

struct Material
{
    float Ka; // ambiant		coefficient
    float Kd; // diffuse		coefficient
    float Ks; // specular		coefficient
    float Kn; // specular power coefficient
};

struct ShadeInfo
{
    vec3 shadeCol;
    float Ks;
};

// Camera Parameters
      vec3		cameraInitPos	= vec3(6, 4, -5);
	  vec3 		cameraPos 		= vec3(6, 4, -5);
const vec3 		cameraTarget	= vec3(3, 1, -8);
const float 	cameraFovY		= 80.0;

//Light Parameters
const vec3		ambiantCol	= vec3(0,0,1);
const vec3		lightCol	= vec3(1,1,1);
const vec3 		lightInitPos= vec3(8, 10, -12);
      vec3		lightPos	= vec3(8, 10, -12);

// Sky Parameters
const vec3		skyCol	= vec3(0.2);
const int		skyId	= 0;
const Material	skyMat	= Material(1.0,0.0,0.0,0.0);

// Sphere Parameters
const int 		sphereId 		= 1;
const vec3 		spherePos		= cameraTarget + vec3(0, 1, 2);
const float 	sphereRadius	= 1.0;
const vec3		sphereCol		= vec3(1,0,0);
const Material 	sphereMat		= Material(0.2, 0.7, 0.1, 50.0);

// Sphere2 Parameters
const int 		sphere2Id 		= 2;
const vec3 		sphere2Pos		= cameraTarget + vec3(2, 1, 5);
const float 	sphere2Radius	= 1.0;
const vec3		sphere2Col		= vec3(0.2,0.5,0.5);
const Material 	sphere2Mat		= Material(0.5, 0.7, 1.0, 500.0);

// Plan Parameters
const int 		planeId 		= 3;
const vec3 		planePos		= vec3(0, 0.1, 0);
const vec3		planeNormal		= vec3(0, 1.0, 0);
const vec3	 	planeCol1		= vec3(1.0);
const vec3		planeCol2		= vec3(0.4);
const Material 	planeMat		= Material(0.2, 1.0, 0.2, 5.0);


void animateCamera(){
    cameraPos = vec3(cameraTarget.x+5.0*cos(iTime/4.0), cameraInitPos.y, cameraTarget.z+5.0*sin(iTime/4.0));
	lightPos  = vec3(lightInitPos.x - 8.0*sin(iTime), lightInitPos.y, lightInitPos.z + 12.0*cos(iTime));

}

void computeCameraRayFromPixel(vec2 fragCoord, out vec3 rayPos, out vec3 rayDir)
{
    float fragX = fragCoord.x - iResolution.x/2.0;
    float fragY = iResolution.y/2.0 - fragCoord.y;
    float fragZ = iResolution.y/(2.0*tan(radians(cameraFovY/2.0)));

    vec3 cz = normalize(cameraTarget - cameraPos); 

    vec3 tmpCy = vec3(0, -1, 0);
    vec3 tmpCx = cross(tmpCy, cz);

    vec3 cx = normalize(tmpCx);
    vec3 cy = cross(cz, cx);

    rayDir = normalize(fragX*cx + fragY*cy + fragZ*cz);
    rayPos = cameraPos;
}

float rayPlane(vec3 rayPos, vec3 rayDir, vec3 planePos, vec3 planeNormal, out vec3 intersectPt, out vec3 normalP)
{
   	float den = dot(planeNormal, rayDir);
    if(abs(den) < 0.0001)
        return(-1.0);
    float intersectDist = dot(planeNormal, planePos - rayPos)/den;
    intersectPt = rayPos + rayDir*intersectDist;
    normalP = -sign(den)*planeNormal;
    return intersectDist;
}

float raySphere(vec3 rayPos, vec3 rayDir, vec3 spherePos, float sphereRadius, out vec3 intersectS, out vec3 normalS)
{
    float intersectDist = 0.0;
    vec3 camSph = spherePos - rayPos;
	float proj = dot(camSph, rayDir);
    float intersect = pow(sphereRadius, 2.0)+ pow(proj,2.0) - pow(length(camSph),2.0);
    if(intersect > 0.0)
        intersectDist = proj - sqrt(intersect);
    intersectS = rayPos + rayDir*intersectDist;
    normalS = normalize(intersectS - spherePos);

    return intersectDist;
}


float computeNearestIntersection(vec3 rayPos, vec3 rayDir, out int objectId, out vec3 intersectI, out vec3 normalI){
	float minDist = FLT_MAX;
    objectId = skyId;

    vec3 intersectS, normalS;
	float distS = raySphere(rayPos, rayDir, spherePos, sphereRadius, intersectS, normalS);
    if((distS > 0.0) && (distS < minDist)){
    	intersectI = intersectS;
        normalI = normalS;
        minDist = distS;
        objectId = sphereId;
    }

    vec3 intersectS2, normalS2;
	float distS2 = raySphere(rayPos, rayDir, vec3(sphere2Pos.x+cos(iTime), sphere2Pos.y+sin(iTime), sphere2Pos.z), sphere2Radius, intersectS2, normalS2);
    if((distS2 > 0.0) && (distS2 < minDist)){
    	intersectI = intersectS2;
        normalI = normalS2;
        minDist = distS2;
        objectId = sphere2Id;
    }

    vec3 intersectP, normalP;
    float distP = rayPlane(rayPos, rayDir, planePos, planeNormal, intersectP, normalP);
    if((distP > 0.0) && (distP < minDist)){
    	intersectI = intersectP;
        normalI = normalP;
        minDist = distP;
        objectId = planeId;
    }
    if (objectId == skyId)
        minDist = -1.0;

    return(minDist);
}

vec3 getSphereColorAtPoint(vec3 pt){
    return(sphereCol);
}

vec3 getSphere2ColorAtPoint(vec3 pt){
    return(sphere2Col);
}

vec3 getPlaneColorAtPoint(vec3 pt){    
    vec3 worldX = vec3(1,0,0);
    vec3 axisX  = normalize(worldX - dot(worldX, planeNormal) * planeNormal);
    vec3 axisY  = normalize(cross(planeNormal, axisX));
    vec3 diff = pt - planePos; 

    float u = dot(diff, axisX);
    float v = dot(diff, axisY);

    vec3 planeCol = (mod(floor(pt.x*0.5) + floor(pt.z*0.5),2.0) < 1.0) ? planeCol1 : planeCol2;
    return(planeCol);
}

vec3 getColorAtPoint(int object, vec3 pt, out Material objectMat){
    if (object == sphereId)
    {
        objectMat = sphereMat;
        return getSphereColorAtPoint(pt);
    }
    else if (object == sphere2Id)
    {
        objectMat = sphere2Mat;
        return getSphere2ColorAtPoint(pt);
    } 
    else if (object == planeId)
    {
        objectMat = planeMat;
        return getPlaneColorAtPoint(pt);
    }

    return skyCol;
}

float getShadowCoeffAtPoint(Material object, vec3 pt, vec3 normal, vec3 lightPos, vec3 L){
    vec3 null1, null2;
    int null3;

    float distToL = distance(pt, lightPos);
    float epsilon = 0.00001;

    float distIntersect = computeNearestIntersection(pt+normal*epsilon, L, null3, null1, null2);

    if(distIntersect < 0.0 || distIntersect >= distToL){
        return(1.0);
    } else {
        return(object.Ka);
    }
}

vec3 computePhongShader(float shadowCoeff, vec3 objectCol, Material objectMat, vec3 normalI, vec3 L, vec3 R, vec3 V){
    vec3 ambiant 	= objectMat.Ka*ambiantCol;
    float cosTheta	= max(dot(normalI,L),0.0);
    float cosAlpha	= max(dot(R, V),0.0);

    vec3 diffuse 	= objectMat.Kd*lightCol*objectCol*cosTheta;   
    vec3 specular	= objectMat.Ks*lightCol*pow(cosAlpha,objectMat.Kn);

    float shadowCoeffSpec = shadowCoeff<1.0 ? 0.0 : 1.0; 

    return ambiant + diffuse*shadowCoeff + specular*shadowCoeff;
}

vec3 RayTraceAtPixelCoord(vec2 pixCoord){

	// Compute the ray to be casted through the pixel towards the 3D scene
    vec3 rayPos, rayDir;
    computeCameraRayFromPixel(pixCoord, rayPos, rayDir);

    ShadeInfo infos[MAX_NB_BOUNCES];
    int nbBounces = 0;
    do{

        // Test ray-objects intersections and find the nearest one
        // (with its associated intersection point and normal at the object surface)
        int objectId;
        vec3 intersectI, normalI;
        float distI = computeNearestIntersection(rayPos, rayDir, objectId, intersectI, normalI);

        //We did not hit the sphere, so we have the sky color (here : black)
        if (distI <= 0.0){
            infos[nbBounces].shadeCol 	= skyCol;
            infos[nbBounces].Ks			= 0.0;
            break;
        }

        // Apply the shading to the points that are on the surface and seen by the camera

        // Unit-vector from surface point to light
    	vec3 L = normalize(lightPos - intersectI);
    	// Unit-vector of reflection direction of the light at the surface point
    	vec3 R = normalize(2.0*normalI*dot(normalI,L)-L);
    	// Unit-vector from surface point to camera
	    vec3 V = normalize(cameraPos - intersectI);

	    //Get point color
    	Material objectMaterial;
	    vec3 color = getColorAtPoint(objectId, intersectI, objectMaterial); 

        float shadowCoeff;
        shadowCoeff = getShadowCoeffAtPoint(objectMaterial, intersectI, normalI, lightPos, L);

    	infos[nbBounces].shadeCol 	= computePhongShader(shadowCoeff, color, objectMaterial, normalI, L, R, V);
        infos[nbBounces].Ks			= objectMaterial.Ks;

        // Bounce from the surface towards the reflected direction of the ray
        rayPos = intersectI ;
        rayDir = rayDir - 2.0*normalI*dot(rayDir, normalI);

        nbBounces++;
    }
    while(nbBounces < MAX_NB_BOUNCES);

    vec3 resCol = vec3(0);

    for(int i=nbBounces; i>=0; i--){
     	resCol = infos[i].shadeCol + infos[i].Ks*resCol; 
    }

    return resCol;
}

vec2 noise2(vec2 location, vec2 delta){
    const vec2 c = vec2(12.9898, 78.233);
    const float m = 43758.5453;
    return vec2(
        fract(sin(dot(location + delta, c)) * m),
        fract(sin(dot(location + vec2(delta.y, delta.x), c)) * m)
        );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Test intersection
	animateCamera();
    vec3 pixelCol = vec3(0);
    for(int i=0; i<NB_SHOT_RAY; i++){
	    pixelCol += RayTraceAtPixelCoord(fragCoord + noise2(fragCoord,vec2(i,i*i)));
    }
    fragColor = vec4(pixelCol/FLT_NB_SHOT_RAY, 1.0);
}

