//http://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm

// Constants.
#define STEPS 64
#define EPS 0.01
#define FAR 10.0
#define PI 3.14159265359

mat2 rotate(float a) { 
    return mat2( cos(a), sin(a), -sin(a), cos(a) );
}

float opS( float d1, float d2 )
{
    return max(-d1,d2);
}

float sdSphere( vec3 p, float s )
{
  return length(p)-s;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

// Function of distance.
float map( vec3 p ) {
    p += vec3(0,-1,0); 
    p.xz *= rotate(iTime);
    float Sphere = sdSphere(p,1.4);
        float Box = sdBox(p,vec3(1.1));
    return opS(Sphere,Box) ;
   }

// Gradient (numeric) function of the distance function.
vec3 grad(vec3 p) {
    vec2 q = vec2(0.0, EPS);

    return vec3(map(p + q.yxx) - map(p - q.yxx),
                map(p + q.xyx) - map(p - q.xyx),
                map(p + q.xxy) - map(p - q.xxy));
}

vec3 shade(vec3 ro, vec3 rd, float t) {
	vec3 n = normalize(grad(ro + t*rd));
    return vec3(0.3, 0.8, 0.7)*pow(1.0-dot(-rd, n), 1.5);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Prepare the radius.
	vec2 uv = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;



    vec3 ro = vec3(0.0, 1.0, 3.0); // start of the radius.
    vec3 rd = normalize(vec3(uv, -1.0)); // direction of the radius.

    // Loop do raymarcher.
    float t = 0.0, d = EPS;
    for (int i = 0; i < STEPS; ++i) {
        d = map(ro + t*rd);
        if (d < EPS || t > FAR) break;
        t += d;
    }

    // Shading.
    vec3 col = d < EPS ? shade(ro, rd, t) : vec3(0.3, 0.6, 0.7)*(2.0-length(uv));

    // Post-processing. 
    col = smoothstep(0.0, 1.0, col);
    col = pow(col, vec3(0.45));

	fragColor = vec4(col,1.0);
}

float sphere (vec2 p, vec2 m, float r)
{
    return max (0.0, distance (p, m) - r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 screenPos = fragCoord.xy / iResolution.xy;
    float ratio = iResolution.y / iResolution.x;
    screenPos.x /= ratio;
    screenPos.x -= 0.5 * (iResolution.x - iResolution.y)
        / iResolution.y;

    int i;
    float s = 1.0;

    for (i = 0; i < 10; i++)
    {
        float fi = float (i);
        float x = 0.5 + 0.4 * cos (iTime * (0.1 + 0.1 * fi) + fi);
        float y = 0.5 + 0.4 * sin (iTime * (0.1 + 0.2 * fi) + fi);
        s = s * sphere (screenPos, vec2 (x, y), 0.125f);
    }

    fragColor =
          s > 0.001    ? vec4 (0.5, 0.5, 0.5, 1.0)
        : s > 0.0001   ? vec4 (0.2, 0.2, 0.2, 1.0)
        : s > 0.00001  ? vec4 (0.3, 0.2, 0.1, 1.0)
        : s > 0.000005 ? vec4 (0.7, 0.5, 0.1, 1.0)
        :                vec4 (0.0, 0.0, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ratio = iResolution.x / iResolution.y;
	vec2 uv = (fragCoord.xy / iResolution.yy) - vec2(ratio * 0.5, 0.5); // center is (0,0)
    vec2 uvNorm = normalize(uv);

    float bodyRadius = 0.4f;
    float z = sqrt((bodyRadius * bodyRadius) - (uv.x * uv.x + uv.y * uv.y));
    vec3 bodyNormal = normalize(vec3(uv.xy, z));

    float shading = dot(bodyNormal, vec3(0,0,1));
    float eyepos = dot(bodyNormal, normalize(vec3(0.6, 0.6, 1)));
    float bodyMask = (1.0 - smoothstep(bodyRadius - 0.005, bodyRadius, length(uv)));
    float fMouthTime = abs(sin(iTime * 8.0) * 0.4f);
    float outMouth = max(step(0.0, -uvNorm.x), smoothstep(fMouthTime - 0.02, fMouthTime, abs(uvNorm.y)));

	fragColor = vec4(0.9,0.9f,0.0f,1.0f) * bodyMask * shading * outMouth // body
        * smoothstep(0.004, 0.005, 1.0 - eyepos) // eye hole
        + vec4(1.0f) * (1.0f - shading) * bodyMask * outMouth; // external glow
}

// One dimensional, sequential data. This is similar to Viridis palette
// The idea is to have good range by being brighness linear but still exploit color and saturation
vec3 ColorFn1D (float x)
{
	x = clamp (abs (x), 0.0, 1.0); // optional

	float r = -0.121 + 0.893 * x + 0.276 * sin (1.94 - 5.69 * x);
	float g = 0.07 + 0.947 * x;
	float b = 0.107 + (1.5 - 1.22 * x) * x;
	return vec3 (r, g, b);
}

