// One dimensional, sequential data, two categories
vec3 ColorFn1DtwoC (float x, int c)
{
	x = clamp (abs (x), 0.0, 1.0); // optional

	float r, g, b;
	if (c == 0)
	{
		r = max (0.0, -0.724 + (2.52 - 0.865*x)*x);
		g = 0.315 + 0.589*x;
		b = x > 0.464 ? (0.302*x + 0.641) : (1.27*x + 0.191);
	}
	else
	{
		r = min (1.0, 0.5 + 1.36*x);
		g = max (0.0, -0.5 + (2.31 - 0.878*x)*x);
		b = 0.142 + 0.539*x*x*x;
	}
	return vec3 (r, g, b);
}

vec3 ColorFn1DfiveC (float x, int c) // One dimensional, sequential data, up to five categories
{
	x = clamp (abs (x), 0.0, 1.0); // optional

	float r, g, b;
	switch (c)
	{
		case 1 :
		r = 0.22 + 0.71*x; g = 0.036 + 0.95*x; b = 0.5 + 0.49*x;
		break;

		case 2 :
		r = 0.63 + 0.47*x; g = 0.1 + 0.8*x; b = 0.64*x;
		r = 0.48 + x * (1.7 + (-1.8 + 0.56 * x) * x);
		b = x * (-0.21 + x);
		break;

		case 3 :
		r = 0.85*x; g = 0.33 + 0.69*x; b = 0.059 + 0.78*x;
		r = x * (-0.21 + (2.6 - 1.5 * x) * x);
		break;

		case 4 :
		r = 0.83*x; g = 0.22 + 0.75*x; b = 0.52 + 0.5*x;
		r = 0.033 + x * (-0.35 + (2.7 - 1.5 * x) * x);
		b = 0.45 + (0.97 - 0.46 * x) * x;
		break;

		default :
		r = g = b = 0.025 + 0.96*x;
	}
	return vec3 (r, g, b);
}

