    vec2 _p2 = 12.0*p;
   float wibble = floor(fract(abs(_p2.x+_p2.y)-sin(_p2.y+iTime)/2.)*2.);

    float b1 =  2.0 * sin(p.x+time);
    float b2 =  2.0 * cos(p.x*p.y+ time);
    b1 = clamp(b1,0.85,1.25);
    b2 = clamp(b2,0.85,1.25);

    float mag = 1.225;
    mag += sin(time*0.25)*0.35;
	p*=mag;

    p.y *= 0.015;
    p.x -= 4.0;
    float p2 = sin (p.x*(20.0*cos (p.y*12.0)) + time);
	p2 *= cos (p.x*(15.0*sin (p.y*14.0)) + time);

    vec3 col1 = vec3(0.65,0.65,0.9);
    vec3 col2 = vec3(0.6,0.52,0.95);

    col2.r += (wibble*0.25);


    vec3 col = vec3(0.0);

    if (p2<0.0)
        col = col2 * abs(p2*b1);
    else
        col = col1 * p2*b2;


    fragColor = vec4(col.xyz,1.0);    
}

#define MAX_STEP 200.
#define BIAS 1.1

// just draw a 3d point
vec4 dawDot(vec3 eyePos,vec3 rayDir,vec3 center,vec4 color){


    float dist=0.;
    float eyeToCenter=distance(center,eyePos);
    vec3 detectP=vec3(1);
    for(float i=0.;i<MAX_STEP;i++){
       detectP=eyePos+rayDir*(eyeToCenter*(i/MAX_STEP));
       dist=distance(detectP,center);
    }

    dist=0.1/dist;


    vec4 col=vec4(dist,dist,dist,detectP.z)*color;
    return col;
}

// draw a sphere
vec4 drawSphere(vec3 eyePos,vec3 rayDir,vec3 center,float radius,vec3 lightPos,vec4 color){

    vec3 detectP=vec3(1.);
    vec4 col=vec4(0,0,0,0);
    vec3 sphereNorm=vec3(0,0,0);

    float eyeToCenter=distance(center,eyePos)-radius+BIAS;
    float nl=0.;
    float dist=0.;

    for(float i=0.;i<MAX_STEP;i++){
       detectP=eyePos+rayDir*(eyeToCenter*(i/MAX_STEP));
       dist=distance(detectP,center);
       if(dist<=radius){        

            //calculate the norm
            sphereNorm=normalize(detectP-center);
            vec3 lightDir=normalize(lightPos-detectP);
            nl=clamp(dot(lightDir,sphereNorm),0.1,1.);
            break;
       }
    }
    float fnl=mix(nl,pow(nl,60.),0.4);
    col=vec4(fnl,fnl,fnl,detectP.z)*color;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv = fragCoord.xy / iResolution.y;
    vec2 uvc=2.*uv-vec2(1);
    vec3 scnPos=vec3(uvc,0);
    vec3 eyePos=vec3(0,0,-3);
    float time=iTime;
    float freq=texture(iChannel0,vec2(vec2(iTime,iTime))).y;
    vec3 rayDir=normalize(vec3(scnPos-eyePos));
    vec3 lightPos=vec3(cos(time)*0.6,sin(time)*0.8+freq*0.2,-5.*cos(time)+3.2);
    vec4 col =vec4(0);


    vec4 col1=dawDot(eyePos,rayDir,lightPos,vec4(cos(iTime),1,0.3,1));
    vec4 col2=drawSphere(eyePos,rayDir,eyePos+vec3(0,0,1)*6.,0.5,lightPos,vec4(1,0,0.3,1));
    vec4 col3=drawSphere(eyePos,rayDir,eyePos+vec3(0.2,0,1)*5.,0.5,lightPos,vec4(.6,cos(iTime),0.3,1));
    vec4 col4=drawSphere(eyePos,rayDir,eyePos+vec3(0.5,0,1)*5.,0.5,lightPos,vec4(cos(iTime),1,0.3,1));

    col=col1;

    if(col2.z<col1.z){
       col=col2;
    }
    if(col3.z<col2.z){
       col=col3;
    }
    if(col4.z<col3.z){
       col=col4;
    }

	fragColor = vec4(col1+col2+col3+col4);
}

