float sdPlane(in vec3 p) {
    return p.y;
}

float sdSphere(in vec3 p, float s) {
    return length(p) - s;
}

float sdTorus(in vec3 p, in vec2 t) {
    return length(vec2(length(p.xz) - t.x, p.y)) - t.y;
}

vec2 opUnion(vec2 d1, vec2 d2) {
    return d1.x < d2.x ? d1 : d2;
}

vec2 scene(in vec3 position) {
    vec2 scene = opUnion(
          vec2(sdPlane(position), 1.0),
          vec2(sdSphere(position - vec3(0.0, 0.4, 0.0), 0.4), 12.0)
    );
    return scene;
}

vec2 traceRay(in vec3 origin, in vec3 direction) {
    float material = -1.0;

    float t = 0.02;

    for (int i = 0; i < 1000; i++) {
        vec2 hit = scene(origin + direction * t);
        if (hit.x < 0.002 || t > 20.0) break;
        t += hit.x;
        material = hit.y;
    }

    if (t > 20.0) {
        material = -1.0;
    }

    return vec2(t, material);
}

vec3 normal(in vec3 position) {
    vec3 epsilon = vec3(0.001, 0.0, 0.0);
    vec3 n = vec3(
          scene(position + epsilon.xyy).x - scene(position - epsilon.xyy).x,
          scene(position + epsilon.yxy).x - scene(position - epsilon.yxy).x,
          scene(position + epsilon.yyx).x - scene(position - epsilon.yyx).x);
    return normalize(n);
}

vec3 render(in vec3 origin, in vec3 direction, out float distance) {

    vec3 color = vec3(0,0,0);

    // (distance, material)
    vec2 hit = traceRay(origin, direction);
    distance = hit.x;

    // We've hit something in the scene
    if (hit.y > 0.0) {
        vec3 position = origin + distance * direction;

        vec3 v = normalize(-direction);
        vec3 n = normal(position);
        vec3 l1 = normalize(vec3(0.6, 0.7, -0.7));
        vec3 l2 = normalize(vec3(-0.6, 0.5, 0.7));
        vec3 r = normalize(reflect(direction, n));


        vec3 diffuseColor;

        if (hit.y < 4.0)  {
            // sphere
            float f = mod(floor(6.0 * position.z) + floor(6.0 * position.x), 2.0);
            diffuseColor = 0.4 + f * vec3(0.6);
            color = diffuseColor;
        } else if (hit.y < 16.0) {
            // sphere
            diffuseColor = vec3(0.3, 0.0, 0.0);
            color  = shade( n, l1, v, r );
            color += shade( n, l2, v, r );
        }
    }

    return color;
}

mat3 setCamera(in vec3 origin, in vec3 target, float rotation) {
    vec3 forward = normalize(target - origin);
    vec3 orientation = vec3(sin(rotation), cos(rotation), 0.0);
    vec3 left = normalize(cross(forward, orientation));
    vec3 up = normalize(cross(left, forward));
    return mat3(left, up, forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized coordinates
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    // Aspect ratio
    p.x *= iResolution.x / iResolution.y;

    // Camera position and "look at"
    vec3 origin = vec3(0.0, 0.8, 0.0);
    vec3 target = vec3(0.0,.4,0);

    origin.x += 1.1 * cos(iTime * 0.2);
    origin.z += 1.1 * sin(iTime * 0.2);

    mat3 toWorld = setCamera(origin, target, 0.0);
    vec3 direction = toWorld * normalize(vec3(p.xy, 2.0));

    // Render scene
    float distance;
    vec3 color = render(origin, direction, distance);

    // Exponential distance fog
    color = mix(color, 0.8 * vec3(0.8, 0.8, 1.0), 1.0 - exp2(-0.1 * distance * distance));

    fragColor = vec4(color, 1.0);
}

#define HASHSCALE1 .1031
#define HASHSCALE3 vec3(.1031, .1030, .0973)
#define HASHSCALE4 vec4(1031, .1030, .0973, .1099)

#define dir3(num) vec3(equal(abs(dir),vec3(num)))

#define dot2(p) dot(p,p)

#define pi acos(-1.0)

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

//hash function by Dave_Hoskins https://www.shadertoy.com/view/4djSRW
vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

//hash function by Dave_Hoskins https://www.shadertoy.com/view/4djSRW
float hash31(vec3 p3)
{
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.x + p3.y)*p3.z);
}

float mid(vec3 p) {
    //if(var3)
    p = min(p,p.yzx);
    return max(max(p.x,p.y),p.z);
}

float torus(vec3 p, vec2 r) {//creates 4 toruses
    return length(vec2(abs(abs(length(p.xy)-r.x)-0.05),abs(p.z)-0.05))-r.y;
}

float map(vec3 p) {

    vec3 p2 = mod(p,2.0)-1.0;
    vec3 floorpos = floor(p*0.5);
    float len = 1e10;

    //the truchet flipping
    vec3 flipping = floor(hash33(floorpos)+0.5)*2.0-1.0;

    //actually flipping the truchet
    vec3 p3 = p2*flipping;

    //positions relative to truchet centers
    mat3 truchet = mat3(
        vec3(+p3.yz+vec2(-1.0, 1.0),p3.x),
        vec3(+p3.zx+vec2(-1.0, 1.0),p3.y),
        vec3(+p3.yx+vec2( 1.0,-1.0),p3.z)
    );

    //finding distance to truchet
    len = min(min(
        torus(truchet[0],vec2(1.0,0.01)),
        torus(truchet[1],vec2(1.0,0.01))),
        torus(truchet[2],vec2(1.0,0.01)));

    return len;
}

//normal calculation
vec3 findnormal(vec3 p, float len) {
    const vec2 eps = vec2(0.01,0.0);

    return normalize(vec3(
        len-map(p-eps.xyy),
        len-map(p-eps.yxy),
        len-map(p-eps.yyx)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec3 floorpos = vec3(0.0,0.0,0.0);
    vec3 pos = vec3(1.0,1.0,0.0);
    vec3 dir = vec3(3.0,2.0,1.0);
    int num = 2;
    float time = abs(mod(iTime*0.4+50.0,100.0)-50.0);
    for (float i = 0.0; i <= floor(time); i++) {

        pos += dir*dir3(1);

    	vec3 flipping = floor(hash33(floorpos)+0.5)*2.0-1.0; //the truchet flipping

        dir *= flipping;

        int num2 = (num-int(dot(dir,dir3(1)))+3)%3;
        float back = dir[num2];
        dir[num2] = dir[num];
        dir[num] = -back;
        num = num2;

        dir *= flipping;

        vec3 portalpos = floorpos+dir*dir3(1)*0.5;
        if (hash31(portalpos) < 0.2) {
            floorpos += dir*dir3(1)*2.0;
        	pos += dir*dir3(1)*4.0;
        }
        floorpos += dir*dir3(1);
        pos += dir*dir3(1);
    }

    vec3 flipping = floor(hash33(floorpos)+0.5)*2.0-1.0; //the truchet flipping
    vec3 dir2 = dir;

    dir *= flipping;

    int num2 = (num-int(dot(dir,dir3(1)))+3)%3;
    float back = dir[num2];
    dir[num2] = dir[num];
    dir[num] = -back;
    num = num2;

    dir *= flipping;

    //animation
    pos += dir2*vec3(equal(abs(dir2),vec3(1.0)))*(sin(fract(time)*3.14*0.5));
    pos += dir*dir3(1)*(1.0-cos(fract(time)*3.14*0.5));

    //normal pointing towards where the camera moves, would be nice if the camera was looking in that direction
    vec3 forward = dir2*vec3(equal(abs(dir2),vec3(1.0)))*cos(fract(time)*3.14*0.5)+dir*dir3(1)*sin(fract(time)*3.14*0.5);

    /*
    mat3 rotation = mat3(
        vec3(0.0),
        vec3(0.0),
        vec3(0.0));
    rotation[2] = forward;
    rotation[1] = normalize(cross(forward,vec3(1)));
    rotation[0] = cross(rotation[1],forward);
    */

    mat3 rotation = mat3(
        vec3(0.0),
        vec3(0.0),
        vec3(0.0));

    vec2 t = vec2(cos(fract(time)*3.14*0.5),sin(fract(time)*3.14*0.5));
	rotation[2] = normalize(dir2*vec3(equal(abs(dir2),vec3(1)))*t.x+dir*dir3(1)*t.y);
    rotation[1] = normalize(dir2*vec3(equal(abs(dir2),vec3(2)))*t.x+dir*dir3(2)*t.y);
    rotation[0] = normalize(dir2*vec3(equal(abs(dir2),vec3(3)))*t.x+dir*dir3(3)*t.y);



    vec3 ro = pos+rotation[1]*0.125;
    vec3 rd = normalize(vec3(uv,1.0));

    if (length(iMouse.xy) > 40.0) {
    	rd.yz *= rot(iMouse.y/iResolution.y*3.14-3.14*0.5);
    	rd.xz *= rot(iMouse.x/iResolution.x*3.14*2.0-3.14);
    }

    rd = rd*transpose(rotation);


    bool hit = false;
    float len;
    float dist = 0.0;

    //floorpos = floor(ro*0.5);
    vec3 signdir = sign(rd);
    vec3 invdir = 2.0/(abs(rd)+0.0001);
    vec3 dists = abs(-signdir*0.5-0.5+ro*0.5-floorpos)*invdir;

    for (int i = 0; i < 100; i++) {
        float smallest = min(min(dists.x,dists.y),dists.z);
        len = map(ro);
        if (len < 1.0/iResolution.y*dist||dist>10.0) {
            hit = dist < 10.0;//len < 0.01*dist;
            break;
        }

        len = min(len,smallest);

        ro += rd*len;
        dist += len;

        smallest -= len;
        dists -= len;
        if (smallest <= 0.0) {
            vec3 mask = step(dists,vec3(smallest));
            vec3 localpos = (ro+dot(mask*dists,vec3(1))*rd) - floorpos*2.0-1.0;

            vec2 q = vec2(dot(mask.yzx,localpos),dot(mask.zxy,localpos));

            vec3 portalpos = floorpos+mask*signdir*0.5;
            vec4 random = vec4(hash31(portalpos),hash33(portalpos));
            random.x = random.x/0.2;
            //vec3 randompos = floor(hash33(portalpos)*100.0-50.0);

            float t = iTime*random.x+random.y;
            float portalsize =
                length(q)
                +sin(atan(q.x,q.y)* 8.0+t    )*0.04*sin(t*2.0)
                +sin(atan(q.x,q.y)*16.0+t*1.5)*0.02*sin(t*4.0);

            if (random.x > 1.0) portalsize+=100.0;
            if (portalsize < 0.4) {
                //vec3 randdirection = vec3(equal(vec3(1,2,3),vec3(clamp(floor(random.x*3.0),0.0,3.0))));

                floorpos += mask*signdir;
                vec3 localpos = ro-floorpos*2.0;
                floorpos += mask*signdir*2.0;
                ro = floorpos*2.0+localpos;

                //randdirection *= floor(0.5+random.yzw)*2.0-1.0;

            } else if (portalsize < 0.45) {
                fragColor = vec4(-mask*signdir*0.5+0.5,1);
                fragColor = vec4(random.yzwx);
                return;
            } else {
                floorpos += mask*signdir;
            }
            dists = (1.0-mask)*dists+mask*invdir;
        }

    }
    if (hit) {

		fragColor = vec4(findnormal(ro,len)*0.5+0.5,1.0);
        fragColor /= (dist*dist*0.05+1.0);
        //if (all(equal(floor(ro*0.5),floorpos))) fragColor = 1.0-fragColor;
    }
}

// Referencing the tutorial here: http://fabricecastel.github.io/blog/2016-02-11/main.html

// ray computation vars
const float PI = 3.14159265359;
const float fov = 50.0;
const float fovx = PI * fov / 360.0;
const float S = 0.01;
const vec3 deltax = vec3(S ,0, 0);
const vec3 deltay = vec3(0 ,S, 0);
const vec3 deltaz = vec3(0 ,0, S);

float cube(vec3 p){
    float s = 1.0;
    vec3 d = abs(p) - vec3(s);
    return min(max(d.x, max(d.y,d.z)), 0.0)
        + length(max(d,0.0));
}

float sphere(vec3 p, vec3 center){
    return length(center - p) - 1.0;
}

float distanceToNearestSurface(vec3 p){
  float amp = 2.0;
  return min(cube(p), sphere(p, vec3(amp * cos(iTime), amp * sin(iTime), 0.0)));
}

vec3 computeSurfaceNormal(vec3 p){
    float d = distanceToNearestSurface(p);
    return normalize(vec3(
    	distanceToNearestSurface(p + deltax)-d,
        distanceToNearestSurface(p + deltay)-d,
        distanceToNearestSurface(p + deltaz)-d
    ));
}

vec3 computeLambert(vec3 p, vec3 n, vec3 l){
    return vec3(dot(normalize(l-p), n));
}

vec3 intersectsWithWorld(vec3 p, vec3 dir){
  	float dist = 0.0;
    float nearest = 0.0;
    bool hit = false;
    for(int i = 0; i < 20; i++){
        float nearest = distanceToNearestSurface(p + dir*dist);
        if(nearest < 0.01){
            vec3 hit = p + dir * dist;
            vec3 light = vec3(100.0*sin(iTime),
                             	30.0 * cos(iTime),
                              	30.0 * cos(iTime));
            return computeLambert(hit, computeSurfaceNormal(hit), light);
        }
        dist += nearest;
    }
    return vec3(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

  	// camera attributes
    // cameraDirection and cameraUp MUST be normalized
    // (ie. their length must be equal to 1)
    float cameraDistance = 10.0;
    vec3 cameraPosition = vec3(cameraDistance * sin(iTime), 0, cameraDistance * cos(iTime));
    vec3 cameraDirection = vec3(-1.0*sin(iTime), 0.0, -1.0 * cos(iTime));
    vec3 cameraUp = vec3(0.0, 1.0, 0.0);

    // generate the ray for this pixel
    float fovy = fovx * iResolution.y/iResolution.x;
    float ulen = tan(fovx);
	float vlen = tan(fovy);
    vec2 camUV = uv*2.0 - vec2(1.0, 1.0);
    vec3 nright = normalize(cross(cameraUp, cameraDirection));
    vec3 pixel = cameraPosition + cameraDirection + nright*camUV.x*ulen + cameraUp*camUV.y*vlen;
    vec3 rayDirection = normalize(pixel - cameraPosition);

	vec3 pixelColor = intersectsWithWorld(cameraPosition, rayDirection);
    pixelColor.r *= cos(iTime);
    pixelColor.g *= 2.0 * sin(1.0 * iTime);
    fragColor = vec4(pixelColor, 1.0);
}

