mat3	bezier_derivate(mat4 controle_point)
{
    mat3	derivate;

    derivate[0] = ((controle_point[1].xyz - controle_point[0].xyz) * 3.0);
	derivate[1] = ((controle_point[2].xyz - controle_point[1].xyz) * 3.0);
	derivate[2] = ((controle_point[3].xyz - controle_point[2].xyz) * 3.0);
    return (derivate);
}


// 	bezier quadratique... dim 3
vec3	bezier_pos(mat4 controle_point, float t)
{
    vec3	res;

    res  = (pow(t, 0.0) * pow(1.0 - t, 3.0) * 1.0) * controle_point[0].xyz;
    res += (pow(t, 1.0) * pow(1.0 - t, 2.0) * 3.0) * controle_point[1].xyz;
    res += (pow(t, 2.0) * pow(1.0 - t, 1.0) * 3.0) * controle_point[2].xyz;
    res += (pow(t, 3.0) * pow(1.0 - t, 0.0) * 1.0) * controle_point[3].xyz;
    return (res);
}

vec3	bezier_dir_front(mat3 derivate_point, float t)
{
    vec3	res;

    res  = (pow(t, 0.0) * pow(1.0 - t, 2.0) * 1.0) * derivate_point[0];
    res += (pow(t, 1.0) * pow(1.0 - t, 1.0) * 2.0) * derivate_point[1];
    res += (pow(t, 2.0) * pow(1.0 - t, 0.0) * 1.0) * derivate_point[2];   
    return (normalize(res));
}

