mat3	bezier_cam_dir_pos(vec3 pos, mat4 cp, float t)
{
    mat3	cp_d1;
    mat3	cp_d2;
    mat3	ret;
    vec3	t2;

    cp_d1 = bezier_derivate(cp);
    cp_d2 = bezier_derivate(mat4(cp_d1[0], .0, cp_d1[1], .0, cp_d1[2], .0, vec4(.0)));

    ret[2] = normalize(bezier_dir_front(cp_d1, t));		// z => tangeante

//    t2 = bezier_derive_2(cp_d2, t);    
    ret[0] = permute2(ret[2]);
    ret[1] = normalize(cross(ret[2], ret[0]));
    ret[0] = cross(ret[1], ret[2]);


    ret[2] += pos;
    ret[1] += pos;
    ret[0] += pos;
    return (ret);
}

mat3	bezier_cam_dir(mat4 cp, float t)
{
    mat3	cp_d1;
    mat3	cp_d2;
    mat3	ret;
    vec3	t2;

    cp_d1 = bezier_derivate(cp);
    cp_d2 = bezier_derivate(mat4(cp_d1[0], .0, cp_d1[1], .0, cp_d1[2], .0, vec4(.0)));

    ret[2] = normalize(bezier_dir_front(cp_d1, t));
    ret[0] = permute2(ret[2]);
    ret[1] = normalize(cross(ret[2], ret[0]));
    ret[0] = cross(ret[1], ret[2]);
    return (ret);
}


