s_torus	set_torus(vec3 p, vec3 t, vec2 size_ok, vec2 size_debug)
{
    s_torus	ret;
    vec3	p1, p2, n;

    ret.rot = (rotZ(t.z) * rotY(t.y) * rotX(t.x));
    ret.pos[0] = p;
    ret.pos[1] = p +  (n = vec3(.0, 7.0, .0)) * ret.rot;
    ret.pos[2] = p +  (-n * ret.rot);
    ret.size1 = size_ok;
    ret.size2 = size_debug;
    return (ret);
}


mat4	set_bezier_coef(s_torus tt1, s_torus tt2)
{
    mat4	b;

    b[0].xyz = tt1.pos[0];
    b[1].xyz = tt1.pos[1];
    b[2].xyz = tt2.pos[2];
    b[3].xyz = tt2.pos[0];
    return (b);
}


vec2	dist_torus_debug(vec3 p, s_torus t)
{
    vec2	d, n1, n2;

    d  = vec2(torus(t.rot * (p - t.pos[0]), t.size1), 2.0);
    n1 = vec2(torus((p - t.pos[1]), t.size2), 5.0);
    n2 = vec2(torus((p - t.pos[2]), t.size2), 6.0);
    return (vecMin(d, vecMin(n1, n2)));
}

vec2	dist_torus(vec3 p, s_torus t)
{
    vec2	d;

    d  = vec2(torus(t.rot * (p - t.pos[0]), t.size1), 2);
    return (d);
}
