vec2		dist_cam(vec3 p, vec3 pos, vec3 uz, vec3 ux, vec3 uy)
{
    vec2	d;
    vec2 s = vec2(.0, 0.1);

    d = vec2(torus((p - pos), s), 8.0);
	d = vecMin(d, vec2(torus((p - uz), s), 5.0));
	d = vecMin(d, vec2(torus((p - ux), s), 4.0));
	d = vecMin(d, vec2(torus((p - uy), s), 3.0));
    return (d);
}

